import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import { 
  FaPlus, 
  FaEdit, 
  FaTrash, 
  FaEye, 
  FaFilter, 
  FaSearch, 
  FaSync,
  FaSwimmer,
  FaSort,
  FaSortAmountDown,
  FaSortAmountUp,
  FaRuler,
  FaLayerGroup,
  FaCalculator
} from 'react-icons/fa';
import axios from 'axios';

/**
 * ðŸ‡¬ðŸ‡§ Admin page for swim sets management
 * ðŸ‡«ðŸ‡· Page d'administration pour la gestion des sÃ©ries de natation
 */
const AdminSwimSetsPage = () => {
  const navigate = useNavigate();
  const [swimSets, setSwimSets] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [searchTerm, setSearchTerm] = useState('');
  const [exerciseFilter, setExerciseFilter] = useState('all');
  const [currentPage, setCurrentPage] = useState(0);
  const [itemsPerPage, setItemsPerPage] = useState(10);
  const [sortConfig, setSortConfig] = useState({
    key: 'set_distance',
    direction: 'ascending'
  });

  // Fonction de rÃ©cupÃ©ration qui contourne le service api
  const fetchSwimSets = async () => {
    try {
      setLoading(true);
      console.log('RÃ©cupÃ©ration des sÃ©ries de natation...');

      // RÃ©cupÃ©rer le token JWT stockÃ©
      const token = localStorage.getItem('token') || sessionStorage.getItem('token');

      // Appel direct sans passer par le service api.js
      const response = await axios.get('http://127.0.0.1:8000/api/swim-sets', {
        headers: {
          'Content-Type': 'application/json',
          'Accept': 'application/json',
          'Authorization': token ? `Bearer ${token}` : ''
        },
        withCredentials: true
      });

      console.log('RÃ©ponse:', response.data);
      setSwimSets(response.data);

      // Extract unique exercise IDs for filtering
      const exerciseIds = [...new Set(response.data.map(set => set.exercise_id))];
      setExerciseFilter('all');

      setError(null);
    } catch (err) {
      console.error('Erreur lors de la rÃ©cupÃ©ration des sÃ©ries:', err);
      setError('Impossible de charger les sÃ©ries. Veuillez rÃ©essayer.');
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    fetchSwimSets();
  }, []);

  // Sort function
  const requestSort = (key) => {
    let direction = 'ascending';
    if (sortConfig.key === key && sortConfig.direction === 'ascending') {
      direction = 'descending';
    }
    setSortConfig({ key, direction });
  };

  // Get sort indicator icon
  const getSortIcon = (key) => {
    if (sortConfig.key === key) {
      return sortConfig.direction === 'ascending' ? 
        <FaSortAmountUp className="ms-1" /> : 
        <FaSortAmountDown className="ms-1" />;
    }
    return <FaSort className="ms-1 text-muted" />;
  };

  // Calculer le total pour chaque sÃ©rie (distance Ã— rÃ©pÃ©tition)
  const calculateTotal = (set) => {
    const distance = set.set_distance || 0;
    const repetition = set.set_repetition || 1;
    return distance * repetition;
  };

  // Apply sorting and filtering
  const getSortedSwimSets = () => {
    // First filter swim sets
    let filteredItems = swimSets.filter(set => {
      const matchesSearch = 
        (set.exercise_id && set.exercise_id.toString().includes(searchTerm.toLowerCase())) ||
        (set.set_distance && set.set_distance.toString().includes(searchTerm)) ||
        (set.set_repetition && set.set_repetition.toString().includes(searchTerm));

      const matchesExercise = exerciseFilter === 'all' || 
                             (set.exercise_id && set.exercise_id.toString() === exerciseFilter);

      return matchesSearch && matchesExercise;
    });

    // Then sort them
    if (sortConfig.key) {
      filteredItems.sort((a, b) => {
        // Special case for total
        if (sortConfig.key === 'total') {
          const totalA = calculateTotal(a);
          const totalB = calculateTotal(b);

          if (totalA < totalB) {
            return sortConfig.direction === 'ascending' ? -1 : 1;
          }
          if (totalA > totalB) {
            return sortConfig.direction === 'ascending' ? 1 : -1;
          }
          return 0;
        }

        // For regular fields
        if (a[sortConfig.key] < b[sortConfig.key]) {
          return sortConfig.direction === 'ascending' ? -1 : 1;
        }
        if (a[sortConfig.key] > b[sortConfig.key]) {
          return sortConfig.direction === 'ascending' ? 1 : -1;
        }
        return 0;
      });
    }

    return filteredItems;
  };

  // Get current items for pagination
  const sortedFilteredSwimSets = getSortedSwimSets();
  const pageCount = Math.ceil(sortedFilteredSwimSets.length / itemsPerPage);
  const offset = currentPage * itemsPerPage;
  const currentSwimSets = sortedFilteredSwimSets.slice(offset, offset + itemsPerPage);

  // Delete handler
  const handleDelete = (id) => {
    if (window.confirm('ÃŠtes-vous sÃ»r de vouloir supprimer cette sÃ©rie ?')) {
      // RÃ©cupÃ©rer le token JWT stockÃ©
      const token = localStorage.getItem('token') || sessionStorage.getItem('token');

      axios.delete(`http://127.0.0.1:8000/api/swim-sets/${id}`, {
        headers: {
          'Content-Type': 'application/json',
          'Accept': 'application/json',
          'Authorization': token ? `Bearer ${token}` : ''
        },
        withCredentials: true
      }).then(() => {
        fetchSwimSets();
      }).catch(err => {
        console.error('Erreur lors de la suppression:', err);
        setError('Erreur lors de la suppression: ' + err.message);
      });
    }
  };

  if (loading) {
    return (
      <div className="container py-4">
        <div className="text-center">
          <div className="spinner-border text-primary" role="status">
            <span className="visually-hidden">Chargement...</span>
          </div>
        </div>
      </div>
    );
  }

  return (
    <div className="container-fluid py-4">
      {/* Titre Section */}
      <div className="row mb-4">
        <div className="col-12">
          <div className="card border-0 shadow-sm">
            <div className="card-header bg-primary-subtle">
              <h2 className="card-title mb-0">
                <FaSwimmer className="me-2" />
                Gestion des SÃ©ries
              </h2>
            </div>
          </div>
        </div>
      </div>

      <div className="d-flex justify-content-between align-items-center mb-4">
        <div>
          <p className="text-muted mb-0">
            {swimSets.length} sÃ©rie{swimSets.length > 1 ? 's' : ''} au total
          </p>
        </div>
        <button 
          className="btn btn-success btn-lg d-flex align-items-center"
          onClick={() => navigate('/admin/swim-sets/new')}
        >
          <FaPlus className="me-2" /> Ajouter Une SÃ©rie
        </button>
      </div>

      {/* Filters */}
      <div className="card shadow-sm mb-4">
        <div className="card-body">
          <div className="row g-3">
            <div className="col-md-6">
              <div className="input-group">
                <span className="input-group-text bg-light">
                  <FaSearch />
                </span>
                <input
                  type="text"
                  className="form-control"
                  placeholder="Rechercher..."
                  value={searchTerm}
                  onChange={(e) => {
                    setSearchTerm(e.target.value);
                    setCurrentPage(0);
                  }}
                />
              </div>
            </div>
            <div className="col-md-6">
              <div className="d-flex gap-2">
                <div className="input-group">
                  <span className="input-group-text bg-light">
                    <FaFilter />
                  </span>
                  <select
                    className="form-select"
                    value={exerciseFilter}
                    onChange={(e) => {
                      setExerciseFilter(e.target.value);
                      setCurrentPage(0);
                    }}
                  >
                    <option value="all">Tous les exercices</option>
                    {[...new Set(swimSets.map(set => set.exercise_id))].map(exerciseId => (
                      <option key={exerciseId} value={exerciseId.toString()}>
                        Exercice #{exerciseId}
                      </option>
                    ))}
                  </select>
                </div>
                <button 
                  className="btn btn-outline-secondary" 
                  onClick={fetchSwimSets}
                  title="RafraÃ®chir"
                >
                  <FaSync />
                </button>
              </div>
            </div>
          </div>
        </div>
      </div>

      {error && (
        <div className="alert alert-danger" role="alert">
          {error}
        </div>
      )}

      {/* Table View */}
      <div className="card shadow-sm mb-4">
        <div className="card-body p-0">
          <div className="table-responsive">
            <table className="table table-hover align-middle mb-0">
              <thead className="table-light">
                <tr>
                  <th 
                    className="cursor-pointer"
                    style={{cursor: 'pointer'}}
                    onClick={() => requestSort('set_distance')}
                  >
                    <FaRuler className="me-1" />
                    Distance (m) {getSortIcon('set_distance')}
                  </th>
                  <th 
                    className="cursor-pointer"
                    style={{cursor: 'pointer'}}
                    onClick={() => requestSort('set_repetition')}
                  >
                    <FaLayerGroup className="me-1" />
                    RÃ©pÃ©titions {getSortIcon('set_repetition')}
                  </th>
                  <th 
                    className="cursor-pointer"
                    style={{cursor: 'pointer'}}
                    onClick={() => requestSort('total')}
                  >
                    <FaCalculator className="me-1" />
                    Total (m) {getSortIcon('total')}
                  </th>
                  <th 
                    className="cursor-pointer"
                    style={{cursor: 'pointer'}}
                    onClick={() => requestSort('exercise_id')}
                  >
                    Exercice {getSortIcon('exercise_id')}
                  </th>
                  <th className="text-center">Actions</th>
                </tr>
              </thead>
              <tbody>
                {currentSwimSets.length === 0 ? (
                  <tr>
                    <td colSpan="5" className="text-center py-4">
                      Aucune sÃ©rie ne correspond Ã  vos critÃ¨res de recherche.
                    </td>
                  </tr>
                ) : (
                  currentSwimSets.map((set) => (
                    <tr key={set.id}>
                      <td>
                        <span className="fw-medium">{set.set_distance}m</span>
                      </td>
                      <td>
                        <span className="badge bg-secondary">
                          <FaLayerGroup className="me-1" /> {set.set_repetition || 1}
                        </span>
                      </td>
                      <td>
                        <span className="badge bg-info text-dark">
                          <FaCalculator className="me-1" /> {calculateTotal(set)}m
                        </span>
                      </td>
                      <td>
                        <span className="badge bg-primary">
                          Exercice #{set.exercise_id}
                        </span>
                      </td>
                      <td>
                        <div className="d-flex justify-content-center gap-1">
                          <button 
                            className="btn btn-sm btn-outline-primary"
                            onClick={() => navigate(`/admin/swim-sets/${set.id}`)}
                            title="Voir dÃ©tails"
                          >
                            <FaEye />
                          </button>
                          <button 
                            className="btn btn-sm btn-outline-secondary"
                            onClick={() => navigate(`/admin/swim-sets/${set.id}/edit`)}
                            title="Modifier"
                          >
                            <FaEdit />
                          </button>
                          <button 
                            className="btn btn-sm btn-outline-danger"
                            onClick={() => handleDelete(set.id)}
                            title="Supprimer"
                          >
                            <FaTrash />
                          </button>
                        </div>
                      </td>
                    </tr>
                  ))
                )}
              </tbody>
            </table>
          </div>
        </div>
      </div>

      {/* Pagination */}
      {sortedFilteredSwimSets.length > itemsPerPage && (
        <div className="card">
          <div className="card-body d-flex justify-content-between align-items-center">
            <div className="d-flex align-items-center">
              <span className="me-2">Afficher</span>
              <select 
                className="form-select form-select-sm" 
                value={itemsPerPage}
                onChange={(e) => {
                  setItemsPerPage(Number(e.target.value));
                  setCurrentPage(0);
                }}
                style={{width: '70px'}}
              >
                <option value="5">5</option>
                <option value="10">10</option>
                <option value="25">25</option>
                <option value="50">50</option>
              </select>
              <span className="ms-2">Ã©lÃ©ments</span>
            </div>

            <nav aria-label="Navigation des pages">
              <ul className="pagination pagination-sm mb-0">
                <li className={`page-item ${currentPage === 0 ? 'disabled' : ''}`}>
                  <button 
                    className="page-link" 
                    onClick={() => setCurrentPage(currentPage - 1)}
                    disabled={currentPage === 0}
                  >
                    &laquo;
                  </button>
                </li>

                {[...Array(pageCount)].map((_, index) => (
                  <li key={index} className={`page-item ${currentPage === index ? 'active' : ''}`}>
                    <button
                      className="page-link"
                      onClick={() => setCurrentPage(index)}
                    >
                      {index + 1}
                    </button>
                  </li>
                ))}

                <li className={`page-item ${currentPage === pageCount - 1 ? 'disabled' : ''}`}>
                  <button 
                    className="page-link" 
                    onClick={() => setCurrentPage(currentPage + 1)}
                    disabled={currentPage === pageCount - 1}
                  >
                    &raquo;
                  </button>
                </li>
              </ul>
            </nav>
          </div>
        </div>
      )}
    </div>
  );
};

export default AdminSwimSetsPage;
