import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import { 
  FaPlus, 
  FaEdit, 
  FaTrash, 
  FaSearch, 
  FaFilter, 
  FaSync,
  FaUser,
  FaEnvelope,
  FaShieldAlt,
  FaCalendarAlt
} from 'react-icons/fa';
import { getUsers, deleteUser } from '../../../services/users';
import { getRoles } from '../../../services/roles';

const UsersPage = () => {
  const navigate = useNavigate();
  const [users, setUsers] = useState([]);
  const [roles, setRoles] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [searchTerm, setSearchTerm] = useState('');
  const [roleFilter, setRoleFilter] = useState('all');
  const [currentPage, setCurrentPage] = useState(0);
  const [itemsPerPage, setItemsPerPage] = useState(10);

  // Fetch users and roles
  const fetchData = async () => {
    try {
      setLoading(true);
      const [usersResponse, rolesResponse] = await Promise.all([
        getUsers(),
        getRoles()
      ]);
      setUsers(usersResponse.data);
      setRoles(rolesResponse.data);
      setError(null);
    } catch (err) {
      console.error('Erreur lors de la récupération des données:', err);
      setError('Impossible de charger les utilisateurs. Veuillez réessayer.');
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    fetchData();
  }, []);

  // Filter users
  const filteredUsers = users.filter(user => {
    const searchString = searchTerm.toLowerCase();
    const matchesSearch = (user.username && user.username.toLowerCase().includes(searchString)) ||
                        (user.email && user.email.toLowerCase().includes(searchString)) ||
                        (user.first_name && user.first_name.toLowerCase().includes(searchString)) ||
                        (user.last_name && user.last_name.toLowerCase().includes(searchString));
    const matchesRole = roleFilter === 'all' || user.role_id === parseInt(roleFilter);
    return matchesSearch && matchesRole;
  });

  // Pagination
  const pageCount = Math.ceil(filteredUsers.length / itemsPerPage);
  const offset = currentPage * itemsPerPage;
  const currentUsers = filteredUsers.slice(offset, offset + itemsPerPage);

  // Delete handler
  const handleDelete = async (id, username) => {
    if (window.confirm(`Êtes-vous sûr de vouloir supprimer l'utilisateur "${username || id}" ?`)) {
      try {
        await deleteUser(id);
        await fetchData();
      } catch (err) {
        setError('Erreur lors de la suppression: ' + err.message);
      }
    }
  };

  // Get role name by ID
  const getRoleName = (roleId) => {
    const role = roles.find(r => r.id === roleId);
    return role ? role.name : 'Non défini';
  };

  // Get role badge color
  const getRoleBadgeColor = (roleName) => {
    switch (roleName.toLowerCase()) {
      case 'admin': return 'danger';
      case 'coach': return 'success';
      case 'athlet': return 'primary';
      default: return 'secondary';
    }
  };

  if (loading) {
    return (
      <div className="container py-4">
        <div className="text-center">
          <div className="spinner-border text-primary" role="status">
            <span className="visually-hidden">Chargement...</span>
          </div>
        </div>
      </div>
    );
  }

  return (
    <div className="container-fluid py-4">
      <div className="d-flex justify-content-between align-items-center mb-4">
        <div>
          <h1 className="h2 mb-0">Gestion des Utilisateurs</h1>
          <p className="text-muted mb-0">
            {users.length} utilisateur{users.length > 1 ? 's' : ''} au total
          </p>
        </div>
        <button 
          className="btn btn-primary d-flex align-items-center"
          onClick={() => navigate('/admin/users/new')}
        >
          <FaPlus className="me-2" /> Nouvel utilisateur
        </button>
      </div>

      {/* Filters */}
      <div className="card shadow-sm mb-4">
        <div className="card-body">
          <div className="row g-3">
            <div className="col-md-6">
              <div className="input-group">
                <span className="input-group-text bg-light">
                  <FaSearch />
                </span>
                <input
                  type="text"
                  className="form-control"
                  placeholder="Rechercher un utilisateur..."
                  value={searchTerm}
                  onChange={(e) => setSearchTerm(e.target.value)}
                />
              </div>
            </div>
            <div className="col-md-6">
              <div className="d-flex gap-2">
                <div className="input-group">
                  <span className="input-group-text bg-light">
                    <FaFilter />
                  </span>
                  <select
                    className="form-select"
                    value={roleFilter}
                    onChange={(e) => setRoleFilter(e.target.value)}
                  >
                    <option value="all">Tous les rôles</option>
                    {roles.map(role => (
                      <option key={role.id} value={role.id}>
                        {role.name.charAt(0).toUpperCase() + role.name.slice(1)}
                      </option>
                    ))}
                  </select>
                </div>
                <button 
                  className="btn btn-outline-secondary" 
                  onClick={fetchData}
                  title="Rafraîchir"
                >
                  <FaSync />
                </button>
              </div>
            </div>
          </div>
        </div>
      </div>

      {error && (
        <div className="alert alert-danger" role="alert">
          {error}
        </div>
      )}

      {/* Users List */}
      <div className="row g-4">
        {currentUsers.length === 0 ? (
          <div className="col-12">
            <div className="alert alert-info" role="alert">
              Aucun utilisateur ne correspond à vos critères de recherche.
            </div>
          </div>
        ) : (
          currentUsers.map((user) => {
            const roleName = getRoleName(user.role_id);
            const roleColor = getRoleBadgeColor(roleName);
            return (
              <div key={user.id} className="col-md-6 col-xl-4">
                <div className="card h-100 shadow-sm hover-lift">
                  <div className="card-header bg-white border-bottom-0">
                    <div className="d-flex justify-content-between align-items-center">
                      <span className={`badge bg-${roleColor}`}>
                        {roleName}
                      </span>
                      <small className="text-muted">
                        ID: {user.id}
                      </small>
                    </div>
                  </div>
                  <div className="card-body">
                    <div className="d-flex align-items-center mb-3">
                      <div className="bg-primary text-white rounded-circle p-3 me-3">
                        <FaUser />
                      </div>
                      <div>
                        <h2 className="h5 card-title mb-1">
                          {user.username || 'Sans pseudo'}
                        </h2>
                        <p className="card-text text-muted small mb-0">
                          {user.first_name} {user.last_name}
                        </p>
                      </div>
                    </div>

                    <div className="mb-3">
                      <div className="d-flex align-items-center text-muted small mb-2">
                        <FaEnvelope className="me-2" />
                        {user.email}
                      </div>
                      <div className="d-flex align-items-center text-muted small">
                        <FaCalendarAlt className="me-2" />
                        Inscrit le: {new Date(user.created_at).toLocaleDateString()}
                      </div>
                    </div>

                    <div className="d-flex align-items-center text-muted small mb-3">
                      <FaShieldAlt className="me-2" />
                      {user.email_verified_at ? 'Email vérifié' : 'Email non vérifié'}
                    </div>
                  </div>
                  <div className="card-footer bg-white border-top-0">
                    <div className="d-flex justify-content-between">
                      <button 
                        className="btn btn-sm btn-outline-primary"
                        onClick={() => navigate(`/admin/users/${user.id}/edit`)}
                      >
                        <FaEdit className="me-1" /> Modifier
                      </button>
                      <button 
                        className="btn btn-sm btn-outline-danger"
                        onClick={() => handleDelete(user.id, user.username || user.email)}
                      >
                        <FaTrash className="me-1" /> Supprimer
                      </button>
                    </div>
                  </div>
                </div>
              </div>
            );
          })
        )}
      </div>

      {/* Pagination */}
      {filteredUsers.length > itemsPerPage && (
        <div className="card mt-4">
          <div className="card-body d-flex justify-content-between align-items-center">
            <div className="d-flex align-items-center">
              <span className="me-2">Afficher</span>
              <select 
                className="form-select form-select-sm" 
                value={itemsPerPage}
                onChange={(e) => {
                  setItemsPerPage(Number(e.target.value));
                  setCurrentPage(0);
                }}
                style={{width: '70px'}}
              >
                <option value="5">5</option>
                <option value="10">10</option>
                <option value="25">25</option>
                <option value="50">50</option>
              </select>
              <span className="ms-2">éléments</span>
            </div>

            <nav aria-label="Navigation des pages">
              <ul className="pagination pagination-sm mb-0">
                <li className={`page-item ${currentPage === 0 ? 'disabled' : ''}`}>
                  <button 
                    className="page-link" 
                    onClick={() => setCurrentPage(currentPage - 1)}
                    disabled={currentPage === 0}
                  >
                    &laquo;
                  </button>
                </li>

                {[...Array(pageCount)].map((_, index) => (
                  <li key={index} className={`page-item ${currentPage === index ? 'active' : ''}`}>
                    <button
                      className="page-link"
                      onClick={() => setCurrentPage(index)}
                    >
                      {index + 1}
                    </button>
                  </li>
                ))}

                <li className={`page-item ${currentPage === pageCount - 1 ? 'disabled' : ''}`}>
                  <button 
                    className="page-link" 
                    onClick={() => setCurrentPage(currentPage + 1)}
                    disabled={currentPage === pageCount - 1}
                  >
                    &raquo;
                  </button>
                </li>
              </ul>
            </nav>
          </div>
        </div>
      )}
    </div>
  );
};

export default UsersPage;
