// swimSets.js  # API pour la gestion des séries de natation

import api from './api';

// Récupérer toutes les séries
export const getSwimSets = () => {
  return api.get('/swim-sets');
};

// Récupérer une série par ID
export const getSwimSet = (id) => {
  return api.get(`/swim-sets/${id}`);
};

// Créer une nouvelle série
export const createSwimSet = (swimSetData) => {
  return api.post('/swim-sets', swimSetData);
};

// Mettre à jour une série
export const updateSwimSet = (id, swimSetData) => {
  return api.post(`/swim-sets/${id}`, {
    ...swimSetData,
    _method: 'PATCH'  // Laravel form method spoofing
  });
};

// Supprimer une série
export const deleteSwimSet = (id) => {
  return api.delete(`/swim-sets/${id}`);
};
