import React from 'react';
import { Link, useLocation, useNavigate } from 'react-router-dom';
import useAuth from '../../hooks/useAuth';
import '../../assets/styles/Admin-layout.css';

const AdminHeader = () => {
  const location = useLocation();
  const navigate = useNavigate();
  const { logout, user } = useAuth();

  /**
   * ðŸ‡¬ðŸ‡§ Handle user logout with error management
   * ðŸ‡«ðŸ‡· Gestion de la dÃ©connexion avec gestion des erreurs
   */
  const handleLogout = async () => {
    try {
      await logout();
      navigate('/login');
    } catch (error) {
      console.error('Erreur lors de la dÃ©connexion:', error);
      // TODO: Ajouter une notification d'erreur pour l'utilisateur
    }
  };

  /**
   * ðŸ‡¬ðŸ‡§ Navigation links for administration
   * ðŸ‡«ðŸ‡· Liens de navigation pour l'administration
   */
  const navLinks = [
    {
      label: 'Utilisateurs',
      to: '/admin/users',
      icon: 'bi-people',
    },
    {
      label: 'Exercices',
      to: '/admin/exercises',
      icon: 'bi-water',
    },
    {
      label: 'SÃ©ances',
      to: '/admin/workouts',
      icon: 'bi-life-preserver',
    },
    {
      label: 'Plans',
      to: '/admin/plans',
      icon: 'bi-calendar-check',
    },
    {
      label: 'SÃ©ries',
      to: '/admin/swim-sets',
      icon: 'bi-list-check',
    },
    {
      label: 'Pages',
      to: '/admin/pages',
      icon: 'bi-file-earmark-text',
    },
    {
      label: 'MÃ©dias',
      to: '/admin/uploads',
      icon: 'bi-images',
    },
  ];

  /**
   * ðŸ‡¬ðŸ‡§ Function to determine if link is active
   * ðŸ‡«ðŸ‡· Fonction pour dÃ©terminer si un lien est actif
   */
  const isActive = (path) => {
    return location.pathname === path || location.pathname.startsWith(path + '/') ? 'active' : '';
  };

  return (
    <header className="admin-header">
      <div className="container-fluid">
        <nav className="navbar navbar-expand-lg navbar-dark">
          <Link to="/admin" className="navbar-brand admin-logo-wrapper">
            <img
              src="/assets/images/logo/nataswim_app_logo_4.png"
              alt="Logo Administration"
              height="120"
              className="me-2"
            />
          </Link>

          <button
            className="navbar-toggler"
            type="button"
            data-bs-toggle="collapse"
            data-bs-target="#adminHeaderNavbar"
            aria-controls="adminHeaderNavbar"
            aria-expanded="false"
            aria-label="Toggle navigation"
          >
            <span className="navbar-toggler-icon"></span>
          </button>

          <div className="collapse navbar-collapse" id="adminHeaderNavbar">
            <ul className="navbar-nav ms-auto">
              {navLinks.map((link, idx) => (
                <li 
                  key={idx} 
                  className={`nav-item ${isActive(link.to)}`}
                >
                  <Link
                    to={link.to}
                    className={`admin-nav-link admin-menu-item ${isActive(link.to)}`}
                  >
                    <i className={`bi ${link.icon} me-2`}></i>
                    {link.label}
                  </Link>
                </li>
              ))}

              {/* ðŸ‡¬ðŸ‡§ User info and logout button 
                 ðŸ‡«ðŸ‡· Informations utilisateur et bouton de dÃ©connexion */}
              <li className="nav-item ms-2">
                <div className="d-flex align-items-center">
                  {user && (
                    <span className="me-3 text-light">
                      <i className="bi bi-person-circle me-1"></i>
                      {user.name || user.email}
                    </span>
                  )}
                  <button
                    className="admin-logout-btn"
                    onClick={handleLogout}
                    title="DÃ©connexion"
                  >
                    <i className="bi bi-box-arrow-right"></i>
                    <span className="d-none d-md-inline ms-2">DÃ©connexion</span>
                  </button>
                </div>
              </li>
            </ul>
          </div>
        </nav>
      </div>
    </header>
  );
};

export default AdminHeader;
