// src/pages/admin/DashboardPage.jsx
// Tableau de bord administrateur avec des statistiques et des graphiques
import React, { useEffect, useState } from 'react';
import { Link } from 'react-router-dom';
import axios from 'axios';
import { FaUsers, FaDumbbell, FaListAlt, FaCalendarAlt, FaFileAlt, FaSwimmer, 
         FaPlus, FaCog, FaChartLine, FaBell, FaArrowUp, FaArrowDown, 
         FaExclamationTriangle, FaInfoCircle } from 'react-icons/fa';

const DashboardPage = () => {
  const [stats, setStats] = useState({ users: 0, exercises: 0, workouts: 0, plans: 0, pages: 0, swimSets: 0 });
  const [recentItems, setRecentItems] = useState({
    exercises: [],
    workouts: [],
    plans: []
  });


  useEffect(() => {
    setIsLoading(true);
    Promise.all([
      axios.get('http://127.0.0.1:8000/api/users'),
      axios.get('http://127.0.0.1:8000/api/exercises'),
      axios.get('http://127.0.0.1:8000/api/workouts'),
      axios.get('http://127.0.0.1:8000/api/plans'),
      axios.get('http://127.0.0.1:8000/api/pages'),
      axios.get('http://127.0.0.1:8000/api/swim-sets')
    ]).then(([users, exercises, workouts, plans, pages, swimSets]) => {
      // Statistiques globales
      setStats({
        users: users.data.length,
        exercises: exercises.data.length,
        workouts: workouts.data.length,
        plans: plans.data.length,
        pages: pages.data.length,
        swimSets: swimSets.data.length
      });

      // Récupérer les éléments récents
      setRecentItems({
        exercises: exercises.data.slice(0, 5).sort((a, b) => new Date(b.created_at) - new Date(a.created_at)),
        workouts: workouts.data.slice(0, 5).sort((a, b) => new Date(b.created_at) - new Date(a.created_at)),
        plans: plans.data.slice(0, 5).sort((a, b) => new Date(b.created_at) - new Date(a.created_at))
      });

      setIsLoading(false);
    }).catch((error) => {
      console.error("Erreur lors du chargement des données:", error);
      setError("Impossible de charger les données. Vérifiez votre connexion à l'API.");
      setIsLoading(false);
    });
  }, []);

  // Formater la date
  const formatDate = (dateString) => {
    const options = { day: '2-digit', month: '2-digit', year: 'numeric', hour: '2-digit', minute: '2-digit' };
    return new Date(dateString).toLocaleDateString('fr-FR', options);
  };

  // Liens rapides pour la gestion
  const quickLinks = [
    { to: '/admin/users/new', label: 'Ajouter un utilisateur', icon: FaUsers, color: 'primary' },
    { to: '/admin/exercises/new', label: 'Ajouter un exercice', icon: FaDumbbell, color: 'success' },
    { to: '/admin/workouts/new', label: 'Créer une séance', icon: FaSwimmer, color: 'info' },
    { to: '/admin/plans/new', label: 'Créer un plan', icon: FaCalendarAlt, color: 'warning' },
    { to: '/admin/pages/new', label: 'Ajouter une page', icon: FaFileAlt, color: 'danger' },
    { to: '/admin/swim-sets/new', label: 'Ajouter une série', icon: FaListAlt, color: 'secondary' }
  ];

  if (isLoading) {
    return (
      <div className="d-flex justify-content-center align-items-center" style={{ minHeight: '300px' }}>
        <div className="spinner-border text-primary" role="status">
          <span className="visually-hidden">Chargement...</span>
        </div>
        <span className="ms-3">Chargement du tableau de bord...</span>
      </div>
    );
  }

  if (error) {
    return (
      <div className="alert alert-danger" role="alert">
        <FaExclamationTriangle className="me-2" />
        {error}
        <button onClick={() => window.location.reload()} className="btn btn-sm btn-danger ms-3">
          Réessayer
        </button>
      </div>
    );
  }

  return (
    <div className="dashboard-container">
      <div className="row mb-4">
        <div className="col-12">
          <div className="card border-0 shadow-sm">
            <div className="card-body">
              <h1 className="display-6 text-primary mb-0">
                <FaChartLine className="me-3" />
                Tableau de Bord
              </h1>
            </div>
          </div>
        </div>
      </div>



      {/* Actions rapides */}
      <div className="row mb-4">
        <div className="col-12">
          <div className="card border-0 shadow-sm">
            <div className="card-header bg-white">
              <h5 className="card-title mb-0">
                <FaCog className="me-2" />
                Actions rapides
              </h5>
            </div>
            <div className="card-body">
              <div className="row g-3">
                {quickLinks.map((link, index) => (
                  <div key={index} className="col-md-4 col-sm-6">
                    <Link to={link.to} className={`btn btn-${link.color} w-100 d-flex align-items-center justify-content-between p-3`}>
                      <span><link.icon className="me-2" /> {link.label}</span>
                      <FaPlus />
                    </Link>
                  </div>
                ))}
              </div>
            </div>
          </div>
        </div>
      </div>

      {/* Statistiques globales */}
      <div className="row mb-4">
        <div className="col-12">
          <div className="card border-0 shadow-sm">
            <div className="card-header bg-white">
              <h5 className="card-title mb-0">
                <FaChartLine className="me-2" />
                Statistiques globales
              </h5>
            </div>
            <div className="card-body">
              <div className="row g-3">
                {[
                  { key: 'users', icon: FaUsers, label: 'Utilisateurs', color: 'primary', link: '/admin/users' },
                  { key: 'exercises', icon: FaDumbbell, label: 'Exercices', color: 'success', link: '/admin/exercises' },
                  { key: 'workouts', icon: FaSwimmer, label: 'Séances', color: 'info', link: '/admin/workouts' },
                  { key: 'plans', icon: FaCalendarAlt, label: 'Plans', color: 'warning', link: '/admin/plans' },
                  { key: 'pages', icon: FaFileAlt, label: 'Pages', color: 'danger', link: '/admin/pages' },
                  { key: 'swimSets', icon: FaListAlt, label: 'Séries', color: 'secondary', link: '/admin/swim-sets' }
                ].map(({ key, icon: Icon, label, color, link }) => (
                  <div key={key} className="col-md-4 col-sm-6">
                    <Link to={link} className="text-decoration-none">
                      <div className={`card border-${color} hover-shadow h-100`}>
                        <div className="card-body p-4">
                          <div className="d-flex justify-content-between align-items-center">
                            <div>
                              <h3 className={`text-${color} fw-bold display-5`}>{stats[key]}</h3>
                              <h5 className="text-muted">{label}</h5>
                            </div>
                            <div className={`bg-${color} bg-opacity-10 p-3 rounded-circle`}>
                              <Icon className={`text-${color} fs-1`} />
                            </div>
                          </div>
                          <div className="progress mt-3" style={{ height: "5px" }}>
                            <div 
                              className={`progress-bar bg-${color}`} 
                              role="progressbar" 
                              style={{ width: `${Math.min(100, stats[key] * 2)}%` }}
                              aria-valuenow={stats[key]} 
                              aria-valuemin="0" 
                              aria-valuemax="100">
                            </div>
                          </div>
                        </div>
                      </div>
                    </Link>
                  </div>
                ))}
              </div>
            </div>
          </div>
        </div>
      </div>

      {/* Publications récentes */}
      <div className="row">
        <div className="col-md-4 mb-4">
          <div className="card border-0 shadow-sm h-100">
            <div className="card-header bg-white d-flex justify-content-between align-items-center">
              <h5 className="card-title mb-0">
                <FaDumbbell className="me-2 text-success" />
                Derniers exercices
              </h5>
              <Link to="/admin/exercises" className="btn btn-sm btn-outline-success">Voir tout</Link>
            </div>
            <div className="card-body p-0">
              {recentItems.exercises.length > 0 ? (
                <ul className="list-group list-group-flush">
                  {recentItems.exercises.map(exercise => (
                    <li key={exercise.id} className="list-group-item d-flex justify-content-between align-items-center">
                      <div>
                        <h6 className="mb-0">{exercise.title}</h6>
                        <small className="text-muted">{formatDate(exercise.created_at)}</small>
                      </div>
                      <Link to={`/admin/exercises/${exercise.id}`} className="btn btn-sm btn-outline-secondary">
                        <FaCog />
                      </Link>
                    </li>
                  ))}
                </ul>
              ) : (
                <div className="text-center p-4">
                  <p className="text-muted">Aucun exercice disponible</p>
                </div>
              )}
            </div>
          </div>
        </div>

        <div className="col-md-4 mb-4">
          <div className="card border-0 shadow-sm h-100">
            <div className="card-header bg-white d-flex justify-content-between align-items-center">
              <h5 className="card-title mb-0">
                <FaSwimmer className="me-2 text-info" />
                Dernières séances
              </h5>
              <Link to="/admin/workouts" className="btn btn-sm btn-outline-info">Voir tout</Link>
            </div>
            <div className="card-body p-0">
              {recentItems.workouts.length > 0 ? (
                <ul className="list-group list-group-flush">
                  {recentItems.workouts.map(workout => (
                    <li key={workout.id} className="list-group-item d-flex justify-content-between align-items-center">
                      <div>
                        <h6 className="mb-0">{workout.title}</h6>
                        <small className="text-muted">{formatDate(workout.created_at)}</small>
                      </div>
                      <Link to={`/admin/workouts/${workout.id}`} className="btn btn-sm btn-outline-secondary">
                        <FaCog />
                      </Link>
                    </li>
                  ))}
                </ul>
              ) : (
                <div className="text-center p-4">
                  <p className="text-muted">Aucune séance disponible</p>
                </div>
              )}
            </div>
          </div>
        </div>

        <div className="col-md-4 mb-4">
          <div className="card border-0 shadow-sm h-100">
            <div className="card-header bg-white d-flex justify-content-between align-items-center">
              <h5 className="card-title mb-0">
                <FaCalendarAlt className="me-2 text-warning" />
                Derniers plans
              </h5>
              <Link to="/admin/plans" className="btn btn-sm btn-outline-warning">Voir tout</Link>
            </div>
            <div className="card-body p-0">
              {recentItems.plans.length > 0 ? (
                <ul className="list-group list-group-flush">
                  {recentItems.plans.map(plan => (
                    <li key={plan.id} className="list-group-item d-flex justify-content-between align-items-center">
                      <div>
                        <h6 className="mb-0">{plan.title}</h6>
                        <small className="text-muted">{formatDate(plan.created_at)}</small>
                      </div>
                      <Link to={`/admin/plans/${plan.id}`} className="btn btn-sm btn-outline-secondary">
                        <FaCog />
                      </Link>
                    </li>
                  ))}
                </ul>
              ) : (
                <div className="text-center p-4">
                  <p className="text-muted">Aucun plan disponible</p>
                </div>
              )}
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default DashboardPage;
