import React, { useState, useEffect } from 'react';
import { MapContainer, TileLayer, Marker, Popup, useMap } from 'react-leaflet';
import { Icon } from 'leaflet';
import { Search, Navigation } from 'lucide-react';
import 'leaflet/dist/leaflet.css';

/** 
 * üá¨üáß Component to recenter map when location changes
 * üá´üá∑ Composant pour recentrer la carte lorsque la position change
 */
function RecenterMap({ position, zoom }) {
  const map = useMap();

  useEffect(() => {
    if (position) {
      map.setView(position, zoom || map.getZoom());
    }
  }, [position, map, zoom]);

  return null;
}

/** 
 * üá¨üáß Main InteractiveMap component
 * üá´üá∑ Composant principal InteractiveMap
 */
export default function InteractiveMap() {
  const [userLocation, setUserLocation] = useState(null);
  const [selectedLocation, setSelectedLocation] = useState(null);
  const [error, setError] = useState('');
  const [mapType, setMapType] = useState('streets');
  const [searchQuery, setSearchQuery] = useState('');
  const [isSearching, setIsSearching] = useState(false);
  const [locationName, setLocationName] = useState('');

  /** 
   * üá¨üáß Custom marker icon configuration
   * üá´üá∑ Configuration de l'ic√¥ne de marqueur personnalis√©
   */
  const customIcon = new Icon({
    iconUrl: 'https://raw.githubusercontent.com/pointhi/leaflet-color-markers/master/img/marker-icon-2x-blue.png',
    iconSize: [25, 41],
    iconAnchor: [12, 41],
    popupAnchor: [1, -34],
  });

  /** 
   * üá¨üáß Get user's current location
   * üá´üá∑ Obtenir la position actuelle de l'utilisateur
   */
  const getUserLocation = () => {
    if (!navigator.geolocation) {
      setError('La g√©olocalisation n\'est pas support√©e par votre navigateur');
      return;
    }

    navigator.geolocation.getCurrentPosition(
      (position) => {
        const { latitude, longitude } = position.coords;
        setUserLocation({ lat: latitude, lng: longitude });
        setError('');
      },
      (error) => {
        setError(`Erreur de g√©olocalisation: ${error.message}`);
      }
    );
  };

  /** 
   * üá¨üáß Search for a location using OpenStreetMap Nominatim API
   * üá´üá∑ Rechercher un lieu en utilisant l'API Nominatim d'OpenStreetMap
   */
  const searchLocation = async (e) => {
    e.preventDefault();
    if (!searchQuery.trim()) return;

    setIsSearching(true);
    setError('');

    try {
      const response = await fetch(
        `https://nominatim.openstreetmap.org/search?format=json&q=${encodeURIComponent(searchQuery)}&limit=1`
      );
      const data = await response.json();

      if (data && data.length > 0) {
        const { lat, lon, display_name } = data[0];
        const newLocation = { lat: parseFloat(lat), lng: parseFloat(lon) };
        setSelectedLocation(newLocation);
        setLocationName(display_name);
      } else {
        setError('Aucun r√©sultat trouv√© pour cette recherche');
      }
    } catch (err) {
      setError('Erreur lors de la recherche du lieu');
    } finally {
      setIsSearching(false);
    }
  };

  /** 
   * üá¨üáß Get Google Maps directions URL
   * üá´üá∑ Obtenir l'URL pour l'itin√©raire Google Maps
   */
  const getDirectionsUrl = () => {
    if (!userLocation || !selectedLocation) return '';
    return `https://www.google.com/maps/dir/${userLocation.lat},${userLocation.lng}/${selectedLocation.lat},${selectedLocation.lng}`;
  };

  return (
    <div className="h-screen flex flex-col">
      <div className="p-4 bg-white shadow-md z-10">
        <form onSubmit={searchLocation} className="mb-4">
          <div className="flex gap-2">
            <div className="flex-1 relative">
              <input
                type="text"
                value={searchQuery}
                onChange={(e) => setSearchQuery(e.target.value)}
                placeholder="Rechercher un lieu..."
                className="w-full px-4 py-2 pr-10 border rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-500"
              />
              <button
                type="submit"
                className="absolute right-2 top-1/2 -translate-y-1/2 text-gray-500 hover:text-blue-500"
                disabled={isSearching}
              >
                <Search size={20} />
              </button>
            </div>
          </div>
        </form>

        <div className="flex flex-wrap gap-4 items-center">
          <button
            onClick={getUserLocation}
            className="bg-blue-500 text-white px-4 py-2 rounded hover:bg-blue-600 transition-colors"
          >
            Ma Position
          </button>
          <select
            value={mapType}
            onChange={(e) => setMapType(e.target.value)}
            className="border rounded px-3 py-2"
          >
            <option value="streets">Plan</option>
            <option value="satellite">Satellite</option>
          </select>
          {userLocation && selectedLocation && (
            <a
              href={getDirectionsUrl()}
              target="_blank"
              rel="noopener noreferrer"
              className="inline-flex items-center gap-2 bg-green-500 text-white px-4 py-2 rounded hover:bg-green-600 transition-colors"
            >
              <Navigation size={20} />
              Itin√©raire Google Maps
            </a>
          )}
        </div>
        {error && (
          <div className="mt-2 text-red-500">{error}</div>
        )}
      </div>

      <div className="flex-1 relative">
        <MapContainer
          center={[48.8566, 2.3522]} // Paris by default
          zoom={13}
          className="h-full w-full"
        >
          <TileLayer
            url={mapType === 'streets' 
              ? 'https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png'
              : 'https://server.arcgisonline.com/ArcGIS/rest/services/World_Imagery/MapServer/tile/{z}/{y}/{x}'
            }
          />

          {userLocation && (
            <Marker position={userLocation} icon={customIcon}>
              <Popup>Votre position</Popup>
            </Marker>
          )}

          {selectedLocation && (
            <Marker position={selectedLocation} icon={customIcon}>
              <Popup>{locationName || 'Destination'}</Popup>
            </Marker>
          )}

          <RecenterMap position={selectedLocation || userLocation} zoom={selectedLocation ? 15 : 13} />
        </MapContainer>
      </div>
    </div>
  );
}
