import React, { useState, useEffect } from 'react';
import { useParams, useNavigate, Link } from 'react-router-dom';
import { 
  FaArrowLeft, 
  FaSave, 
  FaExclamationTriangle, 
  FaInfoCircle, 
  FaSwimmer, 
  FaTags, 
  FaFileAlt,
  FaPlus
} from 'react-icons/fa';
import { getWorkout, createWorkout, updateWorkout } from '../../../services/workouts';
import useAuth from '../../../hooks/useAuth';

/**
 * üá¨üáß Workout Form Page Component for creating and editing workouts
 * This component renders a form for users to create new workouts or edit existing ones
 * 
 * üá´üá∑ Composant de formulaire de s√©ance pour cr√©er et modifier des s√©ances
 * Ce composant affiche un formulaire permettant aux utilisateurs de cr√©er ou modifier des s√©ances
 */
const WorkoutFormPage = () => {
  const { id } = useParams(); // Get ID from URL if editing
  const navigate = useNavigate();
  const { user } = useAuth();

  // Determine if we're editing or creating
  const isEditing = !!id;

  /**
   * üá¨üáß State management for the component
   * 
   * üá´üá∑ Gestion des √©tats du composant
   */
  const [loading, setLoading] = useState(isEditing);
  const [submitting, setSubmitting] = useState(false);
  const [error, setError] = useState(null);
  const [successMessage, setSuccessMessage] = useState(null);

  /**
   * üá¨üáß Form data state with default values
   * 
   * üá´üá∑ √âtat des donn√©es du formulaire avec valeurs par d√©faut
   */
  const [formData, setFormData] = useState({
    title: '',
    description: '',
    workout_category: '',
    user_id: user?.id || ''
  });

  /**
   * üá¨üáß Available workout categories
   * 
   * üá´üá∑ Cat√©gories de s√©ances disponibles
   */
  const workoutCategories = ['Aero 1', 'Vitesse', 'Mixte', 'Technique', 'R√©cup√©ration'];

  /**
   * üá¨üáß Load workout data if editing
   * 
   * üá´üá∑ Charger les donn√©es de la s√©ance si en mode √©dition
   */
  useEffect(() => {
    const fetchWorkoutData = async () => {
      if (!id) return;

      try {
        setLoading(true);
        const response = await getWorkout(id);

        if (!response || !response.data) {
          throw new Error("Donn√©es de la s√©ance non disponibles");
        }

        // Verify user has permission to edit this workout
        if (response.data.user_id !== user?.id) {
          setError("Vous n'avez pas l'autorisation de modifier cette s√©ance");
          setLoading(false);
          return;
        }

        setFormData({
          title: response.data.title || '',
          description: response.data.description || '',
          workout_category: response.data.workout_category || '',
          user_id: response.data.user_id
        });

        setError(null);
      } catch (err) {
        console.error('Erreur lors de la r√©cup√©ration des donn√©es de la s√©ance:', err);
        setError('Impossible de charger les d√©tails de cette s√©ance. Veuillez r√©essayer plus tard.');
      } finally {
        setLoading(false);
      }
    };

    if (isEditing && user) {
      fetchWorkoutData();
    }
  }, [id, user]);

  /**
   * üá¨üáß Handle form input changes
   * 
   * üá´üá∑ G√©rer les changements dans les champs du formulaire
   */
  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData(prev => ({
      ...prev,
      [name]: value
    }));
  };

  /**
   * üá¨üáß Handle form submission (create or update)
   * 
   * üá´üá∑ G√©rer la soumission du formulaire (cr√©ation ou mise √† jour)
   */
  const handleSubmit = async (e) => {
    e.preventDefault();

    // Validate form
    if (!formData.title.trim()) {
      setError("Le titre ne peut pas √™tre vide");
      return;
    }

    setSubmitting(true);
    setError(null);

    try {
      if (isEditing) {
        // Update existing workout
        const response = await updateWorkout(id, formData);
        setSuccessMessage("S√©ance mise √† jour avec succ√®s!");
        setTimeout(() => {
          navigate(`/user/workouts/${id}`);
        }, 1500);
      } else {
        // Create new workout
        const data = {
          ...formData,
          user_id: user.id
        };

        const response = await createWorkout(data);
        setSuccessMessage("S√©ance cr√©√©e avec succ√®s!");
        setTimeout(() => {
          navigate(`/user/workouts/${response.data.id}`);
        }, 1500);
      }
    } catch (err) {
      console.error('Erreur lors de la soumission du formulaire:', err);

      // Add more detailed error logging
      if (err.response) {
        console.error('D√©tails de l\'erreur:', err.response.data);
      }

      setError(isEditing 
        ? 'Impossible de mettre √† jour la s√©ance. Veuillez r√©essayer.' 
        : 'Impossible de cr√©er la s√©ance. Veuillez r√©essayer.');
    } finally {
      setSubmitting(false);
    }
  };

  /**
   * üá¨üáß Cancel and return to previous page
   * 
   * üá´üá∑ Annuler et retourner √† la page pr√©c√©dente
   */
  const handleCancel = () => {
    if (isEditing) {
      navigate(`/user/workouts/${id}`);
    } else {
      navigate('/user/workouts');
    }
  };

  return (
    <div className="container py-5">
      {/* Back button */}
      <div className="mb-4">
        <Link to="/user/workouts" className="btn btn-outline-secondary">
          <FaArrowLeft className="me-2" /> Retour aux s√©ances
        </Link>
      </div>

      {/* Main form card */}
      <div className="card shadow-sm border-0">
        <div className="card-header bg-primary bg-gradient text-white py-3">
          <h1 className="h4 mb-0">
            {isEditing ? 'Modifier la s√©ance' : 'Cr√©er une nouvelle s√©ance'}
          </h1>
        </div>

        <div className="card-body p-4">
          {/* Success message */}
          {successMessage && (
            <div className="alert alert-success alert-dismissible fade show" role="alert">
              <FaInfoCircle className="me-2" /> {successMessage}
              <button type="button" className="btn-close" onClick={() => setSuccessMessage(null)} aria-label="Close"></button>
            </div>
          )}

          {/* Error message */}
          {error && (
            <div className="alert alert-danger alert-dismissible fade show" role="alert">
              <FaExclamationTriangle className="me-2" /> {error}
              <button type="button" className="btn-close" onClick={() => setError(null)} aria-label="Close"></button>
            </div>
          )}

          {/* Loading state */}
          {loading ? (
            <div className="text-center py-5">
              <div className="spinner-border text-primary" role="status">
                <span className="visually-hidden">Chargement...</span>
              </div>
              <p className="mt-3">Chargement des donn√©es de la s√©ance...</p>
            </div>
          ) : (
            <form onSubmit={handleSubmit}>
              {/* Title field */}
              <div className="mb-4">
                <label htmlFor="title" className="form-label">
                  <FaSwimmer className="me-2 text-primary" /> 
                  Titre <span className="text-danger">*</span>
                </label>
                <input
                  type="text"
                  className="form-control form-control-lg"
                  id="title"
                  name="title"
                  value={formData.title}
                  onChange={handleChange}
                  required
                  placeholder="Titre de la s√©ance d'entra√Ænement"
                  disabled={submitting}
                />
                <div className="form-text">
                  Donnez un titre clair √† votre s√©ance d'entra√Ænement
                </div>
              </div>

              {/* Description field */}
              <div className="mb-4">
                <label htmlFor="description" className="form-label">
                  <FaFileAlt className="me-2 text-primary" /> 
                  Description
                </label>
                <textarea
                  className="form-control"
                  id="description"
                  name="description"
                  rows="5"
                  value={formData.description || ''}
                  onChange={handleChange}
                  placeholder="Description d√©taill√©e de la s√©ance d'entra√Ænement"
                  disabled={submitting}
                ></textarea>
                <div className="form-text">
                  D√©crivez l'objectif et le contenu de cette s√©ance
                </div>
              </div>

              {/* Category selection */}
              <div className="mb-4">
                <label htmlFor="workout_category" className="form-label">
                  <FaTags className="me-2 text-primary" /> 
                  Cat√©gorie <span className="text-danger">*</span>
                </label>
                <select
                  className="form-select"
                  id="workout_category"
                  name="workout_category"
                  value={formData.workout_category}
                  onChange={handleChange}
                  required
                  disabled={submitting}
                >
                  <option value="">-- S√©lectionner une cat√©gorie --</option>
                  {workoutCategories.map(category => (
                    <option key={category} value={category}>{category}</option>
                  ))}
                </select>
                <div className="form-text">
                  Choisissez la cat√©gorie qui correspond le mieux √† cette s√©ance
                </div>
              </div>

              {/* Form actions */}
              <div className="d-flex gap-2 justify-content-end mt-5 pt-3 border-top">
                <button
                  type="button"
                  className="btn btn-outline-secondary"
                  onClick={handleCancel}
                  disabled={submitting}
                >
                  Annuler
                </button>
                <button
                  type="submit"
                  className="btn btn-primary"
                  disabled={submitting}
                >
                  {submitting ? (
                    <>
                      <span className="spinner-border spinner-border-sm me-2" role="status" aria-hidden="true"></span>
                      {isEditing ? 'Mise √† jour...' : 'Cr√©ation...'}
                    </>
                  ) : (
                    <>
                      <FaSave className="me-2" /> 
                      {isEditing ? 'Mettre √† jour' : 'Cr√©er la s√©ance'}
                    </>
                  )}
                </button>
              </div>
            </form>
          )}
        </div>
      </div>

      {/* Info section for new workouts */}
      {!isEditing && (
        <div className="card mt-4 shadow-sm border-0">
          <div className="card-body">
            <h2 className="h5 mb-3">
              <FaInfoCircle className="me-2 text-info" /> 
              Comment cr√©er une s√©ance efficace ?
            </h2>
            <div className="row g-3">
              <div className="col-md-4">
                <div className="card h-100 bg-light border-0">
                  <div className="card-body">
                    <h3 className="h6 fw-bold">1. D√©finir un objectif clair</h3>
                    <p className="card-text small mb-0">
                      Chaque s√©ance devrait avoir un objectif principal : endurance, vitesse, technique, etc.
                    </p>
                  </div>
                </div>
              </div>
              <div className="col-md-4">
                <div className="card h-100 bg-light border-0">
                  <div className="card-body">
                    <h3 className="h6 fw-bold">2. Structurer la s√©ance</h3>
                    <p className="card-text small mb-0">
                      Incluez un √©chauffement, une partie principale et un retour au calme pour une s√©ance √©quilibr√©e.
                    </p>
                  </div>
                </div>
              </div>
              <div className="col-md-4">
                <div className="card h-100 bg-light border-0">
                  <div className="card-body">
                    <h3 className="h6 fw-bold">3. Ajoutez des s√©ries vari√©es</h3>
                    <p className="card-text small mb-0">
                      Apr√®s avoir cr√©√© la s√©ance, vous pourrez ajouter diff√©rentes s√©ries et exercices pour la compl√©ter.
                    </p>
                  </div>
                </div>
              </div>
            </div>
          </div>
        </div>
      )}

      {/* Next steps for existing workouts */}
      {isEditing && (
        <div className="card mt-4 shadow-sm border-0">
          <div className="card-body">
            <h2 className="h5 mb-3">
              <FaInfoCircle className="me-2 text-info" /> 
              Que souhaitez-vous faire ensuite ?
            </h2>
            <div className="d-flex gap-3 flex-wrap">
              <Link to={`/user/workouts/${id}/add-exercise`} className="btn btn-outline-primary">
                <FaPlus className="me-2" /> Ajouter des exercices
              </Link>
              <Link to={`/user/workouts/${id}/add-swim-set`} className="btn btn-outline-primary">
                <FaPlus className="me-2" /> Ajouter des s√©ries
              </Link>
            </div>
          </div>
        </div>
      )}
    </div>
  );
};

export default WorkoutFormPage;
