import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import { 
  FaPlus, 
  FaEdit, 
  FaTrash, 
  FaEye, 
  FaFilter, 
  FaSearch, 
  FaSync,
  FaSwimmingPool,
  FaTag,
  FaUser,
  FaCalendarAlt,
  FaImage,
  FaWater,
  FaSwimmer,
  FaTint
} from 'react-icons/fa';
import { getExercises, deleteExercise } from '../../services/exercises';
import { getUpload } from '../../services/uploads';

/**
 * ðŸ‡¬ðŸ‡§ User's exercises list component 
 * This component displays a table of swimming training exercises with filtering and pagination
 * 
 * ðŸ‡«ðŸ‡· Composant de liste d'exercices utilisateur
 * Ce composant affiche un tableau d'exercices d'entraÃ®nement de natation avec filtrage et pagination
 */
const UserExercisesList = () => {
  const navigate = useNavigate();

  /**
   * ðŸ‡¬ðŸ‡§ State management for the component
   * 
   * ðŸ‡«ðŸ‡· Gestion des Ã©tats du composant
   */
  const [exercises, setExercises] = useState([]);
  const [imageData, setImageData] = useState({});
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [searchTerm, setSearchTerm] = useState('');
  const [categoryFilter, setCategoryFilter] = useState('all');
  const [levelFilter, setLevelFilter] = useState('all');
  const [currentPage, setCurrentPage] = useState(0);
  const [itemsPerPage, setItemsPerPage] = useState(10);
  const [thumbnailErrors, setThumbnailErrors] = useState({});

  // CatÃ©gories et niveaux d'exercices
  const exerciseCategories = ['Correctif De Nage', 'Correctif De Style', 'Travail de Base'];
  const exerciseLevels = ['DÃ©butant', 'IntermÃ©diaire', 'AvancÃ©'];

  /**
   * ðŸ‡¬ðŸ‡§ Fetch exercises data from API
   * 
   * ðŸ‡«ðŸ‡· RÃ©cupÃ©ration des donnÃ©es d'exercices depuis l'API
   */
  const fetchExercises = async () => {
    try {
      setLoading(true);
      const response = await getExercises();
      setExercises(response.data);

      // RÃ©cupÃ©rer les images pour les exercices avec upload_id
      const exercisesWithImages = response.data.filter(exercise => exercise.upload_id);

      // RÃ©cupÃ©rer les images en parallÃ¨le
      const imagePromises = exercisesWithImages.map(exercise => 
        getUpload(exercise.upload_id)
          .then(res => ({ exerciseId: exercise.id, imageData: res.data }))
          .catch(err => {
            console.error(`Erreur de chargement de l'image pour l'exercice ${exercise.id}:`, err);
            return { exerciseId: exercise.id, imageData: null };
          })
      );

      const imagesResults = await Promise.all(imagePromises);

      // CrÃ©er un objet de donnÃ©es d'images
      const imagesObj = imagesResults.reduce((acc, item) => {
        if (item.imageData) {
          acc[item.exerciseId] = item.imageData;
        }
        return acc;
      }, {});

      setImageData(imagesObj);
      setError(null);
    } catch (err) {
      console.error('Erreur lors de la rÃ©cupÃ©ration des exercices:', err);
      setError('Impossible de charger les exercices. Veuillez rÃ©essayer.');
    } finally {
      setLoading(false);
    }
  };

  // Charger les exercices au montage du composant
  useEffect(() => {
    fetchExercises();
  }, []);

  /**
   * ðŸ‡¬ðŸ‡§ Filter exercises based on search term, category and level
   * 
   * ðŸ‡«ðŸ‡· Filtrer les exercices selon le terme de recherche, la catÃ©gorie et le niveau
   */
  const filteredExercises = exercises.filter(exercise => {
    const matchesSearch = exercise.title.toLowerCase().includes(searchTerm.toLowerCase()) ||
                         (exercise.description && exercise.description.toLowerCase().includes(searchTerm.toLowerCase()));
    const matchesCategory = categoryFilter === 'all' || exercise.exercise_category === categoryFilter;
    const matchesLevel = levelFilter === 'all' || exercise.exercise_level === levelFilter;
    return matchesSearch && matchesCategory && matchesLevel;
  });

  // Pagination
  const pageCount = Math.ceil(filteredExercises.length / itemsPerPage);
  const offset = currentPage * itemsPerPage;
  const currentExercises = filteredExercises.slice(offset, offset + itemsPerPage);

  /**
   * ðŸ‡¬ðŸ‡§ Handle exercise deletion
   * 
   * ðŸ‡«ðŸ‡· Gestion de la suppression d'un exercice
   */
  const handleDelete = async (id, title) => {
    if (window.confirm(`ÃŠtes-vous sÃ»r de vouloir supprimer l'exercice "${title}" ?`)) {
      try {
        await deleteExercise(id);
        await fetchExercises();
      } catch (err) {
        setError('Erreur lors de la suppression: ' + err.message);
      }
    }
  };

  /**
   * ðŸ‡¬ðŸ‡§ Handle thumbnail loading errors
   * 
   * ðŸ‡«ðŸ‡· Gestion des erreurs de chargement des miniatures
   */
  const handleThumbnailError = (exerciseId) => {
    setThumbnailErrors(prev => ({
      ...prev,
      [exerciseId]: true
    }));
  };

  /**
   * ðŸ‡¬ðŸ‡§ Get color for level badge
   * 
   * ðŸ‡«ðŸ‡· Obtenir la couleur pour le badge de niveau
   */
  const getLevelBadgeColor = (level) => {
    switch (level) {
      case 'DÃ©butant': return 'success';
      case 'IntermÃ©diaire': return 'warning';
      case 'AvancÃ©': return 'danger';
      default: return 'secondary';
    }
  };

  /**
   * ðŸ‡¬ðŸ‡§ Format description for preview
   * 
   * ðŸ‡«ðŸ‡· Formater la description pour l'aperÃ§u
   */
  const formatDescriptionPreview = (description, maxLength = 120) => {
    if (!description) return "Aucune description disponible";
    return description.length > maxLength 
      ? description.substring(0, maxLength) + '...'
      : description;
  };

  // Affichage pendant le chargement
  if (loading) {
    return (
      <div className="container py-4">
        <div className="text-center">
          <div className="spinner-border text-primary" role="status">
            <span className="visually-hidden">Chargement...</span>
          </div>
        </div>
      </div>
    );
  }

  return (
    <div className="container-fluid py-4">
      {/* En-tÃªte */}
      <div>
        <h1 className="display-6 fw-bold mb-4 title-swim">Educatifs</h1>
      </div>
      <div className="d-flex justify-content-between align-items-center mb-4">
        <div>
          <p className="text-muted mb-0">
            {exercises.length} exercice{exercises.length > 1 ? 's' : ''} au total
          </p>
        </div>
      </div>

      {/* Filtres */}
      <div className="card shadow-sm mb-4">
        <div className="card-body">
          <div className="row g-3">
            <div className="col-md-4">
              <div className="input-group">
                <span className="input-group-text bg-light">
                  <FaSearch />
                </span>
                <input
                  type="text"
                  className="form-control"
                  placeholder="Rechercher un exercice..."
                  value={searchTerm}
                  onChange={(e) => setSearchTerm(e.target.value)}
                />
              </div>
            </div>
            <div className="col-md-8">
              <div className="d-flex gap-2">
                <div className="input-group">
                  <span className="input-group-text bg-light">
                    <FaFilter />
                  </span>
                  <select
                    className="form-select"
                    value={categoryFilter}
                    onChange={(e) => setCategoryFilter(e.target.value)}
                  >
                    <option value="all">Toutes les catÃ©gories</option>
                    {exerciseCategories.map(category => (
                      <option key={category} value={category}>{category}</option>
                    ))}
                  </select>
                </div>
                <div className="input-group">
                  <span className="input-group-text bg-light">
                    <FaFilter />
                  </span>
                  <select
                    className="form-select"
                    value={levelFilter}
                    onChange={(e) => setLevelFilter(e.target.value)}
                  >
                    <option value="all">Tous les niveaux</option>
                    {exerciseLevels.map(level => (
                      <option key={level} value={level}>{level}</option>
                    ))}
                  </select>
                </div>
                <button 
                  className="btn btn-outline-secondary" 
                  onClick={fetchExercises}
                  title="RafraÃ®chir"
                >
                  <FaSync />
                </button>
              </div>
            </div>
          </div>
        </div>
      </div>

      {/* Message d'erreur */}
      {error && (
        <div className="alert alert-danger" role="alert">
          {error}
        </div>
      )}

      {/* Tableau d'exercices */}
      <div className="card shadow-sm">
        <div className="card-body p-0">
          {currentExercises.length === 0 ? (
            <div className="p-4 text-center">
              <FaSwimmer className="text-muted fs-1 mb-3" />
              <p className="text-muted">
                Aucun exercice ne correspond Ã  vos critÃ¨res de recherche.
              </p>
            </div>
          ) : (
            <div className="table-responsive">
              <table className="table table-hover align-middle mb-0">
                <thead className="table-light">
                  <tr>
                    <th scope="col" style={{ width: '70px' }}>#</th>
                    <th scope="col" style={{ width: '80px' }}>Image</th>
                    <th scope="col">Titre</th>
                    <th scope="col">CatÃ©gorie</th>
                    <th scope="col">Niveau</th>
                    <th scope="col">Description</th>
                    <th scope="col" style={{ width: '100px' }}>Actions</th>
                  </tr>
                </thead>
                <tbody>
                  {currentExercises.map((exercise) => (
                    <tr key={exercise.id} className="align-middle">
                      <td>
                        <small className="text-muted">{exercise.id}</small>
                      </td>
                      <td>
                        {exercise.upload_id && imageData[exercise.id] && imageData[exercise.id].url && !thumbnailErrors[exercise.id] ? (
                          <img
                            src={imageData[exercise.id].url}
                            alt={exercise.title}
                            className="img-fluid rounded"
                            style={{ 
                              height: '50px',
                              width: '50px',
                              objectFit: 'cover'
                            }}
                            onError={() => handleThumbnailError(exercise.id)}
                          />
                        ) : (
                          <div className="bg-light rounded d-flex justify-content-center align-items-center" style={{ height: '50px', width: '50px' }}>
                            <FaSwimmingPool className="text-primary" />
                          </div>
                        )}
                      </td>
                      <td>
                        <strong>{exercise.title}</strong>
                      </td>
                      <td>
                        {exercise.exercise_category ? (
                          <span className="badge bg-info bg-opacity-10 text-info">
                            {exercise.exercise_category}
                          </span>
                        ) : (
                          <span className="text-muted">-</span>
                        )}
                      </td>
                      <td>
                        <span className={`badge bg-${getLevelBadgeColor(exercise.exercise_level)}`}>
                          {exercise.exercise_level || 'Non dÃ©fini'}
                        </span>
                      </td>
                      <td className="text-truncate" style={{ maxWidth: '250px' }}>
                        {formatDescriptionPreview(exercise.description, 60)}
                      </td>
                      <td>
                        <div className="btn-group btn-group-sm">
                          <button 
                            className="btn btn-outline-primary"
                            onClick={() => navigate(`/user/exercises/${exercise.id}`)}
                            title="Voir les dÃ©tails"
                          >
                            <FaEye />
                          </button>
                        </div>
                      </td>
                    </tr>
                  ))}
                </tbody>
              </table>
            </div>
          )}
        </div>
      </div>

      {/* Pagination */}
      {filteredExercises.length > itemsPerPage && (
        <div className="card mt-4">
          <div className="card-body d-flex justify-content-between align-items-center">
            <div className="d-flex align-items-center">
              <span className="me-2">Afficher</span>
              <select 
                className="form-select form-select-sm" 
                value={itemsPerPage}
                onChange={(e) => {
                  setItemsPerPage(Number(e.target.value));
                  setCurrentPage(0);
                }}
                style={{width: '70px'}}
              >
                <option value="5">5</option>
                <option value="10">10</option>
                <option value="25">25</option>
                <option value="50">50</option>
              </select>
              <span className="ms-2">Ã©lÃ©ments</span>
            </div>

            <nav aria-label="Navigation des pages">
              <ul className="pagination pagination-sm mb-0">
                <li className={`page-item ${currentPage === 0 ? 'disabled' : ''}`}>
                  <button 
                    className="page-link" 
                    onClick={() => setCurrentPage(currentPage - 1)}
                    disabled={currentPage === 0}
                  >
                    &laquo;
                  </button>
                </li>

                {[...Array(pageCount)].map((_, index) => (
                  <li key={index} className={`page-item ${currentPage === index ? 'active' : ''}`}>
                    <button
                      className="page-link"
                      onClick={() => setCurrentPage(index)}
                    >
                      {index + 1}
                    </button>
                  </li>
                ))}

                <li className={`page-item ${currentPage === pageCount - 1 ? 'disabled' : ''}`}>
                  <button 
                    className="page-link" 
                    onClick={() => setCurrentPage(currentPage + 1)}
                    disabled={currentPage === pageCount - 1}
                  >
                    &raquo;
                  </button>
                </li>
              </ul>
            </nav>
          </div>
        </div>
      )}
    </div>
  );
};

export default UserExercisesList;
