import React, { useState, useEffect } from 'react';
import { useParams, useNavigate, Link } from 'react-router-dom';
import axios from 'axios';
import { 
  FaArrowLeft, 
  FaEdit, 
  FaInfoCircle, 
  FaSwimmer, 
  FaRunning, 
  FaStopwatch, 
  FaRedoAlt, 
  FaDumbbell,
  FaCalendarAlt,
  FaListAlt,
  FaExternalLinkAlt,
  FaRulerHorizontal,
  FaTachometerAlt,
  FaChartLine,
  FaEye
} from 'react-icons/fa';

// API Base URL
const API_BASE_URL = 'http://127.0.0.1:8000/api';

const SwimSetsPage = () => {
  const { id } = useParams();
  const navigate = useNavigate();

  const [swimSet, setSwimSet] = useState(null);
  const [workout, setWorkout] = useState(null);
  const [exercise, setExercise] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [relatedSets, setRelatedSets] = useState([]);
  const [relatedPlans, setRelatedPlans] = useState([]);

  // Load swim set data
  useEffect(() => {
    const fetchSwimSet = async () => {
      try {
        setLoading(true);
        const response = await axios.get(`${API_BASE_URL}/swim-sets/${id}`);
        setSwimSet(response.data);

        // Fetch related data
        if (response.data.workout_id) {
          fetchWorkout(response.data.workout_id);
        }

        if (response.data.exercise_id) {
          fetchExercise(response.data.exercise_id);
        }

        // Fetch related plans (training plans that include this workout)
        fetchRelatedPlans(response.data.workout_id);

        setLoading(false);
      } catch (err) {
        setError(`Erreur lors du chargement de la série: ${err.message}`);
        setLoading(false);
      }
    };

    fetchSwimSet();
  }, [id]);

  // Fetch workout data
  const fetchWorkout = async (workoutId) => {
    try {
      const response = await axios.get(`${API_BASE_URL}/workouts/${workoutId}`);
      setWorkout(response.data);

      // Fetch other swim sets related to this workout
      fetchRelatedSets(workoutId);
    } catch (err) {
      console.error("Erreur lors du chargement de la séance:", err);
    }
  };

  // Fetch exercise data
  const fetchExercise = async (exerciseId) => {
    try {
      const response = await axios.get(`${API_BASE_URL}/exercises/${exerciseId}`);
      setExercise(response.data);
    } catch (err) {
      console.error("Erreur lors du chargement de l'exercice:", err);
    }
  };

  // Fetch related swim sets
  const fetchRelatedSets = async (workoutId) => {
    try {
      const response = await axios.get(`${API_BASE_URL}/workouts/${workoutId}/swim-sets`);
      // Filter out the current swim set
      const filteredSets = response.data.filter(set => set.id !== parseInt(id));
      setRelatedSets(filteredSets);
    } catch (err) {
      console.error("Erreur lors du chargement des séries associées:", err);
    }
  };

  // Fetch related training plans
  const fetchRelatedPlans = async (workoutId) => {
    if (!workoutId) return;

    try {
      const response = await axios.get(`${API_BASE_URL}/workouts/${workoutId}/training-plans`);
      setRelatedPlans(response.data);
    } catch (err) {
      console.error("Erreur lors du chargement des plans d'entraînement:", err);
    }
  };

  // Handle navigation
  const handleBack = () => {
    navigate('/admin/swim-sets');
  };

  const handleEdit = () => {
    navigate(`/admin/swim-sets/${id}/edit`);
  };

  const handleViewWorkout = () => {
    if (workout) {
      navigate(`/admin/workouts/${workout.id}`);
    }
  };

  const handleViewExercise = () => {
    if (exercise) {
      navigate(`/admin/exercises/${exercise.id}`);
    }
  };

  const handleViewSet = (setId) => {
    navigate(`/admin/swim-sets/${setId}`);
  };

  const handleViewPlan = (planId) => {
    navigate(`/admin/training-plans/${planId}`);
  };

  // Format time in seconds to MM:SS
  const formatRestTime = (seconds) => {
    if (!seconds) return '00:00';
    const mins = Math.floor(seconds / 60);
    const secs = seconds % 60;
    return `${mins.toString().padStart(2, '0')}:${secs.toString().padStart(2, '0')}`;
  };

  // Calculate total distance
  const calculateTotalDistance = () => {
    if (!swimSet) return 0;
    const distance = swimSet.set_distance || 0;
    const repetition = swimSet.set_repetition || 1;
    return distance * repetition;
  };

  // Render description with HTML sanitized
  const renderDescription = (description) => {
    if (!description) return <p className="text-muted mb-0">Aucune description disponible</p>;

    return (
      <div
        className="description-content"
        dangerouslySetInnerHTML={{ __html: description }}
      />
    );
  };

  if (loading) {
    return (
      <div className="container py-4">
        <div className="text-center py-5">
          <div className="spinner-border text-primary" role="status">
            <span className="visually-hidden">Chargement...</span>
          </div>
          <p className="mt-3">Chargement des données...</p>
        </div>
      </div>
    );
  }

  if (error) {
    return (
      <div className="container py-4">
        <div className="alert alert-danger" role="alert">
          {error}
        </div>
        <button 
          className="btn btn-outline-secondary" 
          onClick={handleBack}
        >
          <FaArrowLeft className="me-2" /> Retour à la liste
        </button>
      </div>
    );
  }

  if (!swimSet) {
    return (
      <div className="container py-4">
        <div className="alert alert-warning" role="alert">
          Série non trouvée
        </div>
        <button 
          className="btn btn-outline-secondary" 
          onClick={handleBack}
        >
          <FaArrowLeft className="me-2" /> Retour à la liste
        </button>
      </div>
    );
  }

  return (
    <div className="container py-4">
      <div className="card shadow mb-4">
        <div className="card-header bg-white">
          <div className="d-flex justify-content-between align-items-center">
            <div className="d-flex align-items-center">
              <button 
                className="btn btn-outline-secondary me-3" 
                onClick={handleBack}
              >
                <FaArrowLeft className="me-2" /> Retour
              </button>
              <div>
                <h5 className="mb-0">Détails de la Série</h5>
                <small className="text-muted">ID: {id}</small>
              </div>
            </div>
            <button
              className="btn btn-primary"
              onClick={handleEdit}
            >
              <FaEdit className="me-2" /> Modifier
            </button>
          </div>
        </div>

        <div className="card-body">
          <div className="row g-4">
            {/* Informations principales */}
            <div className="col-md-6">
              <div className="card h-100">
                <div className="card-header bg-white">
                  <h6 className="mb-0">
                    <FaSwimmer className="me-2 text-primary" /> Informations sur la Série
                  </h6>
                </div>
                <div className="card-body">
                  <div className="row">
                    <div className="col-6 mb-3">
                      <div className="d-flex align-items-center">
                        <div className="rounded-circle bg-light p-2 me-2">
                          <FaRulerHorizontal className="text-primary" />
                        </div>
                        <div>
                          <span className="text-muted d-block small">Distance par répétition</span>
                          <span className="h5">{swimSet.set_distance || 0} m</span>
                        </div>
                      </div>
                    </div>
                    <div className="col-6 mb-3">
                      <div className="d-flex align-items-center">
                        <div className="rounded-circle bg-light p-2 me-2">
                          <FaRedoAlt className="text-primary" />
                        </div>
                        <div>
                          <span className="text-muted d-block small">Nombre de répétitions</span>
                          <span className="h5">{swimSet.set_repetition || 1}</span>
                        </div>
                      </div>
                    </div>
                    <div className="col-6 mb-3">
                      <div className="d-flex align-items-center">
                        <div className="rounded-circle bg-light p-2 me-2">
                          <FaStopwatch className="text-primary" />
                        </div>
                        <div>
                          <span className="text-muted d-block small">Temps de repos</span>
                          <span className="h5">
                            {formatRestTime(swimSet.rest_time)} 
                            <small className="text-muted ms-1">({swimSet.rest_time || 0}s)</small>
                          </span>
                        </div>
                      </div>
                    </div>
                    <div className="col-6 mb-3">
                      <div className="d-flex align-items-center">
                        <div className="rounded-circle bg-primary p-2 me-2">
                          <FaTachometerAlt className="text-white" />
                        </div>
                        <div>
                          <span className="text-muted d-block small">Distance totale</span>
                          <span className="h5 text-primary fw-bold">{calculateTotalDistance()} m</span>
                        </div>
                      </div>
                    </div>
                  </div>

                  <div className="d-grid gap-2 mt-3">
                    <button 
                      className="btn btn-sm btn-outline-primary" 
                      onClick={handleEdit}
                    >
                      <FaEdit className="me-2" /> Modifier cette série
                    </button>
                  </div>
                </div>
              </div>
            </div>

            {/* Exercice associé */}
            <div className="col-md-6">
              <div className="card mb-3">
                <div className="card-header bg-white d-flex justify-content-between align-items-center">
                  <h6 className="mb-0">
                    <FaDumbbell className="me-2 text-primary" /> Exercice associé
                  </h6>
                  {exercise && (
                    <button 
                      className="btn btn-sm btn-outline-primary" 
                      onClick={handleViewExercise}
                    >
                      <FaExternalLinkAlt className="me-1" /> Voir la fiche
                    </button>
                  )}
                </div>
                <div className="card-body">
                  {exercise ? (
                    <>
                      <h5 className="card-title">{exercise.title}</h5>

                      <div className="my-3">
                        {exercise.exercise_level && (
                          <span className={`badge bg-${
                            exercise.exercise_level === 'Débutant' ? 'success' :
                            exercise.exercise_level === 'Intermédiaire' ? 'warning' :
                            exercise.exercise_level === 'Avancé' ? 'danger' :
                            'secondary'
                          } me-2`}>
                            {exercise.exercise_level}
                          </span>
                        )}
                        {exercise.exercise_category && (
                          <span className="badge bg-info">
                            {exercise.exercise_category}
                          </span>
                        )}
                      </div>

                      <div className="mt-3">
                        {renderDescription(exercise.description)}
                      </div>
                    </>
                  ) : (
                    <div className="alert alert-warning mb-0">
                      <FaInfoCircle className="me-2" /> Information sur l'exercice non disponible
                    </div>
                  )}
                </div>
              </div>
            </div>
          </div>

          {/* Séance d'entraînement associée */}
          <div className="card mt-4">
            <div className="card-header bg-white d-flex justify-content-between align-items-center">
              <h6 className="mb-0">
                <FaRunning className="me-2 text-primary" /> Séance d'entraînement
              </h6>
              {workout && (
                <button 
                  className="btn btn-sm btn-outline-primary" 
                  onClick={handleViewWorkout}
                >
                  <FaExternalLinkAlt className="me-1" /> Voir la séance complète
                </button>
              )}
            </div>
            <div className="card-body">
              {workout ? (
                <>
                  <h5 className="card-title">{workout.title}</h5>

                  {workout.workout_category && (
                    <div className="my-3">
                      <span className="badge bg-primary">
                        {workout.workout_category}
                      </span>

                      {workout.workout_level && (
                        <span className={`badge ms-2 bg-${
                          workout.workout_level === 'Débutant' ? 'success' :
                          workout.workout_level === 'Intermédiaire' ? 'warning' :
                          workout.workout_level === 'Avancé' ? 'danger' : 'secondary'
                        }`}>
                          Niveau: {workout.workout_level}
                        </span>
                      )}

                      {workout.duration && (
                        <span className="badge bg-secondary ms-2">
                          <FaStopwatch className="me-1" /> {workout.duration} min
                        </span>
                      )}
                    </div>
                  )}

                  <div className="mt-3">
                    {renderDescription(workout.description)}
                  </div>
                </>
              ) : (
                <div className="alert alert-warning mb-0">
                  <FaInfoCircle className="me-2" /> Information sur la séance non disponible
                </div>
              )}
            </div>
          </div>

          {/* Autres séries dans la même séance */}
          {relatedSets.length > 0 && (
            <div className="card mt-4">
              <div className="card-header bg-white">
                <h6 className="mb-0">
                  <FaListAlt className="me-2 text-primary" /> 
                  Autres séries dans cette séance ({relatedSets.length})
                </h6>
              </div>
              <div className="card-body">
                <div className="table-responsive">
                  <table className="table table-hover">
                    <thead className="table-light">
                      <tr>
                        <th>ID</th>
                        <th>Exercice</th>
                        <th className="text-center">Distance</th>
                        <th className="text-center">Répétitions</th>
                        <th className="text-center">Repos</th>
                        <th className="text-center">Total</th>
                        <th className="text-end">Actions</th>
                      </tr>
                    </thead>
                    <tbody>
                      {relatedSets.map((set) => (
                        <tr key={set.id}>
                          <td>{set.id}</td>
                          <td>
                            <div className="d-flex align-items-center">
                              <FaDumbbell className="text-primary me-2" />
                              <span>Exercice #{set.exercise_id}</span>
                            </div>
                          </td>
                          <td className="text-center">
                            <span className="badge bg-info text-dark">
                              {set.set_distance} m
                            </span>
                          </td>
                          <td className="text-center">
                            <span className="badge bg-primary">
                              x{set.set_repetition || 1}
                            </span>
                          </td>
                          <td className="text-center">
                            <span className="badge bg-secondary">
                              {formatRestTime(set.rest_time)}
                            </span>
                          </td>
                          <td className="text-center fw-bold">
                            <span className="badge bg-success">
                              {(set.set_distance || 0) * (set.set_repetition || 1)} m
                            </span>
                          </td>
                          <td className="text-end">
                            <button 
                              className="btn btn-sm btn-outline-primary" 
                              onClick={() => handleViewSet(set.id)}
                            >
                              <FaEye className="me-1" /> Voir
                            </button>
                          </td>
                        </tr>
                      ))}
                    </tbody>
                  </table>
                </div>
              </div>
            </div>
          )}

          {/* Plans d'entraînement associés */}
          {relatedPlans.length > 0 && (
            <div className="card mt-4">
              <div className="card-header bg-white">
                <h6 className="mb-0">
                  <FaChartLine className="me-2 text-primary" /> 
                  Plans d'entraînement incluant cette séance ({relatedPlans.length})
                </h6>
              </div>
              <div className="card-body">
                <div className="row row-cols-1 row-cols-md-2 row-cols-lg-3 g-4">
                  {relatedPlans.map(plan => (
                    <div key={plan.id} className="col">
                      <div className="card h-100 shadow-sm">
                        <div className="card-header bg-white">
                          <h6 className="mb-0 text-truncate" title={plan.title}>
                            <FaCalendarAlt className="me-2 text-primary" /> {plan.title}
                          </h6>
                        </div>
                        <div className="card-body">
                          <div className="mb-3">
                            {plan.plan_level && (
                              <span className={`badge bg-${
                                plan.plan_level === 'Débutant' ? 'success' :
                                plan.plan_level === 'Intermédiaire' ? 'warning' :
                                plan.plan_level === 'Avancé' ? 'danger' : 'secondary'
                              } me-2`}>
                                {plan.plan_level}
                              </span>
                            )}
                            {plan.duration && (
                              <span className="badge bg-secondary">
                                {plan.duration} semaines
                              </span>
                            )}
                          </div>

                          <p className="card-text small text-muted">
                            {plan.description ? 
                              (plan.description.length > 100 ? 
                                plan.description.substring(0, 100) + '...' : 
                                plan.description) : 
                              'Aucune description disponible'}
                          </p>
                        </div>
                        <div className="card-footer bg-white">
                          <button 
                            className="btn btn-sm btn-outline-primary w-100" 
                            onClick={() => handleViewPlan(plan.id)}
                          >
                            <FaExternalLinkAlt className="me-1" /> Voir le plan
                          </button>
                        </div>
                      </div>
                    </div>
                  ))}
                </div>
              </div>
            </div>
          )}
        </div>
      </div>
    </div>
  );
};

export default SwimSetsPage;
