/**
 * üá¨üáß Unauthorized Page with automatic redirection to login after brief delay
 * üá´üá∑ Page Non Autoris√© avec redirection automatique vers la page de connexion apr√®s un court d√©lai
 */
import React, { useEffect, useState } from "react";
import { Link, useNavigate, useLocation } from "react-router-dom";


const UnauthorizedPage = () => {
  const navigate = useNavigate();
  const location = useLocation();
  const [countdown, setCountdown] = useState(5);

  // R√©cup√©rer la page d'origine si elle existe dans l'√©tat
  const from = location.state?.from?.pathname || "/";

  useEffect(() => {
    // Redirection automatique apr√®s 5 secondes
    const timer = setTimeout(() => {
      // Rediriger vers la page de connexion avec l'information de la page d'origine
      navigate("/login", { state: { from } });
    }, 5000);

    // Compte √† rebours
    const countdownInterval = setInterval(() => {
      setCountdown(prev => {
        if (prev <= 1) {
          clearInterval(countdownInterval);
          return 0;
        }
        return prev - 1;
      });
    }, 1000);

    // Nettoyer les timers lors du d√©montage du composant
    return () => {
      clearTimeout(timer);
      clearInterval(countdownInterval);
    };
  }, [navigate, from]);

  return (
    <div className="flex items-center justify-center min-h-screen bg-gray-100">
      <div className="text-center px-4 py-10 bg-white rounded-lg shadow-md max-w-md">
        <img
          src={errorImage}
          alt="Unauthorized"
          className="w-64 mx-auto mb-6"
        />
        <h1 className="text-3xl font-bold text-red-600 mb-4">
          Acc√®s non autoris√©
        </h1>
        <p className="text-gray-600 mb-6">
          Vous n'avez pas les autorisations n√©cessaires pour acc√©der √† cette page.
          Veuillez vous connecter ou contacter l'administrateur.
        </p>
        <p className="text-blue-500 mb-4">
          Redirection automatique vers la page de connexion dans {countdown} secondes...
        </p>
        <div className="flex justify-center space-x-4">
          <Link
            to="/login"
            state={{ from }}
            className="px-4 py-2 bg-primary text-white rounded hover:bg-primary-dark transition"
          >
            Se connecter maintenant
          </Link>
          <Link
            to="/"
            className="px-4 py-2 bg-gray-200 text-gray-700 rounded hover:bg-gray-300 transition"
          >
            Retour √† l'accueil
          </Link>
        </div>
      </div>
    </div>
  );
};

export default UnauthorizedPage;
