import axios from 'axios';
import { Card, CardContent } from '@/components/ui/card';
import { Button } from '@/components/ui/button';
import { useNavigate } from 'react-router-dom';

/**
 * ðŸ‡¬ðŸ‡§ This component fetches and displays the list of user workouts using cards instead of a table.
 * ðŸ‡«ðŸ‡· Ce composant rÃ©cupÃ¨re et affiche la liste des sÃ©ances d'entraÃ®nement utilisateur sous forme de cartes au lieu d'un tableau.
 */
const UserWorkoutsList = () => {
  const [workouts, setWorkouts] = useState([]);
  const navigate = useNavigate();

  useEffect(() => {
    // ðŸ‡¬ðŸ‡§ Fetch the user's workouts from the API
    // ðŸ‡«ðŸ‡· RÃ©cupÃ©rer les sÃ©ances d'entraÃ®nement de l'utilisateur depuis l'API
