              {/* ðŸ‡¬ðŸ‡§ Workout title */}
              {/* ðŸ‡«ðŸ‡· Titre de la sÃ©ance */}
              <h2 className="text-xl font-semibold mb-2">{workout.title}</h2>

              {/* ðŸ‡¬ðŸ‡§ Workout description */}
              {/* ðŸ‡«ðŸ‡· Description de la sÃ©ance */}
              <p className="text-gray-600 mb-4">{workout.description}</p>
            </div>

            {/* ðŸ‡¬ðŸ‡§ Button to view workout details */}
            {/* ðŸ‡«ðŸ‡· Bouton pour voir les dÃ©tails de la sÃ©ance */}
            <Button onClick={() => navigate(`/user/workouts/${workout.id}`)}>
              Voir les dÃ©tails
            </Button>
          </CardContent>
        </Card>
      ))}
    </div>
  );
};

export default UserWorkoutsList;
