import React, { useState, useEffect } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import { 
  FaArrowLeft, 
  FaSave, 
  FaPlus, 
  FaTrash, 
  FaRegCheckSquare, 
  FaRegSquare,
  FaSwimmer,
  FaCalendarAlt,
  FaSearch,
  FaInfoCircle,
  FaSort,
  FaSortUp,
  FaSortDown,
  FaChevronLeft,
  FaChevronRight
} from 'react-icons/fa';
import { 
  getWorkout, 
  createWorkout, 
  updateWorkout, 
  getWorkoutExercises,
  addExerciseToWorkout
} from '../../../services/workouts';
import { 
  getSwimSetsForWorkout, 
  addSwimSetToWorkout, 
  removeSwimSetFromWorkout 
} from '../../../services/workoutSwimSets';
import { getUsers } from '../../../services/users';
import { getPlans, addWorkoutToPlan } from '../../../services/plans';
import { getSwimSets } from '../../../services/swimsets';
import { getExercise } from '../../../services/exercises';
import api from '../../../services/api';
import TextEditor from "../../../components/ui/forms/TextEditor";
import "quill/dist/quill.snow.css"; // Import du CSS pour TextEditor

/**
 * ðŸ‡¬ðŸ‡§ Component for creating and editing workouts
 * ðŸ‡«ðŸ‡· Composant pour crÃ©er et modifier des sÃ©ances d'entraÃ®nement
 */
const WorkoutFormPage = () => {
  const { id } = useParams();
  const navigate = useNavigate();
  const isEditMode = !!id;

  // Form state
  const [formData, setFormData] = useState({
    title: '',
    description: '',
    workout_category: '',
    user_id: ''
  });

  // Associated data
  const [allSets, setAllSets] = useState([]);
  const [selectedSets, setSelectedSets] = useState([]);
  const [allPlans, setAllPlans] = useState([]);
  const [selectedPlans, setSelectedPlans] = useState([]);
  const [currentWorkoutSets, setCurrentWorkoutSets] = useState([]);
  const [currentWorkoutPlans, setCurrentWorkoutPlans] = useState([]);

  // Search and filter state
  const [setsSearchTerm, setSetsSearchTerm] = useState('');
  const [plansSearchTerm, setPlansSearchTerm] = useState('');

  // Pagination state for sets
  const [setsCurrentPage, setSetsCurrentPage] = useState(1);
  const [setsPerPage, setSetsPerPage] = useState(5);

  // Pagination state for plans
  const [plansCurrentPage, setPlansCurrentPage] = useState(1);
  const [plansPerPage, setPlansPerPage] = useState(6);

  // Sorting state for sets
  const [setsSortConfig, setSetsSortConfig] = useState({
    key: 'id',
    direction: 'asc'
  });

  // Sorting state for plans
  const [plansSortConfig, setPlansSortConfig] = useState({
    key: 'id',
    direction: 'asc'
  });

  // Status states
  const [loading, setLoading] = useState(false);
  const [saving, setSaving] = useState(false);
  const [error, setError] = useState(null);
  const [users, setUsers] = useState([]);

  // Workout categories
  const workoutCategories = ['Aero 1', 'Vitesse', 'Mixte', 'Technique', 'RÃ©cupÃ©ration'];

  /**
   * ðŸ‡¬ðŸ‡§ Load workout data if in edit mode
   * ðŸ‡«ðŸ‡· Charger les donnÃ©es de la sÃ©ance en mode Ã©dition
   */
  useEffect(() => {
    const fetchWorkout = async () => {
      if (isEditMode) {
        try {
          setLoading(true);
          console.log(`Chargement de la sÃ©ance ${id}...`);

          // Get workout data
          const response = await getWorkout(id);
          const workoutData = response.data;

          setFormData({
            title: workoutData.title || '',
            description: workoutData.description || '',
            workout_category: workoutData.workout_category || '',
            user_id: workoutData.user_id ? workoutData.user_id.toString() : ''
          });

          // Fetch associated swim sets using the service
          try {
            console.log(`RÃ©cupÃ©ration des sÃ©ries pour la sÃ©ance ${id}...`);
            const setsResponse = await getSwimSetsForWorkout(id);
            const currentSets = setsResponse.data || [];
            console.log(`${currentSets.length} sÃ©ries rÃ©cupÃ©rÃ©es:`, currentSets);
            setCurrentWorkoutSets(currentSets);

            // Set the IDs of already associated sets
            setSelectedSets(currentSets.map(set => set.id));
          } catch (setsErr) {
            console.error(`Erreur lors de la rÃ©cupÃ©ration des sÃ©ries pour la sÃ©ance ${id}:`, setsErr);
            setError(`Erreur lors de la rÃ©cupÃ©ration des sÃ©ries: ${setsErr.message}`);
          }

          // Fetch associated plans
          try {
            const plansResponse = await api.get(`/api/workouts/${id}/plans`);
            setCurrentWorkoutPlans(plansResponse.data || []);
            setSelectedPlans(plansResponse.data.map(plan => plan.id) || []);
          } catch (plansErr) {
            console.error(`Erreur lors de la rÃ©cupÃ©ration des plans pour la sÃ©ance ${id}:`, plansErr);
          }

          setLoading(false);
        } catch (err) {
          console.error('Erreur lors du chargement de la sÃ©ance:', err);
          setError(`Erreur lors du chargement de la sÃ©ance: ${err.response?.data?.message || err.message}`);
          setLoading(false);
        }
      }
    };

    fetchWorkout();
  }, [id, isEditMode]);

  /**
   * ðŸ‡¬ðŸ‡§ Load users, plans, and sets on component mount
   * ðŸ‡«ðŸ‡· Charger les utilisateurs, plans et sÃ©ries au montage du composant
   */
  useEffect(() => {
    const fetchData = async () => {
      try {
        setLoading(true);
        console.log('Chargement des donnÃ©es associÃ©es (utilisateurs, sÃ©ries, plans)...');

        // Fetch users
        const usersResponse = await getUsers();
        setUsers(usersResponse.data);

        // Fetch all swim sets
        try {
          const setsResponse = await getSwimSets();
          const setsData = setsResponse.data || [];
          console.log(`${setsData.length} sÃ©ries rÃ©cupÃ©rÃ©es au total`);

          // Enrich swim sets with exercise data
          const enrichedSets = await Promise.all(setsData.map(async (set) => {
            if (set.exercise_id) {
              try {
                const exerciseResponse = await getExercise(set.exercise_id);
                return {
                  ...set,
                  exerciseTitle: exerciseResponse.data.title,
                  totalDistance: (set.set_distance || 0) * (set.set_repetition || 1)
                };
              } catch (err) {
                console.error(`Erreur lors de la rÃ©cupÃ©ration de l'exercice ${set.exercise_id}:`, err);
                return {
                  ...set,
                  exerciseTitle: `Exercise #${set.exercise_id}`,
                  totalDistance: (set.set_distance || 0) * (set.set_repetition || 1)
                };
              }
            }
            return {
              ...set,
              exerciseTitle: 'Unknown Exercise',
              totalDistance: (set.set_distance || 0) * (set.set_repetition || 1)
            };
          }));

          setAllSets(enrichedSets);
        } catch (setsErr) {
          console.error('Erreur lors de la rÃ©cupÃ©ration des sÃ©ries:', setsErr);
          setError(`Erreur lors de la rÃ©cupÃ©ration des sÃ©ries: ${setsErr.message}`);
        }

        // Fetch all plans
        try {
          const plansResponse = await getPlans();
          setAllPlans(plansResponse.data || []);
        } catch (plansErr) {
          console.error('Erreur lors de la rÃ©cupÃ©ration des plans:', plansErr);
        }

        setLoading(false);
      } catch (err) {
        console.error("Erreur lors du chargement des donnÃ©es:", err);
        setError("Erreur lors du chargement des donnÃ©es associÃ©es.");
        setLoading(false);
      }
    };

    fetchData();
  }, []);

  /**
   * ðŸ‡¬ðŸ‡§ Load saved state from localStorage when returning from creating a new set
   * ðŸ‡«ðŸ‡· Charger l'Ã©tat sauvegardÃ© depuis localStorage lors du retour de la crÃ©ation d'une nouvelle sÃ©rie
   */
  useEffect(() => {
    const savedState = localStorage.getItem('workout_form_state');
    if (savedState && !isEditMode) {
      try {
        const state = JSON.parse(savedState);
        setFormData({
          title: state.title || '',
          description: state.description || '',
          workout_category: state.workout_category || '',
          user_id: state.user_id || ''
        });
        if (state.selectedSets) {
          setSelectedSets(state.selectedSets);
        }
        // Supprimer l'Ã©tat sauvegardÃ© aprÃ¨s l'avoir chargÃ©
        localStorage.removeItem('workout_form_state');
      } catch (err) {
        console.error('Erreur lors du chargement de l\'Ã©tat sauvegardÃ©:', err);
      }
    }
  }, [isEditMode]);

  /**
   * ðŸ‡¬ðŸ‡§ Handle form input changes
   * ðŸ‡«ðŸ‡· GÃ©rer les changements dans les champs du formulaire
   */
  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData(prev => ({
      ...prev,
      [name]: value
    }));
  };

  /**
   * ðŸ‡¬ðŸ‡§ Handle description text editor changes
   * ðŸ‡«ðŸ‡· GÃ©rer les changements dans l'Ã©diteur de texte pour la description
   */
  const handleDescriptionChange = (value) => {
    setFormData(prev => ({
      ...prev,
      description: value
    }));
  };

  /**
   * ðŸ‡¬ðŸ‡§ Handle category selection
   * ðŸ‡«ðŸ‡· GÃ©rer la sÃ©lection de catÃ©gorie
   */
  const handleCategoryChange = (category) => {
    setFormData(prev => ({
      ...prev,
      workout_category: prev.workout_category === category ? '' : category
    }));
  };

  /**
   * ðŸ‡¬ðŸ‡§ Handle set selection toggle
   * ðŸ‡«ðŸ‡· GÃ©rer la sÃ©lection/dÃ©sÃ©lection d'une sÃ©rie
   */
  const handleSetToggle = (setId) => {
    setSelectedSets(prev => {
      if (prev.includes(setId)) {
        return prev.filter(id => id !== setId);
      } else {
        return [...prev, setId];
      }
    });
  };

  /**
   * ðŸ‡¬ðŸ‡§ Handle plan selection toggle
   * ðŸ‡«ðŸ‡· GÃ©rer la sÃ©lection/dÃ©sÃ©lection d'un plan
   */
  const handlePlanToggle = (planId) => {
    setSelectedPlans(prev => {
      if (prev.includes(planId)) {
        return prev.filter(id => id !== planId);
      } else {
        return [...prev, planId];
      }
    });
  };

  /**
   * ðŸ‡¬ðŸ‡§ Format time in seconds to MM:SS
   * ðŸ‡«ðŸ‡· Formater le temps en secondes au format MM:SS
   */
  const formatRestTime = (seconds) => {
    if (!seconds) return '00:00';
    const mins = Math.floor(seconds / 60);
    const secs = seconds % 60;
    return `${mins.toString().padStart(2, '0')}:${secs.toString().padStart(2, '0')}`;
  };

  /**
   * ðŸ‡¬ðŸ‡§ Calculate total workout distance based on selected sets
   * ðŸ‡«ðŸ‡· Calculer la distance totale de la sÃ©ance en fonction des sÃ©ries sÃ©lectionnÃ©es
   */
  const calculateTotalDistance = () => {
