import React, { useState, useEffect } from 'react';
import Header from '../../components/template/Header';
import Footer from '../../components/template/Footer';
import { FaStopwatch, FaInfoCircle } from 'react-icons/fa';

/**
 * üá¨üáß Helper to format seconds into MM:SS.CS
 * üá´üá∑ Formateur de temps brut (secondes) en MM:SS.CS
 */
const formatTime = (timeInSeconds) => {
  const minutes = Math.floor(timeInSeconds / 60);
  const seconds = Math.floor(timeInSeconds % 60);
  const centiseconds = Math.round((timeInSeconds % 1) * 100);
  return `${minutes}:${seconds.toString().padStart(2, '0')}.${centiseconds.toString().padStart(2, '0')}`;
};

const addFinalPercentage = (time) => time * 1.049;

const calculatePredictedTimes = (distance, time50) => {
  const times = [];
  const time100 = addFinalPercentage((time50 + 1.5) + (time50 + 2.49));

  if (distance === 100) {
    times.push({ label: '1er 50m', time: time50 + 1.5 });
    times.push({ label: '2√®me 50m', time: addFinalPercentage(time50 + 2.49) });
  } else if (distance === 200) {
    const additions = [2.5, 4.13, 4.53, 2.57];
    additions.forEach((a, i) => {
      const t = i === 3 ? addFinalPercentage(time50 + a) : time50 + a;
      times.push({ label: `${i + 1}·µâ 50m`, time: t });
    });
  } else if (distance === 400) {
    const additions = [4.5, 6.13, 6, 2.59];
    additions.forEach((a, i) => {
      const t = i === 3 ? addFinalPercentage(time100 + a) : time100 + a;
      times.push({ label: `${i + 1}·µâ 100m`, time: t });
    });
  }
  return times.map(t => ({ ...t, timeFormatted: formatTime(t.time) }));
};

const SwimmingPerformancePredictor = () => {
  const [best50m, setBest50m] = useState('');
  const [distance, setDistance] = useState(100);
  const [predictions, setPredictions] = useState([]);
  const [totalTime, setTotalTime] = useState('');
  const [error, setError] = useState('');

  const handleCalculate = () => {
    const time50 = parseFloat(best50m);
    if (isNaN(time50) || time50 <= 0) {
      setError('Veuillez entrer un temps valide pour le 50m.');
      return;
    }

    const calculated = calculatePredictedTimes(distance, time50);
    const total = calculated.reduce((acc, val) => acc + val.time, 0);

    setPredictions(calculated);
    setTotalTime(formatTime(total));
    setError('');
  };

  return (
    <>
      <Header />
      <main>
        <section className="py-5 bg-light">
          <div className="container">
            <div className="d-flex align-items-center mb-4">
              <FaStopwatch className="me-3 text-primary fs-2" />
              <h1 className="h3 fw-bold mb-0">Pr√©dicteur de Performance Natation</h1>
            </div>

            <div className="alert alert-info d-flex align-items-start gap-2">
              <FaInfoCircle className="mt-1" />
              <div>
                <p className="mb-1">
                  Cet outil permet d'estimer vos temps interm√©diaires sur diff√©rentes distances (100, 200, 400m) √† partir de votre meilleur temps sur 50m.
                </p>
                <p className="mb-0">
                  Les calculs sont bas√©s sur des formules de projection standards utilis√©es par les entra√Æneurs.
                </p>
              </div>
            </div>

            <div className="row g-3 mb-4">
              <div className="col-md-6">
                <label className="form-label">Temps 50m (en secondes)</label>
                <input
                  type="number"
                  value={best50m}
                  onChange={(e) => setBest50m(e.target.value)}
                  className="form-control"
                  placeholder="ex : 32.75"
                  step="0.01"
                  min="0"
                />
              </div>
              <div className="col-md-6">
                <label className="form-label">Distance √† pr√©dire</label>
                <select
                  className="form-select"
                  value={distance}
                  onChange={(e) => setDistance(parseInt(e.target.value))}
                >
                  {[100, 200, 400].map(d => (
                    <option key={d} value={d}>{d}m</option>
                  ))}
                </select>
              </div>
            </div>

            <div className="mb-4">
              <button onClick={handleCalculate} className="btn btn-primary w-100">
                Calculer mes temps pr√©visionnels
              </button>
            </div>

            {error && (
              <div className="alert alert-danger">{error}</div>
            )}

            {totalTime && (
              <div className="alert alert-success">
                Temps total estim√© : <strong>{totalTime}</strong>
              </div>
            )}

            {predictions.length > 0 && (
              <div className="card shadow-sm">
                <div className="card-body">
                  <h5 className="card-title mb-3">D√©tails des temps interm√©diaires</h5>
                  <div className="table-responsive">
                    <table className="table table-bordered align-middle text-center">
                      <thead className="table-light">
                        <tr>
                          <th>Fraction</th>
                          <th>Temps (format√©)</th>
                          <th>Temps brut (sec)</th>
                        </tr>
                      </thead>
                      <tbody>
                        {predictions.map((p, index) => (
                          <tr key={index}>
                            <td>{p.label}</td>
                            <td><strong>{p.timeFormatted}</strong></td>
                            <td>{p.time.toFixed(2)}</td>
                          </tr>
                        ))}
                      </tbody>
                    </table>
                  </div>
                </div>
              </div>
            )}
          </div>
        </section>
      </main>
      <Footer />
    </>
  );
};

export default SwimmingPerformancePredictor;
