import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import { FaPlus, FaEye, FaEdit, FaTrash, FaSwimmer } from 'react-icons/fa';
import api from '../../../services/api';

/**
 * üá¨üáß Admin Swim Sets Page - Displays and manages all swimming series with pagination
 * üá´üá∑ Page d'administration des s√©ries de natation - Affiche et g√®re toutes les s√©ries avec pagination
 */
const AdminSwimSetsPage = () => {
  const navigate = useNavigate();
  const [swimSets, setSwimSets] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  // Pagination state
  const [currentPage, setCurrentPage] = useState(1);
  const [totalPages, setTotalPages] = useState(1);
  const [perPage] = useState(10);
  const [totalItems, setTotalItems] = useState(0);

  /**
   * üá¨üáß Fetches swim sets with pagination from the API
   * üá´üá∑ R√©cup√®re les s√©ries de natation avec pagination depuis l'API
   */
  const fetchSwimSets = async (page = 1) => {
    try {
      setLoading(true);
      console.log(`R√©cup√©ration des s√©ries de natation (page ${page})...`);

      const response = await api.get(`/swim-sets?page=${page}&per_page=${perPage}`);

      console.log('R√©ponse:', response.data);

      // Si la r√©ponse est pagin√©e (format Laravel)
      if (response.data.data && response.data.meta) {
        setSwimSets(response.data.data);
        setTotalPages(response.data.meta.last_page);
        setTotalItems(response.data.meta.total);
        setCurrentPage(response.data.meta.current_page);
      } else {
        // Si la r√©ponse n'est pas pagin√©e
        setSwimSets(response.data);
        setTotalItems(response.data.length);
      }

      setError(null);
    } catch (err) {
      console.error('Erreur lors de la r√©cup√©ration des s√©ries:', err);
      setError('Impossible de charger les s√©ries. Veuillez r√©essayer.');
    } finally {
      setLoading(false);
    }
  };

  /**
   * üá¨üáß Handles page change in pagination
   * üá´üá∑ G√®re le changement de page dans la pagination
   */
  const handlePageChange = (newPage) => {
    setCurrentPage(newPage);
    fetchSwimSets(newPage);
  };

  /**
   * üá¨üáß Handles swim set deletion
   * üá´üá∑ G√®re la suppression d'une s√©rie de natation
   */
  const handleDelete = async (id) => {
    if (window.confirm('√ätes-vous s√ªr de vouloir supprimer cette s√©rie ?')) {
      try {
        await api.delete(`/swim-sets/${id}`);
        alert('S√©rie supprim√©e avec succ√®s');
        fetchSwimSets(currentPage);
      } catch (err) {
        console.error('Erreur lors de la suppression:', err);
        setError('Erreur lors de la suppression: ' + err.message);
        alert('Erreur lors de la suppression de la s√©rie');
      }
    }
  };

  useEffect(() => {
    fetchSwimSets();
    // eslint-disable-next-line react-hooks/exhaustive-deps
  }, []);

  /**
   * üá¨üáß Get the exercise name or placeholder
   * üá´üá∑ R√©cup√®re le nom de l'exercice ou un placeholder
   */
  const getExerciseName = (set) => {
    return set.exercise ? set.exercise.name : `Exercice #${set.exercise_id}`;
  };

  /**
   * üá¨üáß Simple pagination component
   * üá´üá∑ Composant de pagination simple
   */
  const SimplePagination = () => {
    const pages = [];
    const maxVisiblePages = 5;

    let startPage = Math.max(1, currentPage - Math.floor(maxVisiblePages / 2));
    let endPage = Math.min(totalPages, startPage + maxVisiblePages - 1);

    if (endPage - startPage + 1 < maxVisiblePages) {
      startPage = Math.max(1, endPage - maxVisiblePages + 1);
    }

    for (let i = startPage; i <= endPage; i++) {
      pages.push(
        <li key={i} className={`page-item ${currentPage === i ? 'active' : ''}`}>
          <button 
            className="page-link" 
            onClick={() => handlePageChange(i)}
          >
            {i}
          </button>
        </li>
      );
    }

    return (
      <nav aria-label="Navigation des pages" className="mt-4">
        <ul className="pagination justify-content-center">
          <li className={`page-item ${currentPage === 1 ? 'disabled' : ''}`}>
            <button
              className="page-link"
              onClick={() => handlePageChange(1)}
              disabled={currentPage === 1}
            >
              &laquo;
            </button>
          </li>
          <li className={`page-item ${currentPage === 1 ? 'disabled' : ''}`}>
            <button
              className="page-link"
              onClick={() => handlePageChange(currentPage - 1)}
              disabled={currentPage === 1}
            >
              &lt;
            </button>
          </li>

          {pages}

          <li className={`page-item ${currentPage === totalPages ? 'disabled' : ''}`}>
            <button
              className="page-link"
              onClick={() => handlePageChange(currentPage + 1)}
              disabled={currentPage === totalPages}
            >
              &gt;
            </button>
          </li>
          <li className={`page-item ${currentPage === totalPages ? 'disabled' : ''}`}>
            <button
              className="page-link"
              onClick={() => handlePageChange(totalPages)}
              disabled={currentPage === totalPages}
