/**
 * ðŸ‡¬ðŸ‡§ FitnessCalculator component for calculating various fitness metrics
 * ðŸ‡«ðŸ‡· Composant FitnessCalculator pour calculer diffÃ©rentes mÃ©triques de fitness
 */

import React, { useState } from 'react';

const FitnessCalculator = () => {
  /**
   * ðŸ‡¬ðŸ‡§ Form state for user input values
   * ðŸ‡«ðŸ‡· Ã‰tat du formulaire pour les valeurs saisies par l'utilisateur
   */
  const [formValues, setFormValues] = useState({
    age: '',
    restingHeartRate: '',
    intensity: '',
    weight: '',
    height: '',
    formula: 'asstrand'
  });

  /**
   * ðŸ‡¬ðŸ‡§ Results of calculations: HRR, Target HR, and BMI
   * ðŸ‡«ðŸ‡· RÃ©sultats des calculs : FCr, FC cible et IMC
   */
  const [results, setResults] = useState({
    hrr: 0,
    targetHr: 0,
    bmi: 0
  });

  /**
   * ðŸ‡¬ðŸ‡§ Handles changes in input fields
   * ðŸ‡«ðŸ‡· GÃ¨re les changements dans les champs de saisie
   */
  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormValues(prev => ({ ...prev, [name]: value }));
  };

  /**
   * ðŸ‡¬ðŸ‡§ Resets form and results
   * ðŸ‡«ðŸ‡· RÃ©initialise le formulaire et les rÃ©sultats
   */
  const handleReset = () => {
    setFormValues({
      age: '',
      restingHeartRate: '',
      intensity: '',
      weight: '',
      height: '',
      formula: 'asstrand'
    });
    setResults({ hrr: 0, targetHr: 0, bmi: 0 });
  };

  /**
   * ðŸ‡¬ðŸ‡§ Calculates maximum heart rate based on selected formula
   * ðŸ‡«ðŸ‡· Calcule la frÃ©quence cardiaque maximale selon la formule choisie
   */
  const calculateMaxHr = (age) => {
    return formValues.formula === 'asstrand' 
      ? 220 - age 
      : 208 - (0.7 * age);
  };

  /**
   * ðŸ‡¬ðŸ‡§ Calculates heart rate reserve (HRR)
   * ðŸ‡«ðŸ‡· Calcule la frÃ©quence cardiaque de rÃ©serve (FCr)
   */
  const calculateHrr = () => {
    const age = parseFloat(formValues.age);
    const restingHr = parseFloat(formValues.restingHeartRate);
    if (isNaN(age) || isNaN(restingHr)) return;

    const maxHr = calculateMaxHr(age);
    const hrr = maxHr - restingHr;
    setResults(prev => ({ ...prev, hrr }));
  };

  /**
   * ðŸ‡¬ðŸ‡§ Calculates target heart rate based on HRR and intensity
   * ðŸ‡«ðŸ‡· Calcule la FC cible en fonction de l'intensitÃ© et de la FCr
   */
  const calculateTargetHr = () => {
    const intensity = parseFloat(formValues.intensity) / 100;
    const restingHr = parseFloat(formValues.restingHeartRate);
    if (isNaN(intensity) || isNaN(restingHr) || results.hrr === 0) return;

    const targetHr = (results.hrr * intensity) + restingHr;
    setResults(prev => ({ ...prev, targetHr }));
  };

  /**
   * ðŸ‡¬ðŸ‡§ Calculates BMI using weight and height
   * ðŸ‡«ðŸ‡· Calcule l'IMC Ã  partir du poids et de la taille
   */
  const calculateBmi = () => {
    const weight = parseFloat(formValues.weight);
    const heightCm = parseFloat(formValues.height);
    if (isNaN(weight) || isNaN(heightCm) || heightCm === 0) return;

    const heightMeters = heightCm / 100;
    const bmi = weight / (heightMeters * heightMeters);
    setResults(prev => ({ ...prev, bmi }));
  };

  return (
    <div className="max-w-2xl mx-auto p-6 bg-white rounded-lg shadow-lg">
      <h1 className="text-3xl font-bold text-center mb-6">Calculateur Fitness</h1>

      {/* ðŸ§¾ Formulaire utilisateur */}
      <div className="space-y-4">
        <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
          {[
            { name: 'age', label: 'Ã‚ge (annÃ©es) / Age (years)' },
            { name: 'restingHeartRate', label: 'FC repos (bpm) / Resting HR (bpm)' },
            { name: 'intensity', label: 'IntensitÃ© (%) / Intensity (%)' },
            { name: 'weight', label: 'Poids (kg) / Weight (kg)' },
            { name: 'height', label: 'Taille (cm) / Height (cm)' },
          ].map(({ name, label }) => (
            <div key={name}>
              <label className="block text-sm font-medium text-gray-700">{label}</label>
              <input
                type="number"
                name={name}
                value={formValues[name]}
                onChange={handleInputChange}
                className="mt-1 block w-full rounded-md border-gray-300 shadow-sm focus:border-blue-500 focus:ring-blue-500"
              />
            </div>
          ))}

          <div>
            <label className="block text-sm font-medium text-gray-700">
              Formule FCmax / Max HR Formula
            </label>
            <select
              name="formula"
              value={formValues.formula}
              onChange={handleInputChange}
              className="mt-1 block w-full rounded-md border-gray-300 shadow-sm focus:border-blue-500 focus:ring-blue-500"
            >
              <option value="asstrand">Asstrand (220 - Ã¢ge)</option>
              <option value="tanaka">Tanaka (208 - 0.7 Ã— Ã¢ge)</option>
            </select>
          </div>
        </div>

        {/* ðŸ§® Boutons de calcul */}
        <div className="grid grid-cols-1 md:grid-cols-4 gap-4 mt-6">
          <button
            onClick={calculateHrr}
            className="bg-blue-500 text-white px-4 py-2 rounded-md hover:bg-blue-600 transition-colors"
          >
            Calculer FCr / Calculate HRR
          </button>

          <button
            onClick={calculateTargetHr}
            className="bg-green-500 text-white px-4 py-2 rounded-md hover:bg-green-600 transition-colors"
          >
            Calculer FC cible / Target HR
          </button>

          <button
            onClick={calculateBmi}
            className="bg-purple-500 text-white px-4 py-2 rounded-md hover:bg-purple-600 transition-colors"
          >
            Calculer IMC / Calculate BMI
          </button>

          <button
            onClick={handleReset}
            className="bg-red-500 text-white px-4 py-2 rounded-md hover:bg-red-600 transition-colors"
          >
            Mettre Ã  zÃ©ro / Reset
          </button>
        </div>

        {/* ðŸ“Š Affichage des rÃ©sultats */}
        <div className="mt-8 space-y-4">
          <div className="p-4 bg-gray-50 rounded-md">
            <h2 className="font-semibold">FCr / HRR:</h2>
            <p>{results.hrr.toFixed(2)} bpm</p>
          </div>
          <div className="p-4 bg-gray-50 rounded-md">
            <h2 className="font-semibold">FC cible / Target HR:</h2>
            <p>{results.targetHr.toFixed(2)} bpm</p>
          </div>
          <div className="p-4 bg-gray-50 rounded-md">
            <h2 className="font-semibold">IMC / BMI:</h2>
            <p>{results.bmi.toFixed(2)} kg/mÂ²</p>
          </div>
        </div>
      </div>
    </div>
  );
};

export default FitnessCalculator;
