import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import { 
  FaPlus, 
  FaEdit, 
  FaTrash, 
  FaEye, 
  FaFilter, 
  FaSearch, 
  FaSync,
  FaSwimmer,
  FaSort,
  FaSortAmountDown,
  FaSortAmountUp,
  FaLayerGroup,
  FaRuler,
  FaHourglass
} from 'react-icons/fa';
import axios from 'axios';

/**
 * ðŸ‡¬ðŸ‡§ Admin page for swim sets management
 * ðŸ‡«ðŸ‡· Page d'administration pour la gestion des sÃ©ries de natation
 */
const AdminSwimSetsPage = () => {
  const navigate = useNavigate();
  const [swimSets, setSwimSets] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [searchTerm, setSearchTerm] = useState('');
  const [exerciseFilter, setExerciseFilter] = useState('all');
  const [exerciseOptions, setExerciseOptions] = useState([]);
  const [currentPage, setCurrentPage] = useState(0);
  const [itemsPerPage, setItemsPerPage] = useState(10);
  const [sortConfig, setSortConfig] = useState({
    key: 'id',
    direction: 'ascending'
  });

  /**
   * ðŸ‡¬ðŸ‡§ Fetches swim sets data from the API
   * ðŸ‡«ðŸ‡· RÃ©cupÃ¨re les donnÃ©es des sÃ©ries de natation depuis l'API
   */
  const fetchSwimSets = async () => {
    try {
      setLoading(true);
      console.log('RÃ©cupÃ©ration des sÃ©ries de natation...');

      // RÃ©cupÃ©rer le token JWT stockÃ©
      const token = localStorage.getItem('token') || sessionStorage.getItem('token');

      const response = await axios.get('http://127.0.0.1:8000/api/swim-sets', {
        headers: {
          'Content-Type': 'application/json',
          'Accept': 'application/json',
          'Authorization': token ? `Bearer ${token}` : ''
        },
        withCredentials: true
      });

      console.log('RÃ©ponse:', response.data);

      // Handle paginated or non-paginated response
      const data = response.data.data || response.data;
      setSwimSets(data);

      // Extract unique exercise IDs for filtering
      const exerciseIds = [...new Set(data.map(set => set.exercise_id))];
      setExerciseOptions(exerciseIds);

      setError(null);
    } catch (err) {
      console.error('Erreur lors de la rÃ©cupÃ©ration des sÃ©ries:', err);
      setError('Impossible de charger les sÃ©ries. Veuillez rÃ©essayer.');
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    fetchSwimSets();
  }, []);

  /**
   * ðŸ‡¬ðŸ‡§ Handles swim set deletion
   * ðŸ‡«ðŸ‡· GÃ¨re la suppression d'une sÃ©rie de natation
   */
  const handleDelete = async (id, exerciseId) => {
    if (window.confirm(`ÃŠtes-vous sÃ»r de vouloir supprimer cette sÃ©rie pour l'exercice #${exerciseId} ?`)) {
      try {
        // RÃ©cupÃ©rer le token JWT stockÃ©
        const token = localStorage.getItem('token') || sessionStorage.getItem('token');

        await axios.delete(`http://127.0.0.1:8000/api/swim-sets/${id}`, {
          headers: {
            'Content-Type': 'application/json',
            'Accept': 'application/json',
            'Authorization': token ? `Bearer ${token}` : ''
          },
          withCredentials: true
        });

        // Afficher une alerte de succÃ¨s
        alert('SÃ©rie supprimÃ©e avec succÃ¨s');
        fetchSwimSets();
      } catch (err) {
        console.error('Erreur lors de la suppression:', err);
        setError('Erreur lors de la suppression: ' + err.message);
      }
    }
  };

  /**
   * ðŸ‡¬ðŸ‡§ Sorts swim sets by the given key
   * ðŸ‡«ðŸ‡· Trie les sÃ©ries de natation par la clÃ© donnÃ©e
   */
  const requestSort = (key) => {
    let direction = 'ascending';
    if (sortConfig.key === key && sortConfig.direction === 'ascending') {
      direction = 'descending';
    }
    setSortConfig({ key, direction });
  };

  /**
   * ðŸ‡¬ðŸ‡§ Returns appropriate sort icon based on current sort configuration
   * ðŸ‡«ðŸ‡· Renvoie l'icÃ´ne de tri appropriÃ©e en fonction de la configuration de tri actuelle
   */
  const getSortIcon = (key) => {
    if (sortConfig.key === key) {
      return sortConfig.direction === 'ascending' ? 
        <FaSortAmountUp className="ms-1" /> : 
        <FaSortAmountDown className="ms-1" />;
    }
    return <FaSort className="ms-1 text-muted" />;
  };

  /**
   * ðŸ‡¬ðŸ‡§ Filters and sorts swim sets based on current configuration
   * ðŸ‡«ðŸ‡· Filtre et trie les sÃ©ries de natation en fonction de la configuration actuelle
   */
  const getSortedSwimSets = () => {
    // First filter swim sets
    let filteredItems = swimSets.filter(set => {
      const matchesSearch = 
        set.id.toString().includes(searchTerm) || 
        (set.exercise_id && set.exercise_id.toString().includes(searchTerm));

      const matchesExercise = exerciseFilter === 'all' || set.exercise_id.toString() === exerciseFilter;

      return matchesSearch && matchesExercise;
    });

    // Then sort them
    if (sortConfig.key) {
      filteredItems.sort((a, b) => {
        // For numeric values
        if (typeof a[sortConfig.key] === 'number' || !isNaN(parseInt(a[sortConfig.key]))) {
          const aValue = a[sortConfig.key] || 0;
          const bValue = b[sortConfig.key] || 0;

          if (aValue < bValue) {
            return sortConfig.direction === 'ascending' ? -1 : 1;
          }
          if (aValue > bValue) {
            return sortConfig.direction === 'ascending' ? 1 : -1;
          }
          return 0;
        }

        // For string values
        if (a[sortConfig.key] < b[sortConfig.key]) {
          return sortConfig.direction === 'ascending' ? -1 : 1;
        }
        if (a[sortConfig.key] > b[sortConfig.key]) {
          return sortConfig.direction === 'ascending' ? 1 : -1;
        }
        return 0;
      });
    }

    return filteredItems;
  };

  // Get current items for pagination
  const sortedFilteredSwimSets = getSortedSwimSets();
  const pageCount = Math.ceil(sortedFilteredSwimSets.length / itemsPerPage);
  const offset = currentPage * itemsPerPage;
  const currentSwimSets = sortedFilteredSwimSets.slice(offset, offset + itemsPerPage);

  if (loading && swimSets.length === 0) {
    return (
      <div className="container py-4">
        <div className="text-center">
          <div className="spinner-border text-primary" role="status">
            <span className="visually-hidden">Chargement...</span>
          </div>
          <p className="mt-3">Chargement des sÃ©ries de natation...</p>
        </div>
      </div>
    );
  }

  return (
    <div className="container-fluid py-4">
      {/* Titre Section */}
      <div className="row mb-4">
        <div className="col-12">
          <div className="card border-0 shadow-sm">
            <div className="card-header bg-primary-subtle">
              <h2 className="card-title mb-0">
                <FaSwimmer className="me-2" />
                Gestion des SÃ©ries
              </h2>
            </div>
          </div>
        </div>
      </div>

      <div className="d-flex justify-content-between align-items-center mb-4">
        <div>
          <p className="text-muted mb-0">
            {swimSets.length} sÃ©rie{swimSets.length > 1 ? 's' : ''} au total
            {sortedFilteredSwimSets.length !== swimSets.length && 
             ` (${sortedFilteredSwimSets.length} filtrÃ©e${sortedFilteredSwimSets.length > 1 ? 's' : ''})`}
          </p>
        </div>
        <button 
          className="btn btn-success btn-lg d-flex align-items-center"
          onClick={() => navigate('/admin/swim-sets/new')}
        >
          <FaPlus className="me-2" /> Ajouter Une SÃ©rie
        </button>
      </div>

      {/* Filters */}
      <div className="card shadow-sm mb-4">
        <div className="card-body">
          <div className="row g-3">
            <div className="col-md-6">
              <div className="input-group">
                <span className="input-group-text bg-light">
                  <FaSearch />
                </span>
                <input
                  type="text"
                  className="form-control"
                  placeholder="Rechercher par ID ou exercice..."
                  value={searchTerm}
                  onChange={(e) => {
                    setSearchTerm(e.target.value);
                    setCurrentPage(0); // RÃ©initialiser Ã  la premiÃ¨re page lors de la recherche
                  }}
                />
              </div>
            </div>
            <div className="col-md-6">
              <div className="d-flex gap-2">
                <div className="input-group">
                  <span className="input-group-text bg-light">
                    <FaFilter />
                  </span>
                  <select
                    className="form-select"
                    value={exerciseFilter}
                    onChange={(e) => {
                      setExerciseFilter(e.target.value);
                      setCurrentPage(0); // RÃ©initialiser Ã  la premiÃ¨re page lors du changement de filtre
                    }}
                  >
                    <option value="all">Tous les exercices</option>
                    {exerciseOptions.map(exerciseId => (
                      <option key={exerciseId} value={exerciseId.toString()}>Exercice #{exerciseId}</option>
                    ))}
                  </select>
                </div>
                <button 
                  className="btn btn-outline-secondary" 
                  onClick={fetchSwimSets}
                  title="RafraÃ®chir"
                >
                  <FaSync />
                </button>
              </div>
            </div>
          </div>
        </div>
      </div>

      {error && (
        <div className="alert alert-danger" role="alert">
          {error}
        </div>
      )}

      {/* Table View */}
      <div className="card shadow-sm mb-4">
        <div className="card-body p-0">
          <div className="table-responsive">
            <table className="table table-hover align-middle mb-0">
              <thead className="table-light">
                <tr>
                  <th 
                    className="cursor-pointer"
                    style={{cursor: 'pointer'}}
                    onClick={() => requestSort('id')}
                  >
                    ID {getSortIcon('id')}
                  </th>
                  <th 
                    className="cursor-pointer"
                    style={{cursor: 'pointer'}}
                    onClick={() => requestSort('exercise_id')}
                  >
                    Exercice {getSortIcon('exercise_id')}
                  </th>
                  <th 
                    className="cursor-pointer"
                    style={{cursor: 'pointer'}}
                    onClick={() => requestSort('set_distance')}
                  >
                    <FaRuler className="me-1" />
                    Distance (m) {getSortIcon('set_distance')}
                  </th>
                  <th 
                    className="cursor-pointer"
                    style={{cursor: 'pointer'}}
                    onClick={() => requestSort('set_repetition')}
                  >
                    <FaLayerGroup className="me-1" />
                    RÃ©pÃ©titions {getSortIcon('set_repetition')}
                  </th>
                  <th 
                    className="cursor-pointer"
                    style={{cursor: 'pointer'}}
                    onClick={() => requestSort('rest_time')}
                  >
                    <FaHourglass className="me-1" />
                    Repos (s) {getSortIcon('rest_time')}
                  </th>
                  <th className="text-center">Actions</th>
                </tr>
              </thead>
              <tbody>
                {currentSwimSets.length === 0 ? (
                  <tr>
                    <td colSpan="6" className="text-center py-4">
                      Aucune sÃ©rie ne correspond Ã  vos critÃ¨res de recherche.
                    </td>
                  </tr>
                ) : (
                  currentSwimSets.map((set) => (
                    <tr key={set.id}>
                      <td className="fw-medium">{set.id}</td>
                      <td>
                        <span className="badge bg-info text-dark">
                          Exercice #{set.exercise_id}
                        </span>
                      </td>
                      <td>{set.set_distance}m</td>
                      <td>
                        <span className="badge bg-secondary">
                          <FaLayerGroup className="me-1" /> {set.set_repetition || 1}
                        </span>
                      </td>
                      <td>
                        <span className="badge bg-warning text-dark">
                          <FaHourglass className="me-1" /> {set.rest_time || 0}s
                        </span>
                      </td>
                      <td>
                        <div className="d-flex justify-content-center gap-1">
                          <button 
                            className="btn btn-sm btn-outline-primary"
                            onClick={() => navigate(`/admin/swim-sets/${set.id}`)}
                            title="Voir dÃ©tails"
                          >
                            <FaEye />
                          </button>
                          <button 
                            className="btn btn-sm btn-outline-secondary"
                            onClick={() => navigate(`/admin/swim-sets/${set.id}/edit`)}
                            title="Modifier"
                          >
                            <FaEdit />
                          </button>
                          <button 
                            className="btn btn-sm btn-outline-danger"
                            onClick={() => handleDelete(set.id, set.exercise_id)}
                            title="Supprimer"
                          >
                            <FaTrash />
                          </button>
                        </div>
                      </td>
                    </tr>
                  ))
                )}
              </tbody>
            </table>
          </div>
        </div>
      </div>

      {/* Pagination */}
      {sortedFilteredSwimSets.length > itemsPerPage && (
        <div className="card">
          <div className="card-body d-flex justify-content-between align-items-center">
            <div className="d-flex align-items-center">
              <span className="me-2">Afficher</span>
              <select 
                className="form-select form-select-sm" 
                value={itemsPerPage}
                onChange={(e) => {
                  setItemsPerPage(Number(e.target.value));
                  setCurrentPage(0);
                }}
                style={{width: '70px'}}
              >
                <option value="5">5</option>
                <option value="10">10</option>
                <option value="25">25</option>
                <option value="50">50</option>
              </select>
              <span className="ms-2">Ã©lÃ©ments</span>
            </div>

            <nav aria-label="Navigation des pages">
              <ul className="pagination pagination-sm mb-0">
                <li className={`page-item ${currentPage === 0 ? 'disabled' : ''}`}>
                  <button 
                    type="button"
                    className="page-link" 
                    onClick={() => setCurrentPage(currentPage - 1)}
                    disabled={currentPage === 0}
                  >
                    &laquo;
                  </button>
                </li>

                {[...Array(pageCount)].map((_, index) => (
                  <li key={index} className={`page-item ${currentPage === index ? 'active' : ''}`}>
                    <button
                      type="button"
                      className="page-link"
                      onClick={() => setCurrentPage(index)}
                    >
                      {index + 1}
                    </button>
                  </li>
                ))}

                <li className={`page-item ${currentPage === pageCount - 1 ? 'disabled' : ''}`}>
                  <button 
                    type="button"
                    className="page-link" 
                    onClick={() => setCurrentPage(currentPage + 1)}
                    disabled={currentPage === pageCount - 1}
                  >
                    &raquo;
                  </button>
                </li>
              </ul>
            </nav>
          </div>
        </div>
      )}
    </div>
  );
};

export default AdminSwimSetsPage;
