import React, { useState, useEffect } from 'react';
import { useParams, useNavigate, Link } from 'react-router-dom';
import { 
  FaSave, 
  FaArrowLeft, 
  FaExclamationTriangle, 
  FaInfoCircle, 
  FaSwimmingPool, 
  FaTags, 
  FaPalette,
  FaWater,
  FaTint,
  FaBook,
  FaPencilAlt,
  FaLayerGroup,
  FaLightbulb,
  FaTools,
  FaPlus,
  FaSwimmer
} from 'react-icons/fa';
import Footer from '../../../components/template/Footer';
import { createMylist, getMylist, updateMylist } from '../../../services/mylists';
import useAuth from '../../../hooks/useAuth';

/**
 * 🇬🇧 User's list creation and edit form page
 * This component allows users to create new lists or edit existing ones
 * with title, description, color, category and visibility options.
 * 
 * 🇫🇷 Page de formulaire de création et modification de liste utilisateur
 * Ce composant permet aux utilisateurs de créer de nouvelles listes ou de modifier des listes existantes
 * avec titre, description, couleur, catégorie et options de visibilité.
 */
const UserMyListFormPage = () => {
  const { id } = useParams();
  const navigate = useNavigate();
  const { user } = useAuth();

  /**
   * 🇬🇧 State management for the component
   * 
   * 🇫🇷 Gestion des états du composant
   */
  const [loading, setLoading] = useState(id ? true : false);
  const [submitting, setSubmitting] = useState(false);
  const [error, setError] = useState(null);
  const [successMessage, setSuccessMessage] = useState(null);

  /**
   * 🇬🇧 Form data state
   * 
   * 🇫🇷 État des données du formulaire
   */
  const [formData, setFormData] = useState({
    title: '',
    description: '',
    // Note: ces champs sont pour l'UI uniquement, ils ne seront pas envoyés à l'API
    color: '#1976d2', // Couleur par défaut
    category: '', // Catégorie optionnelle
    is_public: false, // Par défaut privé
  });

  /**
   * 🇬🇧 Predefined categories for lists
   * 
   * 🇫🇷 Catégories prédéfinies pour les listes
   */
  const categories = [
    { value: 'favorites', label: 'Favoris' },
    { value: 'training', label: 'Entraînement' },
    { value: 'competition', label: 'Compétition' },
    { value: 'technique', label: 'Technique' },
    { value: 'personal', label: 'Personnel' },
    { value: 'other', label: 'Autre' }
  ];

  /**
   * 🇬🇧 Predefined colors for lists with water-themed colors
   * 
   * 🇫🇷 Couleurs prédéfinies pour les listes avec des couleurs à thème aquatique
   */
  const colors = [
    { value: '#1976d2', label: 'Bleu Piscine' },
    { value: '#0288d1', label: 'Bleu Océan' },
    { value: '#388e3c', label: 'Vert Algue' },
    { value: '#d32f2f', label: 'Rouge Compétition' },
    { value: '#0097a7', label: 'Bleu Lagon' },
    { value: '#7b1fa2', label: 'Violet Profond' }
  ];

  /**
   * 🇬🇧 Fetch list data if in edit mode
   * 
   * 🇫🇷 Récupération des données de la liste si en mode édition
   */
  useEffect(() => {
    const fetchListData = async () => {
      if (!id) return;

      setLoading(true);
      try {
        const response = await getMylist(id);
        const listData = response.data;

        // Vérifier si l'utilisateur a les permissions pour cette liste
        if (listData.user_id !== user?.id) {
          setError("Vous n'avez pas l'autorisation de modifier cette liste.");
          setLoading(false);
          return;
        }

        // Initialiser le formulaire avec les données existantes
        setFormData({
          title: listData.title || '',
          description: listData.description || '',
          color: listData.color || '#1976d2',
          category: listData.category || '',
          is_public: listData.is_public || false
        });

        setError(null);
      } catch (err) {
        console.error('Erreur lors de la récupération des données de la liste', err);
        setError('Impossible de charger les détails de cette liste. Veuillez réessayer plus tard.');
      } finally {
        setLoading(false);
      }
    };

    if (id && user) {
      fetchListData();
    }
  }, [id, user]);

  /**
   * 🇬🇧 Handle form field changes
   * 
   * 🇫🇷 Gestion des changements dans les champs du formulaire
   */
  const handleChange = (e) => {
    const { name, value, type, checked } = e.target;
    setFormData({
      ...formData,
      [name]: type === 'checkbox' ? checked : value
    });
  };

  /**
   * 🇬🇧 Handle form submission (create or update)
   * 
   * 🇫🇷 Gestion de la soumission du formulaire (création ou mise à jour)
   */
  const handleSubmit = async (e) => {
    e.preventDefault();

    if (!formData.title.trim()) {
      setError("Le titre de la liste ne peut pas être vide");
      return;
    }

    setSubmitting(true);
    setError(null);

    try {
      // Ne pas envoyer les champs qui ne sont pas dans le modèle
      const data = {
        user_id: user.id,
        title: formData.title,
        description: formData.description || null
        // color, category, et is_public ne sont pas envoyés
      };

      console.log('Données envoyées:', data);

      if (id) {
        // Mode édition
        const response = await updateMylist(id, data);
        console.log('Réponse mise à jour:', response);
        setSuccessMessage("Liste mise à jour avec succès!");
        setTimeout(() => {
          navigate(`/user/mylists/${id}`);
        }, 1500);
      } else {
        // Mode création
        const response = await createMylist(data);
        console.log('Réponse création:', response);
        setSuccessMessage("Liste créée avec succès!");
        setTimeout(() => {
          navigate(`/user/mylists/${response.data.id}`);
        }, 1500);
      }
    } catch (err) {
      console.error('Erreur lors de la soumission du formulaire', err);
      // Afficher les détails de l'erreur pour le débogage
      if (err.response) {
        console.error('Détails de l\'erreur:', err.response.data);
      }
      setError(id 
        ? 'Impossible de mettre à jour la liste. Veuillez réessayer.' 
        : 'Impossible de créer la liste. Veuillez réessayer.');
    } finally {
      setSubmitting(false);
    }
  };

  /**
   * 🇬🇧 Cancel and return to previous page
   * 
   * 🇫🇷 Annuler et retourner à la page précédente
   */
  const handleCancel = () => {
    if (id) {
      navigate(`/user/mylists/${id}`);
    } else {
      navigate('/user/mylists');
    }
  };

  return (
    <>
      <main className="container py-5">
        {/* Bouton de retour */}
        <div className="mb-4">
          <Link to="/user/mylists" className="btn btn-success">
            <FaArrowLeft className="me-2" /> Retour aux carnets
          </Link>
        </div>

        {/* Carte principale du formulaire */}
        <div className="card shadow-sm border-0 mb-4">
          <div className="title-swim">
            <h1>
              {id ? 'Modifier la liste' : 'Nouveau Carnet'}
            </h1>
          </div>
          <div className="card-body p-4">
            {/* Messages de succès */}
            {successMessage && (
              <div className="alert alert-success alert-dismissible fade show" role="alert">
                <FaInfoCircle className="me-2" /> {successMessage}
                <button type="button" className="btn-close" onClick={() => setSuccessMessage(null)} aria-label="Close"></button>
              </div>
            )}

            {/* Messages d'erreur */}
            {error && (
              <div className="alert alert-danger alert-dismissible fade show" role="alert">
                <FaExclamationTriangle className="me-2" /> {error}
                <button type="button" className="btn-close" onClick={() => setError(null)} aria-label="Close"></button>
              </div>
            )}

            {/* Affichage conditionnel selon l'état de chargement */}
            {loading ? (
              <div className="text-center py-5">
                <div className="spinner-border text-primary" role="status">
                  <span className="visually-hidden">Chargement...</span>
                </div>
                <p className="mt-3">Chargement des données de la liste...</p>
              </div>
            ) : (
              <form onSubmit={handleSubmit}>
                <div className="row">
                  {/* Colonne principale avec les champs de base */}
                  <div className="col-md-8">
                    <div className="mb-4">
                      <label htmlFor="title" className="form-label">
                        <FaSwimmingPool className="me-2 text-primary" /> 
                        Titre <span className="text-danger">*</span>
                      </label>
                      <input
                        type="text"
                        className="form-control form-control-lg"
                        id="title"
                        name="title"
                        value={formData.title}
                        onChange={handleChange}
                        required
                        placeholder="Donnez un titre à votre liste"
                        disabled={submitting}
                      />
                    </div>

                    <div className="mb-4">
                      <label htmlFor="description" className="form-label">
                        <FaWater className="me-2 text-primary" /> 
                        Description
                      </label>
                      <textarea
                        className="form-control"
                        id="description"
                        name="description"
                        rows="4"
                        value={formData.description || ''}
                        onChange={handleChange}
                        placeholder="Décrivez l'objectif de cette liste (optionnel)"
                        disabled={submitting}
                      ></textarea>
                    </div>

                    <div className="mb-4">
                      <label htmlFor="category" className="form-label">
                        <FaTags className="me-2 text-primary" /> 
                        Catégorie
                      </label>
                      <select
                        className="form-select"
                        id="category"
                        name="category"
                        value={formData.category}
                        onChange={handleChange}
                        disabled={submitting}
                      >
                        <option value="">-- Sélectionner une catégorie (optionnel) --</option>
                        {categories.map(category => (
                          <option key={category.value} value={category.value}>
                            {category.label}
                          </option>
                        ))}
                      </select>
                      <small className="text-muted">
                        Note: Cette fonctionnalité est en préparation pour une future mise à jour.
                      </small>
                    </div>
                  </div>

                  {/* Colonne latérale avec les options */}
                  <div className="col-md-4">
                    <div className="card border-0 shadow-sm mb-4">
                      <div className="card-header bg-light">
                        <h5 className="card-title mb-0">Options</h5>
                      </div>
                      <div className="card-body">
                        <div className="mb-4">
                          <label className="form-label d-block">
                            <FaPalette className="me-2 text-primary" /> 
                            Couleur
                          </label>
                          <div className="d-flex flex-wrap gap-2">
                            {colors.map(color => (
                              <div 
                                key={color.value} 
                                className={`color-option rounded-circle p-2 ${formData.color === color.value ? 'border-3 border-primary' : ''}`}
                                style={{ 
                                  backgroundColor: color.value, 
                                  width: '40px', 
                                  height: '40px',
                                  cursor: 'pointer'
                                }}
                                onClick={() => setFormData({...formData, color: color.value})}
                                title={color.label}
                              ></div>
                            ))}
                            <input
                              type="color"
                              className="form-control form-control-color border-0"
                              id="color"
                              name="color"
                              value={formData.color}
                              onChange={handleChange}
                              title="Choisir une couleur personnalisée"
                              disabled={submitting}
                            />
                          </div>
                          <small className="text-muted">
                            Note: Cette fonctionnalité est en préparation pour une future mise à jour.
                          </small>
                        </div>

                        <div className="form-check form-switch">
                          <input
                            className="form-check-input"
                            type="checkbox"
                            id="is_public"
                            name="is_public"
                            checked={formData.is_public}
                            onChange={handleChange}
                            disabled={submitting}
                          />
                          <label className="form-check-label" htmlFor="is_public">
                            Liste publique
                          </label>
                          <small className="form-text text-muted d-block">
                            Si activé, d'autres utilisateurs pourront voir cette liste.
                            <br/>Note: Cette fonctionnalité est en préparation pour une future mise à jour.
                          </small>
                        </div>
                      </div>
                    </div>

                    {/* Aperçu de la liste */}
                    <div className="card bg-light border-0">
                      <div className="card-body">
                        <h5 className="card-title h6">Aperçu</h5>
                        <div 
                          className="p-3 rounded-3 mb-3"
                          style={{ backgroundColor: formData.color, color: '#fff' }}
                        >
                          <h6 className="mb-1">{formData.title || 'Titre de la liste'}</h6>
                          <small>{formData.description || 'Description (optionnelle)'}</small>
                        </div>
                        {formData.category && (
                          <div className="d-inline-block bg-secondary bg-opacity-10 text-secondary rounded-pill px-3 py-1 small">
                            {categories.find(c => c.value === formData.category)?.label || formData.category}
                          </div>
                        )}
                      </div>
                    </div>
                  </div>
                </div>

                {/* Boutons d'action */}
                <div className="d-flex gap-2 justify-content-end mt-4 border-top pt-4">
                  <button
                    type="button"
                    className="btn btn-outline-secondary"
                    onClick={handleCancel}
                    disabled={submitting}
                  >
                    Annuler
                  </button>
                  <button
                    type="submit"
                    className="btn btn-primary"
                    disabled={submitting || !formData.title.trim()}
                  >
                    {submitting ? (
                      <>
                        <span className="spinner-border spinner-border-sm me-2" role="status" aria-hidden="true"></span>
                        {id ? 'Mise à jour...' : 'Création...'}
                      </>
                    ) : (
                      <>
                        <FaSave className="me-2" /> 
                        {id ? 'Mettre à jour' : 'Créer la liste'}
                      </>
                    )}
                  </button>
                </div>
              </form>
            )}
          </div>
        </div>



        {/* 
          Guide d'utilisation des carnets d'entraînement 
          Nouvelle section ajoutée avec des cartes thématiques
        */}
        <section className="mb-5">
          <div className="card border-0 shadow-sm">
            <div className="btn btn-danger btn-lg btn-block">
              <h2 className="h4 mb-0">
                <FaBook className="me-2" /> Guide d'utilisation des carnets d'entraînement
              </h2>
            </div>
            <div className="card-body p-4">
              <div className="row mb-4">
                <div className="col-lg-8 mx-auto">
                  <div className="alert alert-info d-flex">
                    <FaInfoCircle className="me-3 fs-5 mt-1" />
                    <div>
                      <p className="mb-0">Créez et personnalisez vos carnets d'entraînement en quelques étapes simples pour organiser efficacement vos programmes de natation.</p>
                    </div>
                  </div>
                </div>
              </div>

              <div className="row row-cols-1 row-cols-md-2 row-cols-lg-4 g-4 mb-4">
                {/* Carte 1: Création */}
                <div className="col">
                  <div className="card h-100 border-0 shadow-sm">
                    <div className="card-header bg-primary bg-opacity-10 text-primary py-3">
                      <h3 className="h5 mb-0 d-flex align-items-center">
                        <FaPlus className="me-2" /> 1️⃣ Création d'un carnet
                      </h3>
                    </div>
                    <div className="card-body">
                      <ul className="list-unstyled mb-0">
                        <li className="mb-2 d-flex">
                          <span className="me-2 text-primary">•</span>
                          <span>Rendez-vous sur la page "Mes Carnets"</span>
                        </li>
                        <li className="mb-2 d-flex">
                          <span className="me-2 text-primary">•</span>
                          <span>Attribuez un titre clair et pertinent</span>
                        </li>
                        <li className="mb-2 d-flex">
                          <span className="me-2 text-primary">•</span>
                          <span>Ajoutez une description précise</span>
                        </li>
                        <li className="d-flex">
                          <span className="me-2 text-primary">•</span>
                          <span>Validez la création du carnet</span>
                        </li>
                      </ul>
                    </div>
                  </div>
                </div>

                {/* Carte 2: Personnalisation */}
                <div className="col">
                  <div className="card h-100 border-0 shadow-sm">
                    <div className="card-header bg-success bg-opacity-10 text-success py-3">
                      <h3 className="h5 mb-0 d-flex align-items-center">
                        <FaPencilAlt className="me-2" /> 2️⃣ Personnalisation
                      </h3>
                    </div>
                    <div className="card-body">
                      <ul className="list-unstyled mb-0">
                        <li className="mb-2 d-flex">
                          <span className="me-2 text-success">•</span>
                          <span>Sélectionnez votre carnet créé</span>
                        </li>
                        <li className="mb-2 d-flex">
                          <span className="me-2 text-success">•</span>
                          <span>Cliquez sur "Ajouter des éléments"</span>
                        </li>
                        <li className="mb-2 d-flex">
                          <span className="me-2 text-success">•</span>
                          <span>Choisissez le type d'élément à ajouter</span>
                        </li>
                        <li className="d-flex">
                          <span className="me-2 text-success">•</span>
                          <span>Sélectionnez et validez vos choix</span>
                        </li>
                      </ul>
                    </div>
                  </div>
                </div>

                {/* Carte 3: Gestion */}
                <div className="col">
                  <div className="card h-100 border-0 shadow-sm">
                    <div className="card-header bg-info bg-opacity-10 text-info py-3">
                      <h3 className="h5 mb-0 d-flex align-items-center">
                        <FaLayerGroup className="me-2" /> 3️⃣ Gestion des carnets
                      </h3>
                    </div>
                    <div className="card-body">
                      <ul className="list-unstyled mb-0">
                        <li className="mb-2 d-flex">
                          <span className="me-2 text-info">•</span>
                          <span>Accédez à vos carnets 24h/24</span>
                        </li>
                        <li className="mb-2 d-flex">
                          <span className="me-2 text-info">•</span>
                          <span>Consultez ou modifiez vos carnets</span>
                        </li>
                        <li className="mb-2 d-flex">
                          <span className="me-2 text-info">•</span>
                          <span>Dupliquez pour créer des variantes</span>
                        </li>
                        <li className="d-flex">
                          <span className="me-2 text-info">•</span>
                          <span>Supprimez les carnets non utilisés</span>
                        </li>
                      </ul>
                    </div>
                  </div>
                </div>

                {/* Carte 4: Points clés */}
                <div className="col">
                  <div className="card h-100 border-0 shadow-sm">
                    <div className="card-header bg-warning bg-opacity-10 text-warning py-3">
                      <h3 className="h5 mb-0 d-flex align-items-center">
                        <FaLightbulb className="me-2" /> 4️⃣ Points clés
                      </h3>
                    </div>
                    <div className="card-body">
                      <ul className="list-unstyled mb-0">
                        <li className="mb-2 d-flex">
                          <span className="me-2 text-warning">•</span>
                          <span>Confidentialité garantie</span>
                        </li>
                        <li className="mb-2 d-flex">
                          <span className="me-2 text-warning">•</span>
                          <span>Flexibilité selon vos objectifs</span>
                        </li>
                        <li className="mb-2 d-flex">
                          <span className="me-2 text-warning">•</span>
                          <span>Bibliothèque complète disponible</span>
                        </li>
                        <li className="d-flex">
                          <span className="me-2 text-warning">•</span>
                          <span>Interface intuitive pour faciliter l'usage</span>
                        </li>
                      </ul>
                    </div>
                  </div>
                </div>
              </div>

              {/* Conseils supplémentaires */}
              <div className="row mt-4">
                <div className="col-12">
                  <div className="card border-0 bg-light">
                    <div className="card-body p-4">
                      <h3 className="h5 mb-3 d-flex align-items-center">
                        <FaTools className="me-2 text-secondary" /> 🏅 Conseils supplémentaires
                      </h3>
                      <div className="row">
                        <div className="col-md-4 mb-3 mb-md-0">
                          <div className="d-flex">
                            <span className="fw-bold text-primary me-2">•</span>
                            <p className="mb-0">Utilisez des titres précis pour retrouver facilement vos carnets.</p>
                          </div>
                        </div>
                        <div className="col-md-4 mb-3 mb-md-0">
                          <div className="d-flex">
                            <span className="fw-bold text-primary me-2">•</span>
                            <p className="mb-0">Dupliquez un carnet existant pour créer rapidement une nouvelle version.</p>
                          </div>
                        </div>
                        <div className="col-md-4">
                          <div className="d-flex">
                            <span className="fw-bold text-primary me-2">•</span>
