import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import { FaPlus, FaEye, FaEdit, FaTrash, FaSwimmer } from 'react-icons/fa';
import Pagination from '../../../components/common/Pagination';
import { apiService } from '../../../services/api';
import { toast } from 'react-toastify';

/**
 * üá¨üáß Admin Swim Sets Page - Displays and manages all swimming series with pagination
 * üá´üá∑ Page d'administration des s√©ries de natation - Affiche et g√®re toutes les s√©ries avec pagination
 */
const AdminSwimSetsPage = () => {
  const navigate = useNavigate();
  const [swimSets, setSwimSets] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  // Pagination state
  const [currentPage, setCurrentPage] = useState(1);
  const [totalPages, setTotalPages] = useState(1);
  const [itemsPerPage, setItemsPerPage] = useState(10);
  const [totalItems, setTotalItems] = useState(0);

  /**
   * üá¨üáß Fetches swim sets with pagination from the API
   * üá´üá∑ R√©cup√®re les s√©ries de natation avec pagination depuis l'API
   */
  const fetchSwimSets = async (page = 1) => {
    try {
      setLoading(true);
      console.log(`R√©cup√©ration des s√©ries de natation (page ${page})...`);

      const response = await apiService.get(`/swim-sets`, {
        params: {
          page: page,
          per_page: itemsPerPage
        }
      });

      console.log('R√©ponse:', response.data);

      // Mise √† jour des donn√©es et des informations de pagination
      setSwimSets(response.data.data);
      setTotalPages(response.data.meta.last_page);
      setTotalItems(response.data.meta.total);
      setCurrentPage(response.data.meta.current_page);
      setError(null);
    } catch (err) {
      console.error('Erreur lors de la r√©cup√©ration des s√©ries:', err);
      setError('Impossible de charger les s√©ries. Veuillez r√©essayer.');
      toast.error('Erreur lors du chargement des s√©ries de natation');
    } finally {
      setLoading(false);
    }
  };

  /**
   * üá¨üáß Handles page change in pagination
   * üá´üá∑ G√®re le changement de page dans la pagination
   */
  const handlePageChange = (page) => {
    setCurrentPage(page);
    fetchSwimSets(page);
  };

  /**
   * üá¨üáß Handles swim set deletion
   * üá´üá∑ G√®re la suppression d'une s√©rie de natation
   */
  const handleDelete = async (id) => {
    if (window.confirm('√ätes-vous s√ªr de vouloir supprimer cette s√©rie ?')) {
      try {
        await apiService.delete(`/swim-sets/${id}`);
        toast.success('S√©rie supprim√©e avec succ√®s');
        fetchSwimSets(currentPage);
      } catch (err) {
        console.error('Erreur lors de la suppression:', err);
        setError('Erreur lors de la suppression: ' + err.message);
        toast.error('Erreur lors de la suppression de la s√©rie');
      }
    }
  };

  useEffect(() => {
    fetchSwimSets();
    // eslint-disable-next-line react-hooks/exhaustive-deps
  }, [itemsPerPage]);

  /**
   * üá¨üáß Get the exercise name or placeholder
   * üá´üá∑ R√©cup√®re le nom de l'exercice ou un placeholder
   */
  const getExerciseName = (set) => {
    return set.exercise ? set.exercise.name : `Exercice #${set.exercise_id}`;
  };

  if (loading && swimSets.length === 0) {
    return (
      <div className="container py-4">
        <div className="text-center">
          <div className="spinner-border text-primary" role="status">
            <span className="visually-hidden">Chargement...</span>
          </div>
        </div>
      </div>
    );
  }

  return (
    <div className="container-fluid py-4">
      {/* Header Section */}
      <div className="d-flex justify-content-between align-items-center mb-4">
        <div>
          <h1 className="h2 mb-0 d-flex align-items-center">
            <FaSwimmer className="me-2 text-primary" /> 
            Gestion des S√©ries
          </h1>
          <p className="text-muted mb-0">
            {totalItems} s√©rie{totalItems > 1 ? 's' : ''} au total
          </p>
        </div>
        <button 
          className="btn btn-primary d-flex align-items-center"
          onClick={() => navigate('/admin/swim-sets/new')}
        >
          <FaPlus className="me-2" /> Nouvelle s√©rie
        </button>
      </div>

      {/* Error Alert */}
      {error && (
        <div className="alert alert-danger" role="alert">
          {error}
        </div>
      )}

      {/* Table View */}
      <div className="card shadow-sm mb-4">
        <div className="card-body">
          <div className="table-responsive">
            <table className="table table-hover align-middle">
              <thead className="table-light">
                <tr>
                  <th scope="col">ID</th>
                  <th scope="col">Exercice</th>
                  <th scope="col">Distance</th>
                  <th scope="col">R√©p√©titions</th>
                  <th scope="col">Repos</th>
                  <th scope="col" className="text-end">Actions</th>
                </tr>
              </thead>
              <tbody>
                {swimSets.length > 0 ? (
                  swimSets.map(set => (
                    <tr key={set.id}>
                      <td>{set.id}</td>
                      <td>{getExerciseName(set)}</td>
                      <td>{set.set_distance}m</td>
                      <td>{set.set_repetition || 1}</td>
                      <td>{set.rest_time || 0}s</td>
                      <td>
                        <div className="d-flex justify-content-end">
                          <button
                            className="btn btn-sm btn-outline-primary me-1"
                            onClick={() => navigate(`/admin/swim-sets/${set.id}`)}
                            title="Voir les d√©tails"
                          >
                            <FaEye />
                          </button>
                          <button
                            className="btn btn-sm btn-outline-secondary me-1"
                            onClick={() => navigate(`/admin/swim-sets/${set.id}/edit`)}
                            title="Modifier"
                          >
                            <FaEdit />
                          </button>
                          <button
                            className="btn btn-sm btn-outline-danger"
                            onClick={() => handleDelete(set.id)}
                            title="Supprimer"
                          >
                            <FaTrash />
                          </button>
                        </div>
                      </td>
                    </tr>
                  ))
                ) : (
                  <tr>
                    <td colSpan="6" className="text-center py-4">
                      Aucune s√©rie disponible.
                    </td>
                  </tr>
                )}
              </tbody>
            </table>
          </div>
        </div>
      </div>

      {/* Pagination */}
      {totalPages > 1 && (
        <Pagination
          currentPage={currentPage}
          totalPages={totalPages}
          onPageChange={handlePageChange}
          totalItems={totalItems}
          itemsPerPage={itemsPerPage}
          onItemsPerPageChange={(value) => {
            setItemsPerPage(value);
            setCurrentPage(1);
          }}
        />
      )}
    </div>
  );
};

export default AdminSwimSetsPage;
