import React, { useState, useEffect } from 'react';
import { useParams, useNavigate, Link } from 'react-router-dom';
import axios from 'axios';
import { 
  FaSave, 
  FaArrowLeft, 
  FaExclamationTriangle, 
  FaInfoCircle, 
  FaSwimmingPool, 
  FaWater,
  FaEdit,
  FaTrash,
  FaEye,
  FaTable,
  FaListAlt,
  FaSwimmer,
  FaDumbbell,
  FaCalendarAlt,
  FaPlusCircle,
  FaMinusCircle
} from 'react-icons/fa';
import { createMylist, getMylist, updateMylist, getMylists } from '../../../services/mylists';
import useAuth from '../../../hooks/useAuth';

/**
 * ðŸ‡¬ðŸ‡§ User's list creation form page (simplified)
 * This component allows users to create new lists or edit existing ones with title and description.
 * It also displays a table of the user's existing lists at the bottom.
 * 
 * ðŸ‡«ðŸ‡· Page de formulaire de crÃ©ation de liste utilisateur (simplifiÃ©e)
 * Ce composant permet aux utilisateurs de crÃ©er de nouvelles listes ou de modifier des listes existantes avec titre et description.
 * Il affiche Ã©galement un tableau des listes existantes de l'utilisateur en bas.
 */
const UserMyListFormPage = () => {
  const { id } = useParams();
  const navigate = useNavigate();
  const { user } = useAuth();

  /**
   * ðŸ‡¬ðŸ‡§ State management for the component
   * 
   * ðŸ‡«ðŸ‡· Gestion des Ã©tats du composant
   */
  const [loading, setLoading] = useState(id ? true : false);
  const [submitting, setSubmitting] = useState(false);
  const [error, setError] = useState(null);
  const [successMessage, setSuccessMessage] = useState(null);
  const [userLists, setUserLists] = useState([]);
  const [listLoading, setListLoading] = useState(true);

  /**
   * ðŸ‡¬ðŸ‡§ State for list items management
   * 
   * ðŸ‡«ðŸ‡· Ã‰tat pour la gestion des Ã©lÃ©ments de liste
   */
  const [listItems, setListItems] = useState([]);
  const [itemsLoading, setItemsLoading] = useState(id ? true : false);

  /**
   * ðŸ‡¬ðŸ‡§ Form data state
   * 
   * ðŸ‡«ðŸ‡· Ã‰tat des donnÃ©es du formulaire
   */
  const [formData, setFormData] = useState({
    title: '',
    description: ''
  });

  /**
   * ðŸ‡¬ðŸ‡§ Fetch list data if in edit mode
   * 
   * ðŸ‡«ðŸ‡· RÃ©cupÃ©ration des donnÃ©es de la liste si en mode Ã©dition
   */
  useEffect(() => {
    const fetchListData = async () => {
      if (!id) return;

      setLoading(true);
      try {
        const response = await getMylist(id);
        const listData = response.data;

        // VÃ©rifier si l'utilisateur a les permissions pour cette liste
        if (listData.user_id !== user?.id) {
          setError("Vous n'avez pas l'autorisation de modifier cette liste.");
          setLoading(false);
          return;
        }

        // Initialiser le formulaire avec les donnÃ©es existantes
        setFormData({
          title: listData.title || '',
          description: listData.description || ''
        });

        setError(null);
      } catch (err) {
        console.error('Erreur lors de la rÃ©cupÃ©ration des donnÃ©es de la liste', err);
        setError('Impossible de charger les dÃ©tails de cette liste. Veuillez rÃ©essayer plus tard.');
      } finally {
        setLoading(false);
      }
    };

    if (id && user) {
      fetchListData();
    }
  }, [id, user]);

  /**
   * ðŸ‡¬ðŸ‡§ Fetch list items if in edit mode
   * 
   * ðŸ‡«ðŸ‡· RÃ©cupÃ©ration des Ã©lÃ©ments de la liste si en mode Ã©dition
   */
  useEffect(() => {
    const fetchListItems = async () => {
      if (!id) return;

      setItemsLoading(true);
      try {
        // RÃ©cupÃ©rer les Ã©lÃ©ments associÃ©s Ã  cette liste
        const response = await axios.get(`/api/mylists/${id}/items`);
        console.log('API response for list items:', response.data);

        // VÃ©rifier si la rÃ©ponse contient des donnÃ©es valides
        if (!response.data || !Array.isArray(response.data)) {
          console.error('RÃ©ponse API invalide:', response.data);
          setListItems([]);
          setItemsLoading(false);
          return;
        }

        // Si la liste est vide, pas besoin de traitement supplÃ©mentaire
        if (response.data.length === 0) {
          setListItems([]);
          setItemsLoading(false);
          return;
        }

        // Enrichir les donnÃ©es avec le type d'Ã©lÃ©ment (exercice, sÃ©ance, etc.)
        const enrichedItems = await Promise.all(
          response.data.map(async (item) => {
            try {
              let itemDetails = {};
              let itemType = '';

              // VÃ©rifier que item et item_type sont valides
              if (!item || !item.item_type || !item.item_id) {
                console.warn('Ã‰lÃ©ment de liste invalide:', item);
                return null; // Cet Ã©lÃ©ment sera filtrÃ© plus tard
              }

              switch(item.item_type) {
                case 'exercise':
                  const exerciseResponse = await axios.get(`/api/exercises/${item.item_id}`);
                  itemDetails = exerciseResponse.data;
                  itemType = 'Exercice';
                  break;
                case 'workout':
                  const workoutResponse = await axios.get(`/api/workouts/${item.item_id}`);
                  itemDetails = workoutResponse.data;
                  itemType = 'SÃ©ance';
                  break;
                case 'plan':
                  const planResponse = await axios.get(`/api/plans/${item.item_id}`);
                  itemDetails = planResponse.data;
                  itemType = 'Plan';
                  break;
                case 'swim_set':
                  const setResponse = await axios.get(`/api/swim-sets/${item.item_id}`);
                  itemDetails = setResponse.data;
                  itemType = 'SÃ©rie';
                  break;
                default:
                  itemType = 'Inconnu';
              }

              return {
                ...item,
                details: itemDetails || {},
                typeName: itemType
              };
            } catch (err) {
              console.error(`Erreur lors de la rÃ©cupÃ©ration des dÃ©tails pour l'Ã©lÃ©ment ${item.id}`, err);
              return {
                ...item,
                details: {},
                typeName: item.item_type
              };
            }
          })
        );

        // Filtrer les Ã©lÃ©ments null (qui ont Ã©chouÃ© lors de l'enrichissement)
        const validItems = enrichedItems.filter(item => item !== null);
        console.log('Processed list items:', validItems);
        setListItems(validItems);
      } catch (err) {
        console.error('Erreur lors de la rÃ©cupÃ©ration des Ã©lÃ©ments de la liste', err);
        setListItems([]);
      } finally {
        setItemsLoading(false);
      }
    };

    if (id) {
      fetchListItems();
    } else {
      // RÃ©initialiser les Ã©lÃ©ments si pas en mode Ã©dition
      setListItems([]);
    }
  }, [id]);

  /**
   * ðŸ‡¬ðŸ‡§ Fetch user's lists
   * 
   * ðŸ‡«ðŸ‡· RÃ©cupÃ©ration des listes de l'utilisateur
   */
  useEffect(() => {
    const fetchUserLists = async () => {
      if (!user) return;

      setListLoading(true);
      try {
        const response = await getMylists();
        // Filtrer les listes de l'utilisateur actuel
        const filteredLists = response.data.filter(list => list.user_id === user.id);
        setUserLists(filteredLists);
      } catch (err) {
        console.error('Erreur lors de la rÃ©cupÃ©ration des listes de l\'utilisateur', err);
      } finally {
        setListLoading(false);
      }
    };

    fetchUserLists();
  }, [user]);

  /**
   * ðŸ‡¬ðŸ‡§ Handle form field changes
   * 
   * ðŸ‡«ðŸ‡· Gestion des changements dans les champs du formulaire
   */
  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData({
      ...formData,
      [name]: value
    });
  };

  /**
   * ðŸ‡¬ðŸ‡§ Handle form submission (create or update)
   * 
   * ðŸ‡«ðŸ‡· Gestion de la soumission du formulaire (crÃ©ation ou mise Ã  jour)
   */
  const handleSubmit = async (e) => {
    e.preventDefault();

