/**
 * üá¨üáß Swimming Performance Predictor Component - allows swimmers to predict intermediate and final race times based on 50m personal bests and medley splits.
 * üá´üá∑ Composant de pr√©diction de performance en natation - permet aux nageurs de pr√©dire les temps interm√©diaires et finaux √† partir du meilleur temps au 50m et des temps par nage pour les quatre nages.
 */

import React, { useState, useEffect } from 'react';
import Header from '../../components/template/Header';
import Footer from '../../components/template/Footer';
import { FaStopwatch, FaInfoCircle } from 'react-icons/fa';
import { Timer, Info } from 'lucide-react';
import { LineChart, Line, XAxis, YAxis, CartesianGrid, Tooltip, Legend } from 'recharts';

// üèä‚Äç‚ôÇÔ∏è D√©finition des styles de nage et distances disponibles
const swimmingStyles = [
  { name: 'Crawl', distances: [100, 200, 400, 800, 1500] },
  { name: 'Papillon', distances: [100, 200] },
  { name: 'Brasse', distances: [100, 200] },
  { name: 'Dos', distances: [100, 200] },
  { name: 'Quatre Nages', distances: [200, 400] },
];

// üßÆ Formatage du temps brut en mm:ss:cs
const formatTime = (timeInSeconds) => {
  const minutes = Math.floor(timeInSeconds / 60);
  const seconds = Math.floor(timeInSeconds % 60);
  const centiseconds = Math.round((timeInSeconds % 1) * 100);
  return `${minutes}:${seconds.toString().padStart(2, '0')}.${centiseconds.toString().padStart(2, '0')}`;
};

// üîÅ Ajoute un pourcentage de marge pour simuler la fatigue/erreur
const addFinalPercentage = (time) => {
  return time * 1.049;
};

// üìà Calcule les temps pr√©visionnels en fonction du style, distance et temps de base
const calculatePredictedTimes = (style, distance, bestTime50m, medleyTimes) => {
  const predictions = [];
  const time50m = bestTime50m;
  const time100m = addFinalPercentage((time50m + 1.5) + (time50m + 2.49));

  switch (style) {
    case 'Quatre Nages':
      if (distance === 200 && medleyTimes) {
        const { butterfly50m, backstroke50m, breaststroke50m, freestyle50m } = medleyTimes;
        const laps = [
          { style: 'Papillon', time: parseFloat(butterfly50m) + 2.2 },
          { style: 'Dos', time: parseFloat(backstroke50m) + 3.09 },
          { style: 'Brasse', time: parseFloat(breaststroke50m) + 5.59 },
          { style: 'Crawl', time: parseFloat(freestyle50m) + 5.09 }
        ];
        laps.forEach((lap, i) => {
          predictions.push({
            distance: (i + 1) * 50,
            time: i === laps.length - 1 ? addFinalPercentage(lap.time) : lap.time,
            timeFormatted: formatTime(i === laps.length - 1 ? addFinalPercentage(lap.time) : lap.time),
            label: `${lap.style} ${i + 1}${i === 0 ? 'er' : '√®me'} 50m`
          });
        });
      } else if (distance === 400 && medleyTimes) {
        const { butterfly100m, backstroke100m, breaststroke100m, freestyle100m } = medleyTimes;
        const laps = [
          { style: 'Papillon', time: parseFloat(butterfly100m) + 3.2 },
          { style: 'Dos', time: parseFloat(backstroke100m) + 6.19 },
          { style: 'Brasse', time: parseFloat(breaststroke100m) + 6 },
          { style: 'Crawl', time: parseFloat(freestyle100m) + 8.97 }
        ];
        laps.forEach((lap, i) => {
          predictions.push({
            distance: (i + 1) * 100,
            time: i === laps.length - 1 ? addFinalPercentage(lap.time) : lap.time,
            timeFormatted: formatTime(i === laps.length - 1 ? addFinalPercentage(lap.time) : lap.time),
            label: `${lap.style} ${i + 1}${i === 0 ? 'er' : '√®me'} 100m`
          });
        });
      }
      break;

    default:
      if (distance === 100) {
        predictions.push(
          { distance: 50, time: time50m + 1.5, timeFormatted: formatTime(time50m + 1.5), label: '1er 50m' },
          { distance: 100, time: addFinalPercentage(time50m + 2.49), timeFormatted: formatTime(addFinalPercentage(time50m + 2.49)), label: '2√®me 50m' }
        );
      } else if (distance === 200) {
        const laps = [2.5, 4.13, 4.53, 2.57];
        laps.forEach((t, i) => {
          const time = time50m + t;
          predictions.push({
            distance: (i + 1) * 50,
            time: i === 3 ? addFinalPercentage(time) : time,
            timeFormatted: formatTime(i === 3 ? addFinalPercentage(time) : time),
            label: `${i + 1}${i === 0 ? 'er' : '√®me'} 50m`
          });
        });
      } else if (distance === 400) {
        const adds = [4.5, 6.13, 6, 2.59];
        adds.forEach((add, i) => {
          const time = time100m + add;
          predictions.push({
            distance: (i + 1) * 100,
            time: i === 3 ? addFinalPercentage(time) : time,
            timeFormatted: formatTime(i === 3 ? addFinalPercentage(time) : time),
            label: `${i + 1}${i === 0 ? 'er' : '√®me'} 100m`
          });
        });
      }
      break;
  }

  return predictions;
};

// ‚ûï Somme tous les temps calcul√©s pour obtenir le total
const calculateTotalTime = (predictions) => {
  return predictions.reduce((total, pred) => total + pred.time, 0);
};

// üß† Composant principal
const SwimmingPerformancePredictor = () => {
  const [selectedStyle, setSelectedStyle] = useState(swimmingStyles[0].name);
  const [selectedDistance, setSelectedDistance] = useState(swimmingStyles[0].distances[0]);
  const [bestTime50m, setBestTime50m] = useState('');
  const [distance, setDistance] = useState(100);
  const [predictions, setPredictions] = useState([]);
  const [error, setError] = useState('');
  const [totalTime, setTotalTime] = useState('');
  const [medleyTimes, setMedleyTimes] = useState({
    butterfly50m: '',
    backstroke50m: '',
    breaststroke50m: '',
    freestyle50m: '',
    butterfly100m: '',
    backstroke100m: '',
    breaststroke100m: '',
    freestyle100m: '',
  });

  // üßÆ G√®re le calcul lorsqu'on clique sur "Calculer"
  const handleCalculate = () => {
    const parsedTime = parseFloat(bestTime50m);
    if (selectedStyle === 'Quatre Nages') {
      const is400 = selectedDistance === 400;
      const requiredFields = is400
        ? ['butterfly100m', 'backstroke100m', 'breaststroke100m', 'freestyle100m']
        : ['butterfly50m', 'backstroke50m', 'breaststroke50m', 'freestyle50m'];

      const missing = requiredFields.some(field => !medleyTimes[field] || isNaN(parseFloat(medleyTimes[field])));
      if (missing) {
        setError(`Veuillez entrer tous les temps pour le ${selectedDistance}m Quatre Nages`);
        return;
      }

      const preds = calculatePredictedTimes(selectedStyle, selectedDistance, 0, medleyTimes);
      setPredictions(preds);
      setTotalTime(formatTime(calculateTotalTime(preds)));
      setError('');
      return;
    }

    if (isNaN(parsedTime) || parsedTime <= 0) {
      setError('Veuillez entrer un temps valide pour le 50m.');
      return;
    }

    const preds = calculatePredictedTimes(selectedStyle, distance, parsedTime);
    setPredictions(preds);
    setTotalTime(formatTime(calculateTotalTime(preds)));
    setError('');
  };

  useEffect(() => {
    const style = swimmingStyles.find(s => s.name === selectedStyle);
    if (style) {
      setSelectedDistance(style.distances[0]);
      setDistance(style.distances[0]);
    }
  }, [selectedStyle]);

  const handleMedleyTimeChange = (field, value) => {
    setMedleyTimes(prev => ({
      ...prev,
      [field]: value
    }));
  };

  // üñ•Ô∏è UI simplifi√©e (peut √™tre enrichie selon ta maquette existante)
  return (
    <>
      <Header />
      <main className="p-4">
        <h1 className="text-2xl font-bold mb-4">Pr√©dicteur de Performance Natation</h1>

        <div className="mb-4">
          <label>Style de nage :</label>
          <select value={selectedStyle} onChange={(e) => setSelectedStyle(e.target.value)}>
            {swimmingStyles.map((s) => (
              <option key={s.name} value={s.name}>{s.name}</option>
            ))}
          </select>
        </div>

        <div className="mb-4">
          <label>Distance :</label>
          <select value={distance} onChange={(e) => setDistance(parseInt(e.target.value))}>
            {swimmingStyles.find(s => s.name === selectedStyle)?.distances.map(d => (
              <option key={d} value={d}>{d}m</option>
            ))}
          </select>
        </div>

        {selectedStyle === 'Quatre Nages' ? (
          <>
            {(distance === 200 || distance === 400) && (
              <>
                <h3>Temps pour chaque nage :</h3>
                {Object.entries(medleyTimes).filter(([key]) => key.includes(distance === 200 ? '50m' : '100m')).map(([key, value]) => (
                  <div key={key} className="mb-2">
                    <label>{key} :</label>
                    <input type="number" step="0.01" value={value} onChange={(e) => handleMedleyTimeChange(key, e.target.value)} />
                  </div>
                ))}
              </>
            )}
          </>
        ) : (
          <div className="mb-4">
            <label>Temps 50m :</label>
            <input type="number" step="0.01" value={bestTime50m} onChange={(e) => setBestTime50m(e.target.value)} />
          </div>
        )}

        <button onClick={handleCalculate}>Calculer mes temps</button>

        {error && <p className="text-red-600">{error}</p>}

        {totalTime && <p className="mt-4">Temps total estim√© : <strong>{totalTime}</strong></p>}

        {predictions.length > 0 && (
          <div className="mt-4">
            <h3>Temps interm√©diaires :</h3>
            <ul>
              {predictions.map((p, idx) => (
                <li key={idx}>{p.label}: {p.timeFormatted} ({p.time.toFixed(2)}s)</li>
              ))}
            </ul>
          </div>
        )}
      </main>
      <Footer />
    </>
  );
};

export default SwimmingPerformancePredictor;
