/**
 * üá¨üáß Private route component with direct localStorage token verification
 * üá´üá∑ Composant de route priv√©e avec v√©rification directe du token dans localStorage
 */
import { Outlet, Navigate, useLocation } from "react-router-dom";
import { useState, useEffect } from "react";

/**
 * üá¨üáß PrivateRoute - Controls access to protected routes
 * Uses direct localStorage verification instead of relying on hooks
 * 
 * üá´üá∑ PrivateRoute - Contr√¥le l'acc√®s aux routes prot√©g√©es
 * Utilise une v√©rification directe dans localStorage au lieu de s'appuyer sur des hooks
 */
const PrivateRoute = () => {
  const location = useLocation();
  const [isAuthenticated, setIsAuthenticated] = useState(false);
  const [isLoading, setIsLoading] = useState(true);

  useEffect(() => {
    // V√©rification directe du token dans localStorage
    const checkAuth = () => {
      try {
        // R√©cup√©ration directe du token de localStorage
        const token = localStorage.getItem("auth_token");

        // Log de d√©bogage
        console.log("Token direct from localStorage:", token);

        // V√©rifie si le token existe
        const hasToken = Boolean(token);

        setIsAuthenticated(hasToken);
      } catch (error) {
        console.error("Error checking auth:", error);
        setIsAuthenticated(false);
      } finally {
        setIsLoading(false);
      }
    };

    checkAuth();
  }, []);

  // Log de d√©bogage suppl√©mentaire
  console.log("Current path:", location.pathname);
  console.log("Authentication status:", isAuthenticated);

  // Afficher un indicateur de chargement pendant la v√©rification
  if (isLoading) {
    return <div className="text-center p-5">Chargement...</div>;
  }

  // Si l'utilisateur n'est pas authentifi√©, rediriger vers la page d'erreur
  if (!isAuthenticated) {
    console.log("Access denied: Redirecting to /401");
    return <Navigate to="/401" state={{ from: location }} replace />;
  }

  // Si l'utilisateur est authentifi√©, afficher le contenu prot√©g√©
  console.log("Access granted: Displaying protected content");
  return <Outlet />;
};

export default PrivateRoute;
