import React, { useState, useEffect } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import { 
  FaWater, 
  FaSwimmingPool, 
  FaSearch, 
  FaTrash, 
  FaEdit, 
  FaExclamationTriangle,
  FaInfoCircle, 
  FaStar, 
  FaFilter, 
  FaSort, 
  FaSortAlphaDown, 
  FaSortAlphaUp,
  FaEye, 
  FaCopy, 
  FaCalendarAlt, 
  FaCheck, 
  FaTint, 
  FaSwimmer, 
  FaPlus,
  FaBook,
  FaPencilAlt,
  FaLayerGroup,
  FaLightbulb,
  FaTools,
  FaArrowLeft
} from 'react-icons/fa';
import Footer from '../../../components/template/Footer';
import { getMylists, createMylist, deleteMylist } from '../../../services/mylists';
import useAuth from '../../../hooks/useAuth';

/**
 * ðŸ‡¬ðŸ‡§ User's personal lists management page
 * This component allows users to view, create, edit and delete their personal lists
 * of exercises, sessions and plans.
 * 
 * ðŸ‡«ðŸ‡· Page de gestion des listes personnelles de l'utilisateur
 * Ce composant permet aux utilisateurs de consulter, crÃ©er, modifier et supprimer leurs listes
 * personnelles d'exercices, de sÃ©ances et de plans.
 */
const UserMyListsPage = () => {
  const { user } = useAuth();
  const navigate = useNavigate();

  /**
   * ðŸ‡¬ðŸ‡§ State management for the component
   * 
   * ðŸ‡«ðŸ‡· Gestion des Ã©tats du composant
   */
  const [mylists, setMylists] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [searchTerm, setSearchTerm] = useState('');
  const [showDeleteModal, setShowDeleteModal] = useState(false);
  const [listToDelete, setListToDelete] = useState(null);
  const [showCreateModal, setShowCreateModal] = useState(false);
  const [newList, setNewList] = useState({
    title: '',
    description: '',
  });
  const [successMessage, setSuccessMessage] = useState(null);
  const [sortBy, setSortBy] = useState('created_at');
  const [sortDirection, setSortDirection] = useState('desc');
  const [viewMode, setViewMode] = useState('grid'); // 'grid' ou 'list'
  const [filter, setFilter] = useState('all'); // 'all', 'recent', 'favorites'

  /**
   * ðŸ‡¬ðŸ‡§ Fetch user's lists on component mount
   * 
   * ðŸ‡«ðŸ‡· RÃ©cupÃ©ration des listes de l'utilisateur au montage du composant
   */
  useEffect(() => {
    const fetchMyLists = async () => {
      setLoading(true);
      try {
        const response = await getMylists();
        // Filtrer les listes de l'utilisateur actuel
        const userLists = response.data.filter(list => list.user_id === user?.id);
        setMylists(userLists);
        setError(null);
      } catch (err) {
        console.error('Erreur lors de la rÃ©cupÃ©ration des listes personnelles', err);
        setError('Impossible de charger vos listes personnelles. Veuillez rÃ©essayer plus tard.');
      } finally {
        setLoading(false);
      }
    };

    if (user) {
      fetchMyLists();
    }
  }, [user]);

  /**
   * ðŸ‡¬ðŸ‡§ Filter and sort lists based on search term, filter and sort criteria
   * 
   * ðŸ‡«ðŸ‡· Filtrage et tri des listes selon le terme de recherche, le filtre et les critÃ¨res de tri
   */
  const filteredLists = mylists
    .filter(list => 
      (list.title.toLowerCase().includes(searchTerm.toLowerCase()) || 
      (list.description && list.description.toLowerCase().includes(searchTerm.toLowerCase()))) &&
      (filter === 'all' || 
       (filter === 'recent' && new Date(list.created_at) > new Date(Date.now() - 7 * 24 * 60 * 60 * 1000)) ||
       (filter === 'favorites' && list.is_favorite))
    )
    .sort((a, b) => {
      // Tri des listes
      if (sortBy === 'title') {
        return sortDirection === 'asc' 
          ? a.title.localeCompare(b.title) 
          : b.title.localeCompare(a.title);
      } else {
        // Tri par date
        return sortDirection === 'asc'
          ? new Date(a[sortBy]) - new Date(b[sortBy])
          : new Date(b[sortBy]) - new Date(a[sortBy]);
      }
    });

  /**
   * ðŸ‡¬ðŸ‡§ Handle creation of a new list
   * 
   * ðŸ‡«ðŸ‡· Gestion de la crÃ©ation d'une nouvelle liste
   */
  const handleCreateList = async (e) => {
    e.preventDefault();

    if (!newList.title.trim()) {
      setError("Le titre de la liste est requis");
      return;
    }

    try {
      const response = await createMylist({
        user_id: user.id,
        title: newList.title,
        description: newList.description || null,
      });

      setMylists([...mylists, response.data]);
      setNewList({ title: '', description: '' });
      setShowCreateModal(false);
      setSuccessMessage("Liste crÃ©Ã©e avec succÃ¨s!");
      setTimeout(() => setSuccessMessage(null), 3000);
    } catch (err) {
      console.error('Erreur lors de la crÃ©ation de la liste', err);
      setError('Impossible de crÃ©er la liste. Veuillez rÃ©essayer.');
    }
  };

  /**
   * ðŸ‡¬ðŸ‡§ Handle deletion of a list
   * 
   * ðŸ‡«ðŸ‡· Gestion de la suppression d'une liste
   */
  const handleDeleteList = async () => {
    if (!listToDelete) return;

    try {
      await deleteMylist(listToDelete.id);
      setMylists(mylists.filter(list => list.id !== listToDelete.id));
      setListToDelete(null);
      setShowDeleteModal(false);
      setSuccessMessage("Liste supprimÃ©e avec succÃ¨s");
      setTimeout(() => setSuccessMessage(null), 3000);
    } catch (err) {
      console.error('Erreur lors de la suppression de la liste', err);
      setError('Impossible de supprimer la liste. Veuillez rÃ©essayer.');
    }
  };

  /**
   * ðŸ‡¬ðŸ‡§ Open deletion confirmation modal
   * 
   * ðŸ‡«ðŸ‡· Ouverture de la modale de confirmation de suppression
   */
  const openDeleteModal = (list) => {
    setListToDelete(list);
    setShowDeleteModal(true);
  };

  /**
   * ðŸ‡¬ðŸ‡§ Format date to French locale
   * 
   * ðŸ‡«ðŸ‡· Formatage de la date au format franÃ§ais
   */
  const formatDate = (dateString) => {
    const options = { year: 'numeric', month: 'long', day: 'numeric' };
    return new Date(dateString).toLocaleDateString('fr-FR', options);
  };

  /**
   * ðŸ‡¬ðŸ‡§ Handle sorting change
   * 
   * ðŸ‡«ðŸ‡· Gestion du changement de tri
   */
  const handleSortChange = (newSortBy) => {
    if (sortBy === newSortBy) {
      // Si on clique sur le mÃªme critÃ¨re, on inverse la direction
      setSortDirection(sortDirection === 'asc' ? 'desc' : 'asc');
    } else {
      // Sinon on change le critÃ¨re et on met la direction par dÃ©faut
      setSortBy(newSortBy);
      setSortDirection('desc');
    }
  };

  /**
   * ðŸ‡¬ðŸ‡§ Custom CSS styles for animations and hover effects
   * 
   * ðŸ‡«ðŸ‡· Styles CSS personnalisÃ©s pour les animations et effets au survol
   */
  const customStyles = {
    card: {
      transition: 'all 0.3s ease',
      marginBottom: '1.5rem'
    },
    cardHover: {
      transform: 'translateY(-8px)',
      boxShadow: '0 10px 20px rgba(0, 0, 0, 0.15)'
    },
    detailsButton: {
      transition: 'all 0.3s ease'
    },
    detailsButtonHover: {
      backgroundColor: '#0d6efd',
      color: 'white'
    }
  };

  return (
    <>
      <main className="container-fluid py-4">
        {/* Titre de la page */}
        <div>          
          <h1 className="display-6 fw-bold mb-4 title-swim">Mes Carnets</h1>
        </div>

        {/* Bouton de crÃ©ation et description */}
        <div className="d-flex flex-column flex-md-row justify-content-between align-items-md-center mb-5">
          <p className="mb-3 mb-md-0">Mes listes : - Exercices, SÃ©ances et Plans.</p>

           <div className="mb-4">
                    <Link to="/user/mylists/new" className="btn btn-danger">
                      <FaPlus className="me-2" /> Nouveau Carnet
                    </Link>
                  </div>
        </div>

        {/* Messages de succÃ¨s */}
        {successMessage && (
          <div className="alert alert-success alert-dismissible fade show mb-4" role="alert">
            <FaCheck className="me-2" /> {successMessage}
            <button type="button" className="btn-close" onClick={() => setSuccessMessage(null)}></button>
          </div>
        )}

        {/* Messages d'erreur */}
        {error && (
          <div className="alert alert-danger alert-dismissible fade show mb-4" role="alert">
            <FaExclamationTriangle className="me-2" /> {error}
            <button type="button" className="btn-close" onClick={() => setError(null)}></button>
          </div>
        )}

        {/* Carte principale contenant les listes */}
        <div className="card shadow-sm mb-5 border-0">
          <div className="card-body p-4">
            {/* Barre de recherche et filtres */}
            <div className="row g-4 mb-5">
              <div className="col-md-6">
                <div className="input-group">
                  <span className="input-group-text bg-light border-light">
                    <FaSearch className="text-muted" />
                  </span>
                  <input
                    type="text"
                    className="form-control border-light bg-light"
                    placeholder="Rechercher une liste..."
                    value={searchTerm}
                    onChange={(e) => setSearchTerm(e.target.value)}
                  />
                </div>
              </div>

              <div className="col-md-6">
                <div className="d-flex gap-3 justify-content-md-end">
                  {/* Filtre */}
                  <div className="dropdown">
                    <button className="btn btn-outline-secondary dropdown-toggle" type="button" id="filterDropdown" data-bs-toggle="dropdown" aria-expanded="false">
                      <FaFilter className="me-1" /> {filter === 'all' ? 'Toutes' : filter === 'recent' ? 'RÃ©centes' : 'Favorites'}
                    </button>
                    <ul className="dropdown-menu" aria-labelledby="filterDropdown">
                      <li><button className="dropdown-item" onClick={() => setFilter('all')}>Toutes</button></li>
                      <li><button className="dropdown-item" onClick={() => setFilter('recent')}>RÃ©centes</button></li>
                      <li><button className="dropdown-item" onClick={() => setFilter('favorites')}>Favorites</button></li>
                    </ul>
                  </div>

                  {/* Tri */}
                  <div className="dropdown">
                    <button className="btn btn-outline-secondary dropdown-toggle" type="button" id="sortDropdown" data-bs-toggle="dropdown" aria-expanded="false">
                      <FaSort className="me-1" /> Trier par
                    </button>
                    <ul className="dropdown-menu" aria-labelledby="sortDropdown">
                      <li>
                        <button 
                          className={`dropdown-item ${sortBy === 'title' ? 'active' : ''}`} 
                          onClick={() => handleSortChange('title')}
                        >
                          {sortBy === 'title' && sortDirection === 'asc' ? <FaSortAlphaDown className="me-2" /> : <FaSortAlphaUp className="me-2" />}
                          Titre
                        </button>
                      </li>
                      <li>
                        <button 
                          className={`dropdown-item ${sortBy === 'created_at' ? 'active' : ''}`} 
                          onClick={() => handleSortChange('created_at')}
                        >
                          <FaCalendarAlt className="me-2" />
                          Date de crÃ©ation
                        </button>
                      </li>
                      <li>
                        <button 
                          className={`dropdown-item ${sortBy === 'updated_at' ? 'active' : ''}`} 
                          onClick={() => handleSortChange('updated_at')}
                        >
                          <FaCalendarAlt className="me-2" />
                          DerniÃ¨re modification
                        </button>
                      </li>
                    </ul>
                  </div>

                  {/* Boutons de vue */}
                  <div className="btn-group" role="group" aria-label="Mode d'affichage">
                    <button 
                      type="button" 
                      className={`btn btn-outline-secondary ${viewMode === 'grid' ? 'active' : ''}`}
                      onClick={() => setViewMode('grid')}
                    >
                      <i className="bi bi-grid"></i>
                    </button>
                    <button 
                      type="button" 
                      className={`btn btn-outline-secondary ${viewMode === 'list' ? 'active' : ''}`}
                      onClick={() => setViewMode('list')}
                    >
                      <i className="bi bi-list"></i>
                    </button>
                  </div>
                </div>
              </div>
            </div>

            {/* 
              ðŸ‡¬ðŸ‡§ Conditional rendering based on loading state and lists availability
              ðŸ‡«ðŸ‡· Affichage conditionnel selon l'Ã©tat de chargement et la disponibilitÃ© des listes 
            */}
            {loading ? (
              <div className="text-center py-5">
                <div className="spinner-border text-primary" role="status">
                  <span className="visually-hidden">Chargement...</span>
                </div>
                <p className="mt-3">Chargement de vos listes personnelles...</p>
              </div>
            ) : filteredLists.length > 0 ? (
              viewMode === 'grid' ? (
                /* Vue en grille */
                <div className="row row-cols-1 row-cols-md-2 row-cols-lg-3 g-5">
                  {filteredLists.map((list) => (
                    <div key={list.id} className="col">
                      <div 
                        className="card h-100 border-0 shadow-sm"
                        style={customStyles.card}
                        onMouseOver={(e) => {
                          e.currentTarget.style.transform = 'translateY(-8px)';
                          e.currentTarget.style.boxShadow = '0 10px 20px rgba(0, 0, 0, 0.15)';
                        }}
                        onMouseOut={(e) => {
                          e.currentTarget.style.transform = 'none';
                          e.currentTarget.style.boxShadow = '0 .125rem .25rem rgba(0, 0, 0, .075)';
                        }}
                      >
                        <div className="card-body position-relative p-4">
                          {/* Menu d'actions */}
                          <div className="position-absolute top-0 end-0 mt-3 me-3">
                            <div className="dropdown">
                              <button 
                                className="btn btn-sm btn-light rounded-circle" 
                                type="button" 
                                id={`dropdownButton-${list.id}`} 
                                data-bs-toggle="dropdown" 
                                aria-expanded="false"
                              >
                                <i className="bi bi-three-dots-vertical"></i>
                              </button>
                              <ul className="dropdown-menu dropdown-menu-end" aria-labelledby={`dropdownButton-${list.id}`}>
                                <li>
                                  <Link to={`/user/mylists/${list.id}`} className="dropdown-item">
                                    <FaEye className="me-2 text-primary" /> Voir
                                  </Link>
                                </li>
                                <li>
                                  <button className="dropdown-item" onClick={() => navigate(`/user/mylists/${list.id}/edit`)}>
                                    <FaEdit className="me-2 text-success" /> Modifier
                                  </button>
                                </li>
                                <li>
                                  <button className="dropdown-item">
                                    <FaCopy className="me-2 text-info" /> Dupliquer
                                  </button>
                                </li>
                                <li>
                                  <button className="dropdown-item">
                                    <FaStar className="me-2 text-warning" /> Favori
                                  </button>
                                </li>
                                <li><hr className="dropdown-divider" /></li>
                                <li>
                                  <button 
                                    className="dropdown-item text-danger" 
                                    onClick={() => openDeleteModal(list)}
                                  >
                                    <FaTrash className="me-2" /> Supprimer
                                  </button>
                                </li>
                              </ul>
                            </div>
                          </div>

                          {/* En-tÃªte de la carte */}
                          <div className="d-flex align-items-center mb-4">
                            <div className="rounded-circle bg-primary bg-gradient text-white p-3 me-3">
                              <FaSwimmingPool />
                            </div>
                            <h2 className="card-title h5 mb-0">{list.title}</h2>
                          </div>

                          {/* Description de la liste */}
                          {list.description && (
                            <p className="card-text text-muted small mb-4">
                              {list.description.length > 100
                                ? `${list.description.substring(0, 100)}...`
                                : list.description}
                            </p>
                          )}

                          {/* Bouton de dÃ©tail avec effet au survol */}
                          <div className="mt-4">
                            <Link
                              to={`/user/mylists/${list.id}`}
                              className="btn btn-primary w-100"
                              style={customStyles.detailsButton}
                              onMouseOver={(e) => {
                                e.currentTarget.style.backgroundColor = '#0d6efd';
                                e.currentTarget.style.color = 'white';
                              }}
                              onMouseOut={(e) => {
                                e.currentTarget.style.backgroundColor = '';
                                e.currentTarget.style.color = '';
                              }}
                            >
                              Voir le dÃ©tail
                            </Link>
                          </div>
                        </div>

                        {/* Pied de carte avec date de crÃ©ation */}
                        <div className="card-footer bg-white p-3 text-center">
                          <small className="text-muted">
                            CrÃ©Ã©e le {formatDate(list.created_at)}
                          </small>
                        </div>
                      </div>
                    </div>
                  ))}
                </div>
              ) : (
                /* Vue en liste */
                <div className="list-group">
                  {filteredLists.map((list) => (
                    <div 
                      key={list.id} 
                      className="list-group-item list-group-item-action d-flex justify-content-between align-items-center border-0 mb-3 shadow-sm p-4"
                      style={customStyles.card}
                      onMouseOver={(e) => {
                        e.currentTarget.style.transform = 'translateY(-5px)';
                        e.currentTarget.style.boxShadow = '0 8px 15px rgba(0, 0, 0, 0.1)';
                        e.currentTarget.style.backgroundColor = '#f8f9fa';
                      }}
                      onMouseOut={(e) => {
                        e.currentTarget.style.transform = 'none';
                        e.currentTarget.style.boxShadow = '0 .125rem .25rem rgba(0, 0, 0, .075)';
                        e.currentTarget.style.backgroundColor = '';
                      }}
                    >
                      <div className="d-flex align-items-center">
                        <div className="rounded-circle bg-primary bg-gradient text-white p-3 me-4">
                          <FaSwimmingPool />
                        </div>
                        <div>
                          <h3 className="h5 mb-1">{list.title}</h3>
                          {list.description && (
                            <p className="card-text text-muted small mb-1">
                              {list.description.length > 150
                                ? `${list.description.substring(0, 150)}...`
                                : list.description}
                            </p>
                          )}
                          <small className="text-muted">
                            CrÃ©Ã©e le {formatDate(list.created_at)}
                          </small>
                        </div>
                      </div>
                      <div className="d-flex gap-3">
                        <Link 
                          to={`/user/mylists/${list.id}`} 
                          className="btn btn-sm btn-outline-primary"
                          style={customStyles.detailsButton}
                          onMouseOver={(e) => {
                            e.currentTarget.style.backgroundColor = '#0d6efd';
                            e.currentTarget.style.color = 'white';
                          }}
                          onMouseOut={(e) => {
                            e.currentTarget.style.backgroundColor = '';
                            e.currentTarget.style.color = '';
                          }}
                        >
                          <FaEye className="me-1 d-none d-sm-inline" /> Voir
                        </Link>
                        <button 
                          className="btn btn-sm btn-outline-success"
                          onClick={() => navigate(`/user/mylists/${list.id}/edit`)}
                        >
                          <FaEdit className="me-1 d-none d-sm-inline" /> Modifier
                        </button>
                        <button 
                          className="btn btn-sm btn-outline-danger"
                          onClick={() => openDeleteModal(list)}
                        >
                          <FaTrash className="me-1 d-none d-sm-inline" /> Supprimer
                        </button>
                      </div>
                    </div>
                  ))}
                </div>
              )
            ) : (
              /* Ã‰tat vide - aucune liste trouvÃ©e */
              <div className="text-center py-5 bg-light rounded">
                <div className="bg-white rounded-circle p-4 d-inline-block mb-4 shadow-sm">
                  <FaSwimmer className="text-muted fs-1" />
                </div>
                <p className="text-muted mb-4">
                  {searchTerm
                    ? "Aucune liste ne correspond Ã  votre recherche."
                    : filter !== 'all'
                      ? `Vous n'avez pas de listes ${filter === 'recent' ? 'rÃ©centes' : 'favorites'}.`
                      : "Vous n'avez pas encore crÃ©Ã© de liste personnelle."}
                </p>
                <button 
                  className="btn btn-primary" 
                  onClick={() => setShowCreateModal(true)}
                >
                  <FaPlus className="me-2" /> CrÃ©er ma premiÃ¨re liste
                </button>
              </div>
            )}
          </div>
        </div>


{/* 
  Guide d'utilisation des carnets d'entraÃ®nement 
  Nouvelle section ajoutÃ©e avec des cartes thÃ©matiques
*/}
<section className="mb-5">
  <div className="card border-0 shadow-sm">
    <div className="btn btn-danger btn-lg btn-block">
      <h2 className="h4 mb-0">
        <FaBook className="me-2" /> Guide d'utilisation des carnets d'entraÃ®nement
      </h2>
    </div>
    <div className="card-body p-4">
      <div className="row mb-4">
        <div className="col-lg-8 mx-auto">
          <div className="alert alert-info d-flex">
            <FaInfoCircle className="me-3 fs-5 mt-1" />
            <div>
              <p className="mb-0">CrÃ©ez et personnalisez vos carnets d'entraÃ®nement en quelques Ã©tapes simples pour organiser efficacement vos programmes de natation.</p>
            </div>
          </div>
        </div>
      </div>

      <div className="row row-cols-1 row-cols-md-2 row-cols-lg-4 g-4 mb-4">
        {/* Carte 1: CrÃ©ation */}
        <div className="col">
