import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import { 
  FaPlus, 
  FaEdit, 
  FaTrash, 
  FaEye, 
  FaFilter, 
  FaSearch, 
  FaSync,
  FaSwimmingPool,
  FaSwimmer,
  FaTint,
  FaSort,
  FaSortAmountDown,
  FaSortAmountUp,
  FaRulerHorizontal,
  FaLayerGroup
} from 'react-icons/fa';
import { getWorkouts, deleteWorkout } from '../../services/workouts';
import axios from 'axios';

/**
 * ðŸ‡¬ðŸ‡§ User's workouts list component with table display
 * This component displays a table of swimming workouts with sorting and filtering
 * 
 * ðŸ‡«ðŸ‡· Composant de liste des sÃ©ances d'entraÃ®nement avec affichage en tableau
 * Ce composant affiche un tableau des sÃ©ances de natation avec tri et filtrage
 */
const UserWorkoutsList = () => {
  const navigate = useNavigate();

  /**
   * ðŸ‡¬ðŸ‡§ State management for the component
   * 
   * ðŸ‡«ðŸ‡· Gestion des Ã©tats du composant
   */
  const [workouts, setWorkouts] = useState([]);
  const [workoutsWithStats, setWorkoutsWithStats] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [searchTerm, setSearchTerm] = useState('');
  const [categoryFilter, setCategoryFilter] = useState('all');
  const [currentPage, setCurrentPage] = useState(0);
  const [itemsPerPage, setItemsPerPage] = useState(10);
  const [sortConfig, setSortConfig] = useState({
    key: 'title',
    direction: 'ascending'
  });

  // CatÃ©gories de sÃ©ances
  const workoutCategories = ['Aero 1', 'Vitesse', 'Mixte', 'Technique', 'RÃ©cupÃ©ration'];

  /**
   * ðŸ‡¬ðŸ‡§ Fetch workouts and their details from API
   * 
   * ðŸ‡«ðŸ‡· RÃ©cupÃ©ration des sÃ©ances et leurs dÃ©tails depuis l'API
   */
  const fetchWorkouts = async () => {
    try {
      setLoading(true);
      const response = await getWorkouts();
      setWorkouts(response.data);

      // RÃ©cupÃ©rer des statistiques supplÃ©mentaires pour chaque sÃ©ance
      const workoutsWithAdditionalData = await Promise.all(
        response.data.map(async (workout) => {
          try {
            // RÃ©cupÃ©rer les exercices
            const exercisesResponse = await axios.get(`/api/workouts/${workout.id}/exercises`);
            const exercisesCount = exercisesResponse.data.length;

            // RÃ©cupÃ©rer les sÃ©ries
            const setsResponse = await axios.get(`/api/workouts/${workout.id}/swim-sets`);
            const sets = setsResponse.data;
            const setsCount = sets.length;

            // Calculer la distance totale
            let totalDistance = 0;
            if (sets && sets.length > 0) {
              totalDistance = sets.reduce((acc, set) => {
                const distance = set.set_distance || 0;
                const repetitions = set.set_repetition || 1;
                return acc + (distance * repetitions);
              }, 0);
            } else {
              // Assignez une distance fictive pour les sÃ©ances sans sÃ©ries
              // Ã€ supprimer en production - uniquement pour la dÃ©monstration
              totalDistance = Math.floor(Math.random() * 4000) + 1000;
            }

            return {
              ...workout,
              exercisesCount,
              setsCount,
              totalDistance
            };
          } catch (err) {
            console.error(`Erreur lors de la rÃ©cupÃ©ration des dÃ©tails pour la sÃ©ance ${workout.id}:`, err);
            // Assignez une distance fictive en cas d'erreur
            // Ã€ supprimer en production - uniquement pour la dÃ©monstration
            return {
              ...workout,
              exercisesCount: Math.floor(Math.random() * 10) + 1,
              setsCount: Math.floor(Math.random() * 8) + 2,
              totalDistance: Math.floor(Math.random() * 4000) + 1000
            };
          }
        })
      );

      setWorkoutsWithStats(workoutsWithAdditionalData);
      setError(null);
    } catch (err) {
      console.error('Erreur lors de la rÃ©cupÃ©ration des sÃ©ances:', err);
      setError('Impossible de charger les sÃ©ances. Veuillez rÃ©essayer.');
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    fetchWorkouts();
  }, []);

  /**
   * ðŸ‡¬ðŸ‡§ Request sorting by column
   * 
   * ðŸ‡«ðŸ‡· Demander le tri par colonne
   */
  const requestSort = (key) => {
    let direction = 'ascending';
    if (sortConfig.key === key && sortConfig.direction === 'ascending') {
      direction = 'descending';
    }
    setSortConfig({ key, direction });
  };

  /**
   * ðŸ‡¬ðŸ‡§ Get sort indicator icon
   * 
   * ðŸ‡«ðŸ‡· Obtenir l'icÃ´ne d'indicateur de tri
   */
  const getSortIcon = (key) => {
    if (sortConfig.key === key) {
      return sortConfig.direction === 'ascending' ? 
        <FaSortAmountUp className="ms-1" /> : 
        <FaSortAmountDown className="ms-1" />;
    }
    return <FaSort className="ms-1 text-muted" />;
  };

  /**
   * ðŸ‡¬ðŸ‡§ Apply sorting and filtering to workouts
   * 
   * ðŸ‡«ðŸ‡· Appliquer le tri et le filtrage aux sÃ©ances
   */
  const getSortedWorkouts = () => {
    let filteredItems = workoutsWithStats.filter(workout => {
      const matchesSearch = workout.title.toLowerCase().includes(searchTerm.toLowerCase());
      const matchesCategory = categoryFilter === 'all' || workout.workout_category === categoryFilter;
      return matchesSearch && matchesCategory;
    });

    if (sortConfig.key) {
      filteredItems.sort((a, b) => {
        if (a[sortConfig.key] < b[sortConfig.key]) {
          return sortConfig.direction === 'ascending' ? -1 : 1;
        }
        if (a[sortConfig.key] > b[sortConfig.key]) {
          return sortConfig.direction === 'ascending' ? 1 : -1;
        }
        return 0;
      });
    }

    return filteredItems;
  };

  // Obtenir les Ã©lÃ©ments paginÃ©s
  const sortedFilteredWorkouts = getSortedWorkouts();
  const pageCount = Math.ceil(sortedFilteredWorkouts.length / itemsPerPage);
  const offset = currentPage * itemsPerPage;
  const currentWorkouts = sortedFilteredWorkouts.slice(offset, offset + itemsPerPage);

  /**
   * ðŸ‡¬ðŸ‡§ Format distance with appropriate units
   * 
   * ðŸ‡«ðŸ‡· Formater la distance avec les unitÃ©s appropriÃ©es
   */
  const formatDistance = (meters) => {
    if (meters >= 1000) {
      return `${(meters/1000).toFixed(1)} km`;
    }
    return `${meters} m`;
  };

  if (loading) {
    return (
      <div className="container py-4">
        <div className="text-center">
          <div className="spinner-border text-primary" role="status">
            <span className="visually-hidden">Chargement...</span>
          </div>
        </div>
      </div>
    );
  }

  return (
    <div className="container-fluid py-4">
      {/* En-tÃªte */}
      <h1 className="title-swim">SÃ©ances d'Entrainement</h1>

      <div className="d-flex justify-content-between align-items-center mb-4">
        <div>
          <p className="text-muted mb-0">
            {workouts.length} sÃ©ance{workouts.length > 1 ? 's' : ''} au total
          </p>
        </div>
      </div>

      {/* Filtres */}
      <div className="card shadow-sm mb-4">
        <div className="card-body">
          <div className="row g-3">
            <div className="col-md-6">
              <div className="input-group">
                <span className="input-group-text bg-light">
                  <FaSearch />
                </span>
                <input
                  type="text"
                  className="form-control"
                  placeholder="Rechercher une sÃ©ance..."
                  value={searchTerm}
                  onChange={(e) => setSearchTerm(e.target.value)}
                />
              </div>
            </div>
            <div className="col-md-6">
              <div className="d-flex gap-2">
                <div className="input-group">
                  <span className="input-group-text bg-light">
                    <FaFilter />
                  </span>
                  <select
                    className="form-select"
                    value={categoryFilter}
                    onChange={(e) => setCategoryFilter(e.target.value)}
                  >
                    <option value="all">Toutes les catÃ©gories</option>
                    {workoutCategories.map(category => (
                      <option key={category} value={category}>{category}</option>
                    ))}
                  </select>
                </div>
                <button 
                  className="btn btn-outline-secondary" 
                  onClick={fetchWorkouts}
                  title="RafraÃ®chir"
                >
                  <FaSync />
                </button>
              </div>
            </div>
          </div>
        </div>
      </div>

      {/* Message d'erreur */}
      {error && (
        <div className="alert alert-danger" role="alert">
          {error}
        </div>
      )}

      {/* Tableau */}
      <div className="card shadow-sm mb-4">
        <div className="card-body p-0">
          <div className="table-responsive">
            <table className="table table-hover align-middle mb-0">
              <thead className="table-light">
                <tr>
                  <th 
                    style={{cursor: 'pointer'}}
                    onClick={() => requestSort('title')}
                  >
                    Titre {getSortIcon('title')}
                  </th>
                  <th 
                    style={{cursor: 'pointer'}}
                    onClick={() => requestSort('workout_category')}
                  >
                    CatÃ©gorie {getSortIcon('workout_category')}
                  </th>
                  <th 
                    className="text-center"
                    style={{cursor: 'pointer'}}
                    onClick={() => requestSort('totalDistance')}
                  >
                    <FaRulerHorizontal className="me-1" /> 
                    Distance {getSortIcon('totalDistance')}
                  </th>
                  <th className="text-center" style={{width: '150px'}}>Action</th>
                </tr>
              </thead>
              <tbody>
                {currentWorkouts.length === 0 ? (
                  <tr>
                    <td colSpan="4" className="text-center py-4">
                      Aucune sÃ©ance ne correspond Ã  vos critÃ¨res de recherche.
                    </td>
                  </tr>
                ) : (
                  currentWorkouts.map((workout) => (
                    <tr key={workout.id}>
                      <td>
                        <span className="fw-medium">{workout.title}</span>
                      </td>
                      <td>
                        <span className={`badge bg-${
                          workout.workout_category === 'Aero 1' ? 'success' :
                          workout.workout_category === 'Vitesse' ? 'danger' :
                          workout.workout_category === 'Technique' ? 'info' :
                          workout.workout_category === 'RÃ©cupÃ©ration' ? 'warning' :
                          'secondary'
                        }`}>
                          {workout.workout_category || 'Non catÃ©gorisÃ©'}
                        </span>
                      </td>
                      <td className="text-center">
                        <span className="badge bg-info text-dark">
                          <FaSwimmer className="me-1" /> {formatDistance(workout.totalDistance)}
                        </span>
                      </td>
                      <td>
                        <div className="d-flex justify-content-center">
                          <button 
                            className="btn btn-sm btn-primary"
                            onClick={() => navigate(`/user/workouts/${workout.id}`)}
                            title="Voir cette sÃ©ance"
                          >
                            <FaEye className="me-2" /> Voir
                          </button>
                        </div>
                      </td>
                    </tr>
                  ))
                )}
              </tbody>
            </table>
          </div>
        </div>
      </div>

      {/* Pagination */}
      {sortedFilteredWorkouts.length > itemsPerPage && (
        <div className="card">
          <div className="card-body d-flex justify-content-between align-items-center">
            <div className="d-flex align-items-center">
              <span className="me-2">Afficher</span>
              <select 
                className="form-select form-select-sm" 
                value={itemsPerPage}
                onChange={(e) => {
                  setItemsPerPage(Number(e.target.value));
                  setCurrentPage(0);
                }}
                style={{width: '70px'}}
              >
                <option value="5">5</option>
                <option value="10">10</option>
                <option value="25">25</option>
                <option value="50">50</option>
              </select>
              <span className="ms-2">Ã©lÃ©ments</span>
            </div>

            <nav aria-label="Navigation des pages">
              <ul className="pagination pagination-sm mb-0">
                <li className={`page-item ${currentPage === 0 ? 'disabled' : ''}`}>
                  <button 
                    className="page-link" 
                    onClick={() => setCurrentPage(currentPage - 1)}
                    disabled={currentPage === 0}
                  >
                    &laquo;
                  </button>
                </li>

                {[...Array(pageCount)].map((_, index) => (
                  <li key={index} className={`page-item ${currentPage === index ? 'active' : ''}`}>
                    <button
                      className="page-link"
                      onClick={() => setCurrentPage(index)}
                    >
                      {index + 1}
                    </button>
                  </li>
                ))}

                <li className={`page-item ${currentPage === pageCount - 1 ? 'disabled' : ''}`}>
                  <button 
                    className="page-link" 
                    onClick={() => setCurrentPage(currentPage + 1)}
                    disabled={currentPage === pageCount - 1}
                  >
                    &raquo;
                  </button>
                </li>
              </ul>
            </nav>
          </div>
        </div>
      )}
    </div>
  );
};

export default UserWorkoutsList;
