import React, { useState, useEffect, useCallback } from 'react';
import { Play, Pause, Flag, RotateCcw, Trophy } from 'lucide-react';
import {
  LineChart, Line, XAxis, YAxis, CartesianGrid, Tooltip, ResponsiveContainer, Legend
} from 'recharts';

// ðŸŽ¨ Couleurs distinctes pour chaque nageur
const COLORS = ['#2563eb', '#dc2626', '#16a34a'];

// ðŸŠ Liste initiale des nageurs avec leurs Ã©tats
const INITIAL_SWIMMERS = [
  { id: 1, name: 'Swimmer 1', laps: [], finishTime: null },
  { id: 2, name: 'Swimmer 2', laps: [], finishTime: null },
  { id: 3, name: 'Swimmer 3', laps: [], finishTime: null }
];

export const Chronometer = () => {
  const [time, setTime] = useState(0); // â± Temps global du chronomÃ¨tre
  const [isRunning, setIsRunning] = useState(false); // â–¶ï¸ Statut du chronomÃ¨tre
  const [intervalId, setIntervalId] = useState(null); // â² ID de l'interval
  const [swimmers, setSwimmers] = useState(INITIAL_SWIMMERS); // ðŸŠ Ã‰tats des nageurs
  const [raceFinished, setRaceFinished] = useState(false); // ðŸ Statut de fin de course

  // ðŸ”„ Formater le temps (ms) en MM:SS.ms
  const formatTime = (ms) => {
    const minutes = Math.floor(ms / 60000);
    const seconds = Math.floor((ms % 60000) / 1000);
    const milliseconds = Math.floor((ms % 1000) / 10);
    return `${minutes.toString().padStart(2, '0')}:${seconds.toString().padStart(2, '0')}.${milliseconds.toString().padStart(2, '0')}`;
  };

  // ðŸ•“ Calculer l'Ã©cart entre le meilleur temps et un nageur donnÃ©
  const calculateTimeDifference = (finishTime, fastestTime) => {
    const diff = finishTime - fastestTime;
    return diff === 0 ? '-' : `+${formatTime(diff)}`;
  };

  // ðŸ“Š Calculer la moyenne des temps intermÃ©diaires pour un nageur
  const calculateAverageLap = (swimmerId) => {
    const swimmer = swimmers.find(s => s.id === swimmerId);
    if (!swimmer || swimmer.laps.length === 0) return '00:00.00';
    const avg = swimmer.laps.reduce((acc, lap) => acc + lap.splitTime, 0) / swimmer.laps.length;
    return formatTime(avg);
  };

  // â–¶ï¸â¸ GÃ©rer le dÃ©marrage/pause du chronomÃ¨tre
  const toggleTimer = useCallback(() => {
    if (raceFinished) return;
    if (!isRunning) {
      const id = window.setInterval(() => setTime(prev => prev + 10), 10);
      setIntervalId(id);
    } else {
      clearInterval(intervalId);
    }
    setIsRunning(prev => !prev);
  }, [isRunning, intervalId, raceFinished]);

  // ðŸ Enregistrer un tour pour un nageur
  const recordLap = (swimmerId) => {
    if (!isRunning || raceFinished) return;
    setSwimmers(prev =>
      prev.map(swimmer => {
        if (swimmer.id === swimmerId) {
          const lastLapTime = swimmer.laps.length > 0 ? swimmer.laps[swimmer.laps.length - 1].time : 0;
          return {
            ...swimmer,
            laps: [...swimmer.laps, {
              number: swimmer.laps.length + 1,
              time,
              splitTime: time - lastLapTime
            }]
          };
        }
        return swimmer;
      })
    );
  };

  // ðŸ Marquer un nageur comme ayant terminÃ© la course
  const finishSwimmer = (swimmerId) => {
    if (!isRunning) return;
    setSwimmers(prev =>
      prev.map(swimmer => {
        if (swimmer.id === swimmerId && swimmer.finishTime === null) {
          return { ...swimmer, finishTime: time };
        }
        return swimmer;
      })
    );
  };

  // ðŸ”„ RÃ©initialiser la course
  const reset = () => {
    clearInterval(intervalId);
    setTime(0);
    setSwimmers(INITIAL_SWIMMERS);
    setIsRunning(false);
    setIntervalId(null);
    setRaceFinished(false);
  };

  // ðŸ§¹ Nettoyage Ã  la destruction
  useEffect(() => {
    return () => {
      if (intervalId) clearInterval(intervalId);
    };
  }, [intervalId]);

  // ðŸ“ˆ GÃ©nÃ©rer les donnÃ©es du graphique
  const chartData = swimmers[0].laps.map((_, index) => {
    const data = { lap: index + 1 };
    swimmers.forEach(swimmer => {
      if (swimmer.laps[index]) {
        data[`swimmer${swimmer.id}`] = swimmer.laps[index].splitTime / 1000;
      }
    });
    return data;
  });

  // ðŸ† Calculer les rÃ©sultats finaux triÃ©s
  const getRaceResults = () => {
    const finished = swimmers
      .filter(s => s.finishTime !== null)
      .sort((a, b) => a.finishTime - b.finishTime);
    const fastestTime = finished[0]?.finishTime || 0;
    if (finished.length === swimmers.length) {
      setRaceFinished(true);
      clearInterval(intervalId);
      setIsRunning(false);
    }
    return finished.map(s => ({
      ...s,
      timeDiff: calculateTimeDifference(s.finishTime, fastestTime)
    }));
  };

  return (
    <div className="max-w-4xl mx-auto p-6 bg-white rounded-xl shadow-lg">
      {/* ðŸ–¥ Affichage principal du chronomÃ¨tre */}
      <div className="text-center mb-8">
        <h2 className="text-6xl font-mono font-bold mb-4">{formatTime(time)}</h2>

        {/* ðŸ•¹ Boutons principaux */}
        <div className="flex justify-center gap-4 mb-8">
          <button
            onClick={toggleTimer}
            disabled={raceFinished}
            className="flex items-center gap-2 px-6 py-3 bg-blue-600 text-white rounded-lg hover:bg-blue-700 transition-colors disabled:opacity-50"
          >
            {isRunning ? <Pause size={24} /> : <Play size={24} />}
            {isRunning ? 'Pause' : 'Start'}
          </button>
          <button
            onClick={reset}
            className="flex items-center gap-2 px-6 py-3 bg-red-600 text-white rounded-lg hover:bg-red-700 transition-colors"
          >
            <RotateCcw size={24} />
            Reset
          </button>
        </div>

        {/* ðŸ‘¤ Nageurs */}
        <div className="grid grid-cols-1 md:grid-cols-3 gap-4 mb-8">
          {swimmers.map((swimmer, index) => (
            <div key={swimmer.id} className="p-4 rounded-lg border-2" style={{ borderColor: COLORS[index] }}>
              <h3 className="text-lg font-semibold mb-3">{swimmer.name}</h3>
              <div className="flex gap-2 justify-center">
                <button
                  onClick={() => recordLap(swimmer.id)}
                  disabled={!isRunning || raceFinished || swimmer.finishTime !== null}
                  className="flex items-center gap-1 px-3 py-2 bg-green-600 text-white rounded-lg hover:bg-green-700"
                >
                  <Flag size={20} />
                  Lap
                </button>
                <button
                  onClick={() => finishSwimmer(swimmer.id)}
                  disabled={!isRunning || raceFinished || swimmer.finishTime !== null}
                  className="flex items-center gap-1 px-3 py-2 bg-yellow-600 text-white rounded-lg hover:bg-yellow-700"
                >
                  <Trophy size={20} />
                  Finish
                </button>
              </div>
              <div className="mt-3 text-sm">
                Laps: {swimmer.laps.length} | Avg: {calculateAverageLap(swimmer.id)}
              </div>
            </div>
          ))}
        </div>
      </div>

      {/* ðŸ RÃ©sultats */}
      {(raceFinished || swimmers.some(s => s.finishTime !== null)) && (
        <div className="mb-8 bg-gray-50 p-4 rounded-lg">
          <h3 className="text-xl font-semibold mb-4 flex items-center gap-2 justify-center">
            <Trophy size={24} className="text-yellow-500" />
            Final Rankings
          </h3>
          <table className="w-full">
            <thead className="bg-gray-100">
              <tr>
                <th className="px-4 py-2 text-left">Position</th>
                <th className="px-4 py-2 text-left">Swimmer</th>
                <th className="px-4 py-2 text-left">Final Time</th>
                <th className="px-4 py-2 text-left">Difference</th>
              </tr>
            </thead>
            <tbody>
              {getRaceResults().map((swimmer, index) => (
                <tr key={swimmer.id} className="border-b">
                  <td className="px-4 py-2 font-semibold">{index + 1}</td>
                  <td className="px-4 py-2">{swimmer.name}</td>
                  <td className="px-4 py-2 font-mono">{formatTime(swimmer.finishTime)}</td>
                  <td className="px-4 py-2 font-mono">{swimmer.timeDiff}</td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      )}

      {/* ðŸ“Š Graphique de progression */}
      {chartData.length > 0 && (
        <div className="h-64">
          <h3 className="text-xl font-semibold mb-4">Lap Time Progression</h3>
          <ResponsiveContainer width="100%" height="100%">
            <LineChart data={chartData}>
              <CartesianGrid strokeDasharray="3 3" />
              <XAxis dataKey="lap" />
              <YAxis />
              <Tooltip />
              <Legend />
              {swimmers.map((swimmer, index) => (
                <Line
                  key={swimmer.id}
                  type="monotone"
                  dataKey={`swimmer${swimmer.id}`}
                  name={swimmer.name}
                  stroke={COLORS[index]}
                  strokeWidth={2}
                  dot={{ fill: COLORS[index] }}
                />
              ))}
            </LineChart>
          </ResponsiveContainer>
        </div>
      )}
    </div>
  );
};
