import React, { useState, useEffect } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import { 
  FaSwimmingPool, 
  FaSearch, 
  FaFilter, 
  FaSignInAlt, 
  FaUserPlus,
  FaLock,
  FaEye,
  FaSort,
  FaRulerHorizontal,
  FaLayerGroup,
  FaClock,
  FaSwimmer,
  FaRegSadTear
} from 'react-icons/fa';
import Header from '../../components/template/Header';
import Footer from '../../components/template/Footer';
import { getWorkouts } from '../../services/workouts';
import axios from 'axios';
import useAuth from '../../hooks/useAuth';
import RandomBanner from '../../components/template/RandomBanner';


/**
 * ðŸ‡¬ðŸ‡§ Swimming workouts list component with card display
 * This component displays a list of swimming workouts as cards
 * Only authenticated users can access the complete content
 * 
 * ðŸ‡«ðŸ‡· Composant de liste de sÃ©ances d'entraÃ®nement de natation avec affichage en cartes
 * Ce composant affiche une liste de sÃ©ances d'entraÃ®nement sous forme de cartes
 * Seuls les utilisateurs authentifiÃ©s peuvent accÃ©der au contenu complet
 */
const SwimWorkout = () => {
  const navigate = useNavigate();
  const { isAuthenticated, isLoading: authLoading } = useAuth();

  const [workouts, setWorkouts] = useState([]);
  const [workoutsWithStats, setWorkoutsWithStats] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [searchTerm, setSearchTerm] = useState('');
  const [categoryFilter, setCategoryFilter] = useState('all');

  // CatÃ©gories de sÃ©ances
  const workoutCategories = ['Aero 1', 'Vitesse', 'Mixte', 'Technique', 'RÃ©cupÃ©ration'];

  /**
   * ðŸ‡¬ðŸ‡§ Fetch workouts and their details from API
   * 
   * ðŸ‡«ðŸ‡· RÃ©cupÃ©ration des sÃ©ances et leurs dÃ©tails depuis l'API
   */
  useEffect(() => {
    const fetchWorkouts = async () => {
      try {
        setLoading(true);
        const response = await getWorkouts();

        // Pour les visiteurs non authentifiÃ©s, limiter Ã  6 sÃ©ances comme aperÃ§u
        const workoutsToShow = isAuthenticated 
          ? response.data 
          : response.data.slice(0, 6);

        setWorkouts(workoutsToShow);

        // RÃ©cupÃ©rer des statistiques supplÃ©mentaires pour chaque sÃ©ance
        const workoutsWithAdditionalData = await Promise.all(
          workoutsToShow.map(async (workout) => {
            try {
              // RÃ©cupÃ©rer les exercices
              const exercisesResponse = await axios.get(`http://127.0.0.1:8000/api/workouts/${workout.id}/exercises`);
              const exercisesCount = exercisesResponse.data.length;

              // RÃ©cupÃ©rer les sÃ©ries
              const setsResponse = await axios.get(`http://127.0.0.1:8000/api/workouts/${workout.id}/swim-sets`);
              const sets = setsResponse.data;
              const setsCount = sets.length;

              // Calculer la distance totale
              const totalDistance = sets.reduce((acc, set) => {
                return acc + ((set.set_distance || 0) * (set.set_repetition || 1));
              }, 0);

              // Calculer la durÃ©e approximative
              // En supposant 1 minute par 100m avec un temps de repos
              const estimatedDuration = Math.ceil(totalDistance / 100) + 
                                       sets.reduce((acc, set) => acc + (set.rest_time || 0)/60, 0);

              return {
                ...workout,
                exercisesCount,
                setsCount,
                totalDistance,
                estimatedDuration
              };
            } catch (err) {
              console.error(`Erreur lors de la rÃ©cupÃ©ration des dÃ©tails pour la sÃ©ance ${workout.id}:`, err);
              return {
                ...workout,
                exercisesCount: 0,
                setsCount: 0,
                totalDistance: 0,
                estimatedDuration: 0
              };
            }
          })
        );

        setWorkoutsWithStats(workoutsWithAdditionalData);
        setError(null);
      } catch (err) {
        console.error('Erreur lors de la rÃ©cupÃ©ration des sÃ©ances:', err);
        setError('Pour accÃ©der Ã  ce contenu, veuillez vous connecter et vÃ©rifier que votre profil est complet...');
      } finally {
        setLoading(false);
      }
    };

    fetchWorkouts();
  }, [isAuthenticated]);

  /**
   * ðŸ‡¬ðŸ‡§ Filter workouts based on search term and category
   * 
   * ðŸ‡«ðŸ‡· Filtrer les sÃ©ances selon le terme de recherche et la catÃ©gorie
   */
  const filteredWorkouts = workoutsWithStats.filter(workout => {
    const matchesSearch = workout.title.toLowerCase().includes(searchTerm.toLowerCase()) ||
                         (workout.description && workout.description.toLowerCase().includes(searchTerm.toLowerCase()));
    const matchesCategory = categoryFilter === 'all' || workout.workout_category === categoryFilter;
    return matchesSearch && matchesCategory;
  });

  /**
   * ðŸ‡¬ðŸ‡§ Format distance with appropriate units
   * 
   * ðŸ‡«ðŸ‡· Formater la distance avec les unitÃ©s appropriÃ©es
   */
  const formatDistance = (meters) => {
    if (meters >= 1000) {
      return `${(meters/1000).toFixed(1)} km`;
    }
    return `${meters} m`;
  };

  /**
   * ðŸ‡¬ðŸ‡§ Format duration nicely
   * 
   * ðŸ‡«ðŸ‡· Formater la durÃ©e de faÃ§on Ã©lÃ©gante
   */
  const formatDuration = (minutes) => {
    const mins = Math.round(minutes);
    if (mins >= 60) {
      const hours = Math.floor(mins / 60);
      const remainingMins = mins % 60;
      return `${hours}h ${remainingMins > 0 ? remainingMins + 'min' : ''}`;
    }
    return `${mins} min`;
  };

  /**
   * ðŸ‡¬ðŸ‡§ Get color for category badge
   * 
   * ðŸ‡«ðŸ‡· Obtenir la couleur du badge pour la catÃ©gorie
   */
  const getCategoryBadgeColor = (category) => {
    switch (category) {
      case 'Aero 1': return 'success';
      case 'Vitesse': return 'danger';
      case 'Technique': return 'info';
      case 'RÃ©cupÃ©ration': return 'warning';
      case 'Mixte': return 'primary';
      default: return 'secondary';
    }
  };

  /**
   * ðŸ‡¬ðŸ‡§ Format description for preview
   * 
   * ðŸ‡«ðŸ‡· Formater la description pour l'aperÃ§u
   */
  const formatDescriptionPreview = (description, maxLength = 100) => {
    if (!description) return "Aucune description disponible";
    return description.length > maxLength 
      ? description.substring(0, maxLength) + '...'
      : description;
  };

  // Show loading spinner while checking authentication or loading data
  if (authLoading || loading) {
    return (
      <>
        <Header />
        <main className="container py-5">
          <div className="text-center">
            <div className="spinner-border text-primary" role="status">
              <span className="visually-hidden">Chargement...</span>
            </div>
            <p className="mt-3">Chargement des donnÃ©es...</p>
          </div>
        </main>
        <Footer />
      </>
    );
  }

  return (
    <>
      <Header />
       {/* BanniÃ¨re */}
       <section className="bg-primary text-white py-5">
          <div className="container">
            <div className="row align-items-center">
            <h1 className="title-swim display-4 fw-bold mb-4">Plans d'EntraÃ®nement</h1>

              <div className="col-lg-6">
                <p className="lead mb-4">
                Plongez dans l'eau avec confiance et atteignez vos objectifs grÃ¢ce Ã  notre application de plans d'entraÃ®nement natation. 
                Que vous soyez un nageur dÃ©butant ou chevronnÃ©, notre application vous offre un accompagnement personnalisÃ© pour optimiser vos performances dans l'eau.                </p>

              </div>
              <div className="col-lg-6 text-center mt-4 mt-lg-0">
                <FaSwimmer className="display-1" />
              </div>
            </div>
          </div>
        </section>

      <main className="container-fluid py-4">


        <h1 className="title-swim mb-4">SÃ©ances d'EntraÃ®nement</h1>

        {/* Filtres de recherche */}
        <div className="card shadow-sm mb-4">
          <div className="card-body">
            <div className="row g-3">
              <div className="col-md-6">
                <div className="input-group">
                  <span className="input-group-text bg-light">
                    <FaSearch />
                  </span>
                  <input
                    type="text"
                    className="form-control"
                    placeholder="Rechercher une sÃ©ance..."
                    value={searchTerm}
                    onChange={(e) => setSearchTerm(e.target.value)}
                  />
                </div>
              </div>
              <div className="col-md-6">
                <div className="input-group">
                  <span className="input-group-text bg-light">
                    <FaFilter />
                  </span>
                  <select
                    className="form-select"
                    value={categoryFilter}
                    onChange={(e) => setCategoryFilter(e.target.value)}
                  >
                    <option value="all">Toutes les catÃ©gories</option>
                    {workoutCategories.map(category => (
                      <option key={category} value={category}>{category}</option>
                    ))}
                  </select>
                </div>
              </div>
            </div>
          </div>
        </div>

        {/* Message d'erreur */}
        {error && (
          <div className="alert alert-danger" role="alert">
            {error}
          </div>
        )}

        {/* Affichage des cartes de sÃ©ances */}
        <div className="row row-cols-1 row-cols-md-2 row-cols-lg-3 g-4">
          {filteredWorkouts.length === 0 ? (
            <div className="col-12">

            </div>
          ) : (
            filteredWorkouts.map(workout => (
              <div key={workout.id} className="col">
                <div className="card h-100 shadow-sm hover-card">
                  {/* En-tÃªte de la carte */}
                  <div className="card-header bg-light">
                    <div className="d-flex justify-content-between align-items-center">
                      <span className={`badge bg-${getCategoryBadgeColor(workout.workout_category)}`}>
                        {workout.workout_category || 'Non catÃ©gorisÃ©'}
                      </span>
                      <small className="text-muted">ID: {workout.id}</small>
                    </div>
                  </div>

                  {/* Corps de la carte */}
                  <div className="card-body">
                    <h5 className="card-title mb-3">{workout.title}</h5>
                    <p className="card-text small mb-3">
                      {formatDescriptionPreview(workout.description)}
                    </p>

                    <div className="row g-2 mb-3">
                      <div className="col-6">
                        <div className="bg-light rounded p-2 text-center">
                          <div className="d-flex align-items-center justify-content-center mb-1">
                            <FaRulerHorizontal className="text-primary me-1" />
                            <small className="fw-medium">Distance</small>
                          </div>
                          <span className="badge bg-primary">
                            {formatDistance(workout.totalDistance)}
                          </span>
                        </div>
                      </div>
                      <div className="col-6">
                        <div className="bg-light rounded p-2 text-center">
                          <div className="d-flex align-items-center justify-content-center mb-1">
                            <FaClock className="text-primary me-1" />
                            <small className="fw-medium">DurÃ©e</small>
                          </div>
                          <span className="badge bg-secondary">
                            {formatDuration(workout.estimatedDuration)}
                          </span>
                        </div>
                      </div>
                    </div>

                    <div className="d-flex justify-content-between align-items-center">
                      <div className="d-flex align-items-center">
                        <FaLayerGroup className="text-primary me-1" />
                        <small className="text-muted">
                          {workout.setsCount} sÃ©ries
                        </small>
                      </div>
                      <div className="d-flex align-items-center">
                        <FaSwimmingPool className="text-primary me-1" />
                        <small className="text-muted">
                          {workout.exercisesCount} exercices
                        </small>
                      </div>
                    </div>
                  </div>

                  {/* Pied de la carte */}
                  <div className="card-footer bg-white border-top-0">
                    {isAuthenticated ? (
                      <Link 
                        to={`/user/workouts/${workout.id}`} 
                        className="btn btn-outline-primary w-100"
                      >
                        <FaEye className="me-2" /> Voir les dÃ©tails
                      </Link>
                    ) : (
                      <button 
                        className="btn btn-outline-primary w-100"
                        onClick={() => navigate('/login')}
                      >
                        <FaLock className="me-2" /> Connexion requise
                      </button>
                    )}
                  </div>
                </div>
              </div>
            ))
          )}
        </div>

        {/* Section d'incitation Ã  l'inscription pour visiteurs non connectÃ©s */}
        {!isAuthenticated && (
          <div className="card mt-5 shadow-sm border-0">
            <div className="card-body p-5 text-center">
              <FaLock className="text-muted mb-4" size={40} />
              <h2 className="mb-4">AccÃ©dez Ã  toutes nos sÃ©ances d'entraÃ®nement</h2>
              <p className="lead mb-4">
                Cette page n'affiche qu'un aperÃ§u limitÃ© des sÃ©ances disponibles pour les membres.
              </p>
              <p className="mb-4">
                Inscrivez-vous ou connectez-vous pour accÃ©der Ã  notre bibliothÃ¨que complÃ¨te de sÃ©ances d'entraÃ®nement,
                incluant des programmes structurÃ©s, des sÃ©ries dÃ©taillÃ©es et un suivi personnalisÃ©.
              </p>
              <div className="d-flex justify-content-center gap-3">
                <Link to="/login" className="btn btn-primary">
                  <FaSignInAlt className="me-2" /> Se connecter
                </Link>
                <Link to="/register" className="btn btn-outline-primary">
                  <FaUserPlus className="me-2" /> S'inscrire
                </Link>
              </div>
            </div>
          </div>
        )}
      </main>
      <RandomBanner />

      <Footer />
    </>
  );
};

export default SwimWorkout;
