  /**
   * üá¨üáß Get icon based on item type
   * 
   * üá´üá∑ Obtenir l'ic√¥ne en fonction du type d'√©l√©ment
   */
  const getItemIcon = (itemType) => {
    switch(itemType) {
      case 'exercise':
        return <FaDumbbell className="text-danger" />;
      case 'workout':
        return <FaSwimmer className="text-primary" />;
      case 'plan':
        return <FaCalendarAlt className="text-success" />;
      case 'swim_set':
        return <FaSwimmingPool className="text-info" />;
      default:
        return <FaListAlt className="text-secondary" />;
    }
  };

  /**
   * üá¨üáß Get color based on item type
   * 
   * üá´üá∑ Obtenir la couleur en fonction du type d'√©l√©ment
   */
  const getItemColor = (itemType) => {
    switch(itemType) {
      case 'exercise':
        return 'danger';
      case 'workout':
        return 'primary';
      case 'plan':
        return 'success';
      case 'swim_set':
        return 'info';
      default:
        return 'secondary';
    }
  };

  return (
    <>
      <main className="container py-5">
        {/* Bouton de retour */}
        <div className="mb-4">
          <Link to="/user/mylists" className="btn btn-success">
            <FaArrowLeft className="me-2" /> Retour aux carnets
          </Link>
        </div>

        {/* Carte principale du formulaire */}
        <div className="card shadow-sm border-0 mb-5">
          <div className="title-swim">
            <h1>
              {id ? 'Modifier le carnet' : 'Nouveau Carnet'}
            </h1>
          </div>
          <div className="card-body p-4">
            {/* Messages de succ√®s */}
            {successMessage && (
              <div className="alert alert-success alert-dismissible fade show" role="alert">
                <FaInfoCircle className="me-2" /> {successMessage}
                <button type="button" className="btn-close" onClick={() => setSuccessMessage(null)} aria-label="Close"></button>
              </div>
            )}

            {/* Messages d'erreur */}
            {error && (
              <div className="alert alert-danger alert-dismissible fade show" role="alert">
                <FaExclamationTriangle className="me-2" /> {error}
                <button type="button" className="btn-close" onClick={() => setError(null)} aria-label="Close"></button>
              </div>
            )}

            {/* Affichage conditionnel selon l'√©tat de chargement */}
            {loading ? (
              <div className="text-center py-5">
                <div className="spinner-border text-primary" role="status">
                  <span className="visually-hidden">Chargement...</span>
                </div>
                <p className="mt-3">Chargement des donn√©es du carnet...</p>
              </div>
            ) : (
              <form onSubmit={handleSubmit}>
                <div className="row">
                  {/* Champs du formulaire */}
                  <div className="col-12">
                    <div className="mb-4">
                      <label htmlFor="title" className="form-label">
                        <FaSwimmingPool className="me-2 text-primary" /> 
                        Titre <span className="text-danger">*</span>
                      </label>
                      <input
                        type="text"
                        className="form-control form-control-lg"
                        id="title"
                        name="title"
                        value={formData.title}
                        onChange={handleChange}
                        required
                        placeholder="Donnez un titre √† votre carnet"
                        disabled={submitting}
                      />
                    </div>

                    <div className="mb-4">
                      <label htmlFor="description" className="form-label">
                        <FaWater className="me-2 text-primary" /> 
                        Description
                      </label>
                      <textarea
                        className="form-control"
                        id="description"
                        name="description"
                        rows="4"
                        value={formData.description || ''}
                        onChange={handleChange}
                        placeholder="D√©crivez l'objectif de ce carnet (optionnel)"
                        disabled={submitting}
                      ></textarea>
                    </div>
                  </div>
                </div>

                {/* Boutons d'action */}
                <div className="d-flex gap-2 justify-content-end mt-4">
                  <button
                    type="submit"
                    className="btn btn-primary"
                    disabled={submitting || !formData.title.trim()}
                  >
                    {submitting ? (
                      <>
                        <span className="spinner-border spinner-border-sm me-2" role="status" aria-hidden="true"></span>
                        {id ? 'Mise √† jour...' : 'Cr√©ation...'}
                      </>
                    ) : (
                      <>
                        <FaSave className="me-2" /> 
                        {id ? 'Mettre √† jour' : 'Cr√©er le carnet'}
                      </>
                    )}
                  </button>
                </div>
              </form>
            )}
          </div>
        </div>

        {/* NOUVELLE SECTION: √âl√©ments composant le carnet */}
        {id && (
          <div className="card shadow-sm border-0 mb-5">
            <div className="card-header bg-light">
              <div className="d-flex justify-content-between align-items-center">
                <h2 className="h5 mb-0">
                  <FaListAlt className="me-2 text-primary" /> √âl√©ments composant ce carnet
                </h2>
                <Link to={`/user/mylists/${id}/add-items`} className="btn btn-sm btn-success">
                  <FaPlusCircle className="me-1" /> Ajouter des √©l√©ments
                </Link>
              </div>
            </div>
            <div className="card-body">
              {itemsLoading ? (
                <div className="text-center py-4">
                  <div className="spinner-border text-primary" role="status">
                    <span className="visually-hidden">Chargement...</span>
                  </div>
                  <p className="mt-2">Chargement des √©l√©ments du carnet...</p>
                </div>
              ) : listItems.length === 0 ? (
                <div className="alert alert-info">
                  <FaInfoCircle className="me-2" /> Ce carnet ne contient pas encore d'√©l√©ments. 
                  Ajoutez des exercices, des s√©ances ou des plans pour personnaliser votre carnet.
