                <div className="row row-cols-1 row-cols-md-2 row-cols-lg-3 g-4">
                  {listItems.map(item => (
                    <div key={item.id} className="col">
                      <div className="card h-100 border-0 shadow-sm">
                        <div className={`card-header bg-${getItemColor(item.item_type)} bg-opacity-10`}>
                          <div className="d-flex justify-content-between align-items-center">
                            <div className="d-flex align-items-center">
                              {getItemIcon(item.item_type)}
                              <span className={`badge bg-${getItemColor(item.item_type)} ms-2`}>
                                {item.typeName}
                              </span>
                            </div>
                            <button 
                              className="btn btn-sm btn-outline-danger"
                              onClick={() => handleRemoveItem(item.id)}
                              title="Retirer de ce carnet"
                            >
                              <FaMinusCircle />
                            </button>
                          </div>
                        </div>
                        <div className="card-body">
                          <h5 className="card-title">
                            {item.details.title || `${item.typeName} #${item.item_id}`}
                          </h5>
                          {item.details.description && (
                            <p className="card-text small text-muted">
                              {item.details.description.length > 100 
                                ? item.details.description.substring(0, 100) + '...' 
                                : item.details.description}
                            </p>
                          )}
                          {/* Affichage conditionnel selon le type d'√©l√©ment */}
                          {item.item_type === 'workout' && item.details.workout_category && (
                            <span className="badge bg-secondary me-1">
                              {item.details.workout_category}
                            </span>
                          )}
                          {item.item_type === 'exercise' && item.details.exercise_level && (
                            <span className="badge bg-secondary me-1">
                              {item.details.exercise_level}
                            </span>
                          )}
                          {item.item_type === 'swim_set' && item.details.set_distance && (
                            <span className="badge bg-info text-dark">
                              {item.details.set_distance}m x {item.details.set_repetition || 1}
                            </span>
                          )}
                        </div>
                        <div className="card-footer bg-white">
                          <Link 
                            to={`/${item.item_type === 'exercise' ? 'exercises' : 
                                  item.item_type === 'workout' ? 'workouts' : 
                                  item.item_type === 'plan' ? 'plans' : 
                                  item.item_type === 'swim_set' ? 'swim-sets' : ''}/${item.item_id}`} 
                            className="btn btn-sm btn-outline-primary w-100"
                          >
                            <FaEye className="me-1" /> Voir les d√©tails
                          </Link>
                        </div>
                      </div>
                    </div>
                  ))}
                </div>
              )}
            </div>
          </div>
        )}

        {/* Tableau des listes de l'utilisateur */}
        <div className="card shadow-sm border-0 mb-4">
          <div className="card-header bg-light">
            <h2 className="h5 mb-0">
              <FaTable className="me-2 text-primary" /> Mes carnets existants
            </h2>
          </div>
          <div className="card-body p-0">
            {listLoading ? (
              <div className="text-center py-5">
                <div className="spinner-border text-primary" role="status">
                  <span className="visually-hidden">Chargement...</span>
                </div>
                <p className="mt-3">Chargement de vos carnets...</p>
              </div>
            ) : userLists.length === 0 ? (
              <div className="alert alert-info m-4">
                <FaInfoCircle className="me-2" /> Vous n'avez pas encore cr√©√© de carnet.
              </div>
            ) : (
              <div className="table-responsive">
                <table className="table table-hover mb-0">
                  <thead className="table-light">
                    <tr>
                      <th scope="col">Titre</th>
