import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import { 
  FaPlus, 
  FaEdit, 
  FaTrash, 
  FaEye, 
  FaFilter, 
  FaSearch, 
  FaSync,
  FaSwimmer,
  FaStopwatch,
  FaRedoAlt,
  FaRulerHorizontal,
  FaSortAmountDown,
  FaSortAmountUp,
  FaDumbbell
} from 'react-icons/fa';
import { getSwimSets, deleteSwimSet } from '../../../services/swimsets';
import { getExercise } from '../../../services/exercises';

/**
 * üá¨üáß Admin page for managing swim sets
 * üá´üá∑ Page d'administration pour la gestion des s√©ries de natation
 */
const AdminSwimSetsPage = () => {
  const navigate = useNavigate();
  const [swimSets, setSwimSets] = useState([]);
  const [enrichedSwimSets, setEnrichedSwimSets] = useState([]);
  const [exercises, setExercises] = useState({});
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [searchTerm, setSearchTerm] = useState('');
  const [workoutFilter, setWorkoutFilter] = useState('all');
  const [exerciseFilter, setExerciseFilter] = useState('all');
  const [currentPage, setCurrentPage] = useState(0);
  const [itemsPerPage, setItemsPerPage] = useState(10);

  // Sort state
  const [sortConfig, setSortConfig] = useState({
    key: 'id',
    direction: 'ascending'
  });

  /**
   * üá¨üáß Fetch all swim sets and related exercise data
   * üá´üá∑ R√©cup√©rer toutes les s√©ries de natation et les donn√©es d'exercice associ√©es
   */
  const fetchSwimSets = async () => {
    try {
      setLoading(true);
      console.log('R√©cup√©ration des s√©ries de natation...');
      const response = await getSwimSets();

      if (!response.data) {
        console.error('R√©ponse API invalide:', response);
        setError('R√©ponse API invalide lors du chargement des s√©ries');
        setLoading(false);
        return;
      }

      setSwimSets(response.data);
      console.log(`${response.data.length} s√©ries r√©cup√©r√©es`);

      // Fetch exercise data for each swim set
      const uniqueExerciseIds = [...new Set(response.data.map(set => set.exercise_id))];
      console.log(`R√©cup√©ration des donn√©es pour ${uniqueExerciseIds.length} exercices uniques`);

      const exerciseData = {};

      // Fetch exercise details in parallel
      await Promise.all(uniqueExerciseIds.map(async (exerciseId) => {
        try {
          const exerciseResponse = await getExercise(exerciseId);
          exerciseData[exerciseId] = exerciseResponse.data;
        } catch (err) {
          console.error(`Error fetching exercise ${exerciseId}:`, err);
        }
      }));

      setExercises(exerciseData);

      // Enrich swim sets with calculated data
      const enriched = response.data.map(set => ({
        ...set,
        totalDistance: (set.set_distance || 0) * (set.set_repetition || 1),
        exerciseTitle: exerciseData[set.exercise_id]?.title || `Exercise #${set.exercise_id}`
      }));

      setEnrichedSwimSets(enriched);
      setError(null);
    } catch (err) {
      console.error('Erreur lors de la r√©cup√©ration des s√©ries:', err);
      setError('Impossible de charger les s√©ries. Veuillez r√©essayer.');
    } finally {
      setLoading(false);
    }
  };

  // Load data on component mount
  useEffect(() => {
    fetchSwimSets();
  }, []);

  /**
   * üá¨üáß Request sorting by a specific key
   * üá´üá∑ Demander le tri par une cl√© sp√©cifique
   */
  const requestSort = (key) => {
    let direction = 'ascending';
    if (sortConfig.key === key && sortConfig.direction === 'ascending') {
      direction = 'descending';
    }
    setSortConfig({ key, direction });
  };

  /**
   * üá¨üáß Get sorted and filtered items
   * üá´üá∑ Obtenir les √©l√©ments tri√©s et filtr√©s
   */
  const getSortedItems = () => {
    let filteredItems = enrichedSwimSets.filter(set => {
      const matchesSearch = set.id.toString().includes(searchTerm.toLowerCase()) || 
                            set.exerciseTitle.toLowerCase().includes(searchTerm.toLowerCase());
      const matchesWorkout = workoutFilter === 'all' || set.workout_id === parseInt(workoutFilter);
      const matchesExercise = exerciseFilter === 'all' || set.exercise_id === parseInt(exerciseFilter);
      return matchesSearch && matchesWorkout && matchesExercise;
    });

    if (sortConfig.key) {
      filteredItems.sort((a, b) => {
        if (a[sortConfig.key] < b[sortConfig.key]) {
          return sortConfig.direction === 'ascending' ? -1 : 1;
        }
        if (a[sortConfig.key] > b[sortConfig.key]) {
          return sortConfig.direction === 'ascending' ? 1 : -1;
        }
        return 0;
      });
    }

    return filteredItems;
  };

  // Get sorted and filtered data
  const sortedFilteredItems = getSortedItems();

  // Pagination logic
  const pageCount = Math.ceil(sortedFilteredItems.length / itemsPerPage);
  const offset = currentPage * itemsPerPage;
  const currentItems = sortedFilteredItems.slice(offset, offset + itemsPerPage);

  /**
   * üá¨üáß Delete a swim set
   * üá´üá∑ Supprimer une s√©rie de natation
   */
  const handleDelete = async (id) => {
    if (window.confirm('√ätes-vous s√ªr de vouloir supprimer cette s√©rie ?')) {
      try {
        await deleteSwimSet(id);
        console.log(`S√©rie ${id} supprim√©e avec succ√®s`);
        await fetchSwimSets();
      } catch (err) {
        console.error('Erreur lors de la suppression:', err);
        setError('Erreur lors de la suppression: ' + err.message);
      }
    }
  };

  /**
   * üá¨üáß Format time in seconds to MM:SS
   * üá´üá∑ Formater le temps en secondes au format MM:SS
   */
  const formatRestTime = (seconds) => {
    if (!seconds) return '00:00';
    const mins = Math.floor(seconds / 60);
    const secs = seconds % 60;
    return `${mins.toString().padStart(2, '0')}:${secs.toString().padStart(2, '0')}`;
  };

  // Get unique exercise options for filter
  const exerciseOptions = Object.values(exercises).map(ex => ({
    id: ex.id,
    title: ex.title
  }));

  /**
   * üá¨üáß Get sort icon based on current sort config
   * üá´üá∑ Obtenir l'ic√¥ne de tri en fonction de la configuration de tri actuelle
   */
  const getSortIcon = (key) => {
    if (sortConfig.key === key) {
      return sortConfig.direction === 'ascending' ? 
        <FaSortAmountUp className="ms-1" /> : 
        <FaSortAmountDown className="ms-1" />;
    }
    return null;
  };

  if (loading) {
    return (
      <div className="container py-4">
        <div className="text-center">
          <div className="spinner-border text-primary" role="status">
            <span className="visually-hidden">Chargement...</span>
          </div>
        </div>
      </div>
    );
  }

  return (
    <div className="container-fluid py-4">
      <div className="d-flex justify-content-between align-items-center mb-4">
        <div>
          <h1 className="h2 mb-0">Gestion des S√©ries</h1>
          <p className="text-muted mb-0">
            {swimSets.length} s√©rie{swimSets.length > 1 ? 's' : ''} au total
          </p>
        </div>
        <button 
          className="btn btn-primary d-flex align-items-center"
          onClick={() => navigate('/admin/swim-sets/new')}
        >
          <FaPlus className="me-2" /> Nouvelle s√©rie
        </button>
      </div>

      {/* Filters */}
      <div className="card shadow-sm mb-4">
        <div className="card-body">
          <div className="row g-3">
            <div className="col-md-4">
              <div className="input-group">
                <span className="input-group-text bg-light">
                  <FaSearch />
                </span>
                <input
                  type="text"
                  className="form-control"
                  placeholder="Rechercher une s√©rie..."
                  value={searchTerm}
                  onChange={(e) => setSearchTerm(e.target.value)}
                />
              </div>
            </div>
            <div className="col-md-4">
              <div className="input-group">
                <span className="input-group-text bg-light">
                  <FaFilter />
                </span>
                <select
                  className="form-select"
                  value={exerciseFilter}
                  onChange={(e) => setExerciseFilter(e.target.value)}
                >
                  <option value="all">Tous les exercices</option>
                  {exerciseOptions.map(exercise => (
                    <option key={exercise.id} value={exercise.id}>
                      {exercise.title}
                    </option>
                  ))}
                </select>
              </div>
            </div>
            <div className="col-md-4">
              <div className="d-flex gap-2">
                <button 
                  className="btn btn-outline-secondary" 
                  onClick={fetchSwimSets}
                  title="Rafra√Æchir"
                >
                  <FaSync />
                </button>
              </div>
            </div>
          </div>
        </div>
      </div>

      {error && (
        <div className="alert alert-danger" role="alert">
          {error}
        </div>
      )}

      {/* Table View */}
      <div className="card shadow-sm">
        <div className="card-body">
          <div className="table-responsive">
            <table className="table table-hover table-striped">
              <thead className="table-light">
                <tr>
                  <th 
                    className="cursor-pointer"
                    onClick={() => requestSort('id')}
                    style={{cursor: 'pointer'}}
                  >
                    ID {getSortIcon('id')}
                  </th>
                  <th 
                    className="cursor-pointer"
                    onClick={() => requestSort('set_repetition')}
                    style={{cursor: 'pointer'}}
                  >
                    R√©p√©titions {getSortIcon('set_repetition')}
                  </th>
                  <th 
                    className="cursor-pointer"
                    onClick={() => requestSort('set_distance')}
                    style={{cursor: 'pointer'}}
                  >
                    Distance {getSortIcon('set_distance')}
                  </th>
                  <th 
                    className="cursor-pointer"
                    onClick={() => requestSort('exerciseTitle')}
                    style={{cursor: 'pointer'}}
                  >
                    Exercice {getSortIcon('exerciseTitle')}
                  </th>
                  <th 
                    className="cursor-pointer"
                    onClick={() => requestSort('rest_time')}
                    style={{cursor: 'pointer'}}
                  >
                    Repos {getSortIcon('rest_time')}
                  </th>
                  <th 
                    className="cursor-pointer"
                    onClick={() => requestSort('totalDistance')}
                    style={{cursor: 'pointer'}}
                  >
                    Total {getSortIcon('totalDistance')}
                  </th>
                  <th className="text-end">Actions</th>
                </tr>
              </thead>
              <tbody>
                {currentItems.length > 0 ? (
                  currentItems.map(set => (
                    <tr key={set.id}>
                      <td>{set.id}</td>
                      <td className="text-center">
                        <span className="badge bg-primary">
                          <FaRedoAlt className="me-1" /> {set.set_repetition || 1}
                        </span>
                      </td>
                      <td>
                        <span className="badge bg-info text-dark">
                          <FaRulerHorizontal className="me-1" /> {set.set_distance}m
                        </span>
                      </td>
                      <td>
                        <div className="d-flex align-items-center">
                          <FaDumbbell className="text-primary me-2" />
                          <span>{set.exerciseTitle}</span>
                        </div>
                      </td>
                      <td>
                        <span className="badge bg-secondary">
                          <FaStopwatch className="me-1" /> {formatRestTime(set.rest_time)}
                        </span>
                      </td>
                      <td>
                        <span className="badge bg-success">
                          <FaSwimmer className="me-1" /> {set.totalDistance}m
                        </span>
                      </td>
                      <td>
                        <div className="d-flex justify-content-end">
                          <button
                            className="btn btn-sm btn-outline-primary me-1"
                            onClick={() => navigate(`/admin/swim-sets/${set.id}`)}
                            title="Voir les d√©tails"
                          >
                            <FaEye />
                          </button>
                          <button
                            className="btn btn-sm btn-outline-secondary me-1"
                            onClick={() => navigate(`/admin/swim-sets/${set.id}/edit`)}
                            title="Modifier"
                          >
                            <FaEdit />
                          </button>
                          <button
                            className="btn btn-sm btn-outline-danger"
                            onClick={() => handleDelete(set.id)}
                            title="Supprimer"
                          >
                            <FaTrash />
                          </button>
                        </div>
                      </td>
                    </tr>
                  ))
                ) : (
                  <tr>
                    <td colSpan="7" className="text-center py-4">
                      Aucune s√©rie ne correspond √† vos crit√®res de recherche.
                    </td>
                  </tr>
                )}
              </tbody>
            </table>
          </div>
        </div>
      </div>

      {/* Pagination */}
      {sortedFilteredItems.length > itemsPerPage && (
        <div className="card mt-4">
          <div className="card-body d-flex justify-content-between align-items-center">
            <div className="d-flex align-items-center">
              <span className="me-2">Afficher</span>
              <select 
                className="form-select form-select-sm" 
                value={itemsPerPage}
                onChange={(e) => {
                  setItemsPerPage(Number(e.target.value));
                  setCurrentPage(0);
                }}
                style={{width: '70px'}}
              >
                <option value="5">5</option>
                <option value="10">10</option>
                <option value="25">25</option>
                <option value="50">50</option>
              </select>
              <span className="ms-2">√©l√©ments</span>
            </div>

            <nav aria-label="Navigation des pages">
              <ul className="pagination pagination-sm mb-0">
                <li className={`page-item ${currentPage === 0 ? 'disabled' : ''}`}>
                  <button 
                    className="page-link" 
                    onClick={() => setCurrentPage(currentPage - 1)}
                    disabled={currentPage === 0}
                  >
                    &laquo;
                  </button>
                </li>

                {[...Array(pageCount)].map((_, index) => (
                  <li key={index} className={`page-item ${currentPage === index ? 'active' : ''}`}>
                    <button
                      className="page-link"
                      onClick={() => setCurrentPage(index)}
                    >
                      {index + 1}
                    </button>
                  </li>
                ))}

                <li className={`page-item ${currentPage === pageCount - 1 ? 'disabled' : ''}`}>
                  <button 
                    className="page-link" 
                    onClick={() => setCurrentPage(currentPage + 1)}
                    disabled={currentPage === pageCount - 1}
                  >
                    &raquo;
                  </button>
                </li>
              </ul>
            </nav>
          </div>
        </div>
      )}
    </div>
  );
};

export default AdminSwimSetsPage;
